import moment from 'moment';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";
import { hasAccess } from '../../../components/Utils/Common';
import '../../Utils/Constants';

function ClasificacionRadicado (props){
    
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data: {} });

    const [respuestaTipoExpediente, setRespuestaTipoExpediente] = useState(false);
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");

    const [countTextArea, setCountTextArea] = useState(0);

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const [listaTipoDerechoPeticion, setListaDerechoPeticion] = useState({ data: {} });
    const [selectedTipoDerechoPeticion, setSelectedDerechoPeticion] = useState("");
    const [respuestaTipoDerechoPeticion, setRespuestaDerechoPeticion] = useState(false);

    const [listaTipoRespuesta, setListaTipoRespuesta] = useState({ data: {} });
    const [selectedTipoRespuesta, setSelectedTipoRespuesta] = useState("");
    const [respuestaTipoRespuesta, setRespuestaTipoRespuesta] = useState(false);

    const [listaTerminosRespuesta, setListaTerminosRespuesta] = useState({ data: {} });
    const [selectedTerminosRespuesta, setSelectedTerminosRespuesta] = useState("");
    const [respuestaTerminosRespuesta, setRespuestaTerminosRespuesta] = useState(false);

    const [listaTiposQueja, setListaTiposQueja] = useState({ data: {} });
    const [selectedTiposQueja, setSelectedTiposQueja] = useState("");
    const [respuestaTiposQueja, setRespuestaTiposQueja] = useState(false);

    const [fechaTermino, setFechaTermino] = useState(null);
    const [resultDiasNoLaborales, setResultDiasNoLaborales] = useState([]);

    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [getNombreRol, setNombreRol] = useState("");
    const [getAnosAtrasInvalidos, setAnosAtrasInvalidos] = useState(0);

    const location = useLocation()
    const { from } = location.state

    
    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={props.getParametros.route} state={{ from: from }} />;
    }

    function containsSpecialChars(str) {
        

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    useEffect(() => {
        async function fetchData() {

            //window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            setNombreRol(getUser().roles);

            ParametrosMasApi.getAllTipoRespuesta().then(
                datos => !datos.error ? (setListaTipoRespuesta(datos), setRespuestaTipoRespuesta(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposExpediente().then(
                datos => {
                    if (!datos.error) {
                        setListaTipoExpediente(datos);
                        setRespuestaTipoExpediente(true);
                    }
                    else {
                        window.showModal(1);
                    }
                }
            )

            ParametrosMasApi.getAllTiposDerechoPeticion().then(
                datos => {
                    if (!datos.error) {
                        if (from.subTipoExpediente) {
                            if (from.subTipoExpediente.id_tipo_derecho_peticion) {
                                datos.data = (datos.data.filter(x => x["id"] != from.subTipoExpediente.id_tipo_derecho_peticion))
                            }
                        }

                        setListaDerechoPeticion(datos);
                        setRespuestaDerechoPeticion(true);
                    }
                    else {
                        window.showModal(1)
                    }
                }
            )

            ParametrosMasApi.getAllTerminosRespuesta().then(
                datos => {
                    if (!datos.error) {
                        if (from.subTipoExpediente) {
                            if (from.subTipoExpediente.id_termino_respuesta) {
                                datos.data = (datos.data.filter(x => x["id"] != from.subTipoExpediente.id_termino_respuesta))
                            }
                        }



                        setListaTerminosRespuesta(datos);
                        setRespuestaTerminosRespuesta(true);
                    }
                    else {
                        window.showModal(1)
                    }
                }
            )

            ParametrosMasApi.getAllTiposQueja().then(
                datos => {

                    if (!datos.error) {


                        setListaTiposQueja(datos);
                        setRespuestaTiposQueja(true);
                    }
                    else {
                        window.showModal(1)
                    }
                }
            )

            setResultDiasNoLaborales([]);
            ParametrosMasApi.getAllDiasNoLaborales().then(
                datos => {
                    if (!datos.error) {
                        var data = [];
                        for (var i in datos.data) {
                            var date = datos.data[i]["attributes"]["fecha"].split(' ')[0];
                            var result = new Date(date);
                            result.setDate(result.getDate() + 1);
                            data.push(i, date);
                        }

                        setResultDiasNoLaborales(data);
                        //console.log(resultDiasNoLaborales);
                    } else {
                        window.showModal(1);
                    }

                }

            )

            obtenerParametros();
        }
        fetchData();
    }, []);


    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "limite_años_calendario|minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            ParametrosMasApi.getParametroPorNombre(data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('limite_años_calendario')).map(filteredName => (
                                setAnosAtrasInvalidos(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        window.showModal(1);
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const disableCustomDt = current => {
        return !resultDiasNoLaborales.includes(current.format('YYYY-MM-DD'));
    }


    let selectChangeTipoRespuesta = (e) => {
        setSelectedTipoRespuesta(e);
    }

    let selectChangeTipoExpediente = (e) => {
        setCountTextArea(0)
        setSelectedTipoExpediente(e);
    }

    let selectChangeTipoDerechoPeticion = (e) => {
        setCountTextArea(0)
        setSelectedDerechoPeticion(e);
    }

    let selectChangeTerminoRespuesta = (e) => {
        setSelectedTerminosRespuesta(e);
    }

    let selectChangeTiposQueja = (e) => {
        setSelectedTiposQueja(e);
    }

       //Componetes

       const selectTipoRespuesta = () => {
        return (
            listaTipoRespuesta.data.map((tipo_respuesta, i) => {
                return (
                    <option key={tipo_respuesta.id} value={tipo_respuesta.id}>{tipo_respuesta.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoExpediente = () => {
        return (
            listaTipoExpediente.data.map((expediente, i) => {
                return (
                    <option key={expediente.id} value={expediente.id}>{expediente.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoDerechoPeticion = () => {
        return (
            listaTipoDerechoPeticion.data.map((derecho_peticion, i) => {
                return (
                    <option key={derecho_peticion.id} value={derecho_peticion.id}>{derecho_peticion.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTerminosRespuesta = () => {
        return (
            listaTerminosRespuesta.data.map((termino_respuesta, i) => {
                return (
                    <option key={termino_respuesta.id} value={termino_respuesta.id}>{termino_respuesta.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTiposQueja = () => {
        return (
            listaTiposQueja.data.map((tipo_queja, i) => {
                return (
                    <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTiposQuejaExterna = () => {
        return (
            listaTiposQueja.data.map((tipo_queja, i) => {
                if (from.subTipoExpediente) {

                    if (tipo_queja.id == global.Constants.CLASIFICACION_RADICADO.TIPO_QUEJA_EXTERNA && from.subTipoExpediente.id_tipo_expediente != selectedTipoExpediente) {
                        return (
                            <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
                        )
                    }

                }
                else {
                    if (tipo_queja.id == global.Constants.CLASIFICACION_RADICADO.TIPO_QUEJA_EXTERNA) {
                        return (
                            <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
                        )
                    }
                }


            })
        )
    }

    const selectTiposQuejaInterna = () => {
        return (
            listaTiposQueja.data.map((tipo_queja, i) => {
                if (from.subTipoExpediente) {
                    if (tipo_queja.id == global.Constants.CLASIFICACION_RADICADO.TIPO_QUEJA_INTERNA && from.subTipoExpediente.id_tipo_expediente != selectedTipoExpediente) {
                        return (
                            <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
                        )
                    }
                }
                else{
                    if (tipo_queja.id == global.Constants.CLASIFICACION_RADICADO.TIPO_QUEJA_INTERNA) {
                        return (
                            <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
                        )
                    }
                }
                

            })
        )
    }


    const alertaPorTipoDerechoPeticion = (i) => {
        return (
            listaTipoDerechoPeticion.data[i - 1].attributes.observacion
        )
    }


    const enviarDatosControlInterno = (datos) => {

        window.showSpinner(true);
        let data;

        data = {
            "data": {
                "type": "clasificacion_radicado",
                "attributes": {
                    "id_proceso_disciplinario": props.getParametros.id_proceso_disciplinario,
                    "id_etapa": props.getParametros.id_etapa,
                    "id_tipo_expediente": datos.selectedTipoExpediente,
                    "observaciones": null,
                    "id_tipo_queja": datos.tipo_queja,
                    "id_termino_respuesta": null,
                    "fecha_termino": null,
                    "hora_termino": null,
                    "gestion_juridica": false,
                    "estado": true,
                    "id_estado_reparto": 2,
                    "id_derecho_peticion": null,
                    "oficina_control_interno": true,
                    "created_user": getNombreUsuario,
                    "reclasificacion": props.getParametros.reclasificacion,
                }
            }
        }

        ClasificacionRadicadoApi.addClasificacionRadicado(data).then(
            datos => {
                if (!datos.error) {
                    //console.log(data);
                    window.showModal(2);
                    setIsRedirect(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )


    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        //TIPO EXPEDIENTE => DERECHO DE PETICION => COPIAS 
        if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION 
            && selectedTipoDerechoPeticion === global.Constants.DERECHOS_PETICION.COPIAS) {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": null,
                        "id_termino_respuesta": null,
                        "fecha_termino": null,
                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": selectedTipoDerechoPeticion,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }
        //TIPO EXPEDIENTE => DERECHO DE PETICION => GENERAL     
        else if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION  
            && selectedTipoDerechoPeticion === global.Constants.DERECHOS_PETICION.GENERAL ) {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": null,
                        "id_termino_respuesta": null,
                        "fecha_termino": null,
                        "hora_termino": null,
                        "gestion_juridica": (datos.gestion_juridica === 'true'),
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": selectedTipoDerechoPeticion,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }
        //TIPO EXPEDIENTE => DERECHO DE PETICION => DERECHO DE PETICION => ALERTA CONTROL POLITICO
        else if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION  
            && selectedTipoDerechoPeticion === global.Constants.DERECHOS_PETICION.ALERTA) {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario":props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": null,
                        "id_termino_respuesta": null,
                        "fecha_termino": null,
                        "hora_termino": null,
                        "gestion_juridica": (datos.gestion_juridica === 'true'),
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": selectedTipoDerechoPeticion,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => PODER REFERENTE A SOLICITUD
        if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.PODER_REFERENTE ) {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario":props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.tipo_queja,
                        "id_termino_respuesta": null,
                        "fecha_termino": null,
                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => QUEJA
        if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.QUEJA) {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.tipo_queja,
                        "id_termino_respuesta": null,
                        "fecha_termino": null,
                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => TUTELA => TERMINO RESPUESTA = DIAS
        if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.TUTELA 
            && datos.termino_rta === global.Constants.TERMINOS_RESPUESTA.DIAS) {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": null,
                        "id_termino_respuesta": selectedTerminosRespuesta,
                        "fecha_termino": datos.fechaTermino ? datos.fechaTermino : fechaTermino,
                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => TUTELA => TERMINO RESPUESTA = HORAS
        else if (selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.TUTELA 
            && datos.termino_rta === global.Constants.TERMINOS_RESPUESTA.HORAS) {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": props.getParametros.id_proceso_disciplinario,
                        "id_etapa": props.getParametros.id_etapa,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": null,
                        "id_termino_respuesta": selectedTerminosRespuesta,
                        "fecha_termino": new Date(),
                        "hora_termino": datos.horasTermino,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                        "reclasificacion": props.getParametros.reclasificacion,
                    }
                }
            }
        }

        console.log(JSON.stringify(data));
        
        ClasificacionRadicadoApi.addClasificacionRadicado(data).then(
            datos => {
                if (!datos.error) {
                    console.log(datos);
                    from.cambiaColorClasificacionRadicado = "btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left";
                    window.showModal(global.Constants.MODAL_CODIGO.EXITOSO)
                    setIsRedirect(true);
                }
                else {
                    console.log(datos.error.toString());
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }

    // COMPONENTE CONFIRMACION OFICINA DE CONTROL INTERNO DISCIPLINARIO
    const componenteTipoRespuesta = (tipo_respuesta) => {

        // CONFIRMACION OFICINA DE CONTRO INTERNO = SI
        if (tipo_respuesta === global.Constants.TIPO_RESPUESTA.SI) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            selectedTipoExpediente: 3,
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            console.log(valores);
                            enviarDatosControlInterno(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {/*respuestaTiposQueja ? selectTiposQueja() : null*/}

                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === global.Constants.TIPO_RESPUESTA.NO) {
            return (
                <>
                    <div className="form-group">
                        <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                            value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                            <option value="">Por favor seleccione</option>
                            {respuestaTipoExpediente ? selectTipoExpediente() : null}
                        </select>
                    </div>


                    {/*DERECHO DE PETICION*/}
                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION) : ''}
                    {/*PODER REFERENTE A SOLIICTUD*/}
                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.PODER_REFERENTE ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.PODER_REFERENTE) : ''}
                    {/*QUEJA*/}
                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.QUEJA ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.QUEJA) : ''}
                    {/*TUTELA*/}
                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.TUTELA ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.TUTELA) : ''}

                </>
            );
        }
    };

    // COMPOENENTE TIPO EXPEDIENTE
    const componenteTipoExpediente = (tipo_expediente) => {

        // TIPO DE EXPEDIENTE = DERECHO DE PETICION
        if (tipo_expediente === global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}

                    <div className="form-group">
                        <label htmlFor="ingresoDerechoPeticion">Tipo de derecho de petición <span className="text-danger">*</span></label>
                        <select className="form-control" id="ingresoDerechoPeticion" name="ingresoDerechoPeticion"
                            value={selectedTipoDerechoPeticion} onChange={e => selectChangeTipoDerechoPeticion(e.target.value)}>
                            <option value="">Por favor seleccione</option>
                            {respuestaTipoDerechoPeticion ? selectTipoDerechoPeticion() : null}
                        </select>
                    </div>

                    {/*COPIAS*/}
                    {selectedTipoDerechoPeticion === global.Constants.DERECHOS_PETICION.COPIAS ? componenteTipoDerechoPeticion(global.Constants.DERECHOS_PETICION.COPIAS) : ''}
                    {/*GENERAL*/}
                    {selectedTipoDerechoPeticion === global.Constants.DERECHOS_PETICION.GENERAL ? componenteTipoDerechoPeticion(global.Constants.DERECHOS_PETICION.GENERAL) : ''}
                    {/*ALERTA CONTROL POLITICO*/}
                    {selectedTipoDerechoPeticion === global.Constants.DERECHOS_PETICION.ALERTA ? componenteTipoDerechoPeticion(global.Constants.DERECHOS_PETICION.ALERTA) : ''}
                </>
            );
        }

        // TIPO DE EXPEDIENTE = PODER REFERENTE A SOLICITUD
        else if (tipo_expediente === global.Constants.TIPOS_EXPEDIENTES.PODER_REFERENTE) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            console.log(valores);
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {respuestaTiposQueja ? selectTiposQuejaExterna() : null}
                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        // TIPO DE EXPEDIENTE = QUEJA
        else if (tipo_expediente === global.Constants.TIPOS_EXPEDIENTES.QUEJA) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {respuestaTiposQueja ? selectTiposQuejaExterna() : null}
                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"> <i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        // TIPO DE EXPEDIENTE = TUTELA
        else if (tipo_expediente === global.Constants.TIPOS_EXPEDIENTES.TUTELA) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <label htmlFor="ingresoTipoRtaTutela">Ingrese termino de respuesta <span className="text-danger">*</span></label>

                    <select className="form-control" id="termino_rta" name="termino_rta"
                        value={selectedTerminosRespuesta}
                        onChange={e => selectChangeTerminoRespuesta(e.target.value)}>
                        <option value="{selectedTerminosRespuesta}">Por favor seleccione</option>
                        {respuestaTerminosRespuesta ? selectTerminosRespuesta() : null}
                    </select>

                    {/*DIAS TERMINO*/}
                    {selectedTerminosRespuesta === global.Constants.TERMINOS_RESPUESTA.DIAS ? componenteTerminosRespuesta(global.Constants.TERMINOS_RESPUESTA.DIAS) : ''}
                    {/*HORAS TERMINO*/}
                    {selectedTerminosRespuesta === global.Constants.TERMINOS_RESPUESTA.HORAS ? componenteTerminosRespuesta(global.Constants.TERMINOS_RESPUESTA.HORAS) : ''}

                </>
            );
        }
    };

    // COMOPNENTE TIPO DERECHO DE PETICION
    const componenteTipoDerechoPeticion = (tipo_derecho_peticion) => {

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  COPIAS
        if (tipo_derecho_peticion === global.Constants.DERECHOS_PETICION.COPIAS) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik

                        initialValues={{
                            observaciones: '',
                            tipo_derecho_peticion: tipo_derecho_peticion,
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)


                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            else if (valores.observaciones.length <= getMinimoTextArea) {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'

                            }

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres inválidos'
                            }


                            return errores
                        }}


                        onSubmit={(valores, { resetForm }) => {

                            console.log(valores);
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>
                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                    </div>

                                    <div className="form-group">
                                        <label>Observaciones</label>
                                        <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                            placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                    </div>
                                </div>

                                <div className="block-content block-content-full text-right">

                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                    
                                    <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                    </Link>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  GENERAL
        else if (tipo_derecho_peticion === global.Constants.DERECHOS_PETICION.GENERAL) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik

                        initialValues={{
                            observaciones: '',
                            gestion_juridica: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            else if (valores.observaciones.length <= getMinimoTextArea) {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'
                            }

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres inválidos'
                            }


                            if (!valores.gestion_juridica) {
                                errores.gestion_juridica = 'Debe diligenciar el campo gestión juridica'
                            }

                            return errores
                        }}
                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>

                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                    </div>

                                    <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                    <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
                                        <option value="">Por favor seleccione</option>
                                        <option value="true">Si</option>
                                        <option value="false">No</option>
                                    </Field>

                                    <label>Observaciones</label>  <span className="text-danger">*</span>
                                    <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                        placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                    <div className="text-right">
                                        <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                    </div>
                                    <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                </div>

                                <div className="block-content block-content-full text-right">

                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                    <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                    </Link>
                                </div>

                            </Form>
                        )}
                    </Formik>
                </>
            );
        }

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  ALERTA CONTROL POLITICO
        else if (tipo_derecho_peticion === global.Constants.DERECHOS_PETICION.ALERTA) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            observaciones: '',
                            gestion_juridica: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            else if (valores.observaciones.length <= getMinimoTextArea) {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'
                            }

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres inválidos'
                            }

                            if (!valores.gestion_juridica) {
                                errores.gestion_juridica = 'Debe diligenciar el campo gestión juridica'
                            }

                            return errores
                        }}
                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>

                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                    </div>

                                    <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                    <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
                                        <option value="">Por favor seleccione</option>
                                        <option value="true">Si</option>
                                        <option value="false">No</option>
                                    </Field>


                                    <label>Observaciones</label>  <span className="text-danger">*</span>
                                    <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                        placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                    <div className="text-right">
                                        <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                    </div>
                                    <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />

                                    <div className="block-content block-content-full text-right">
                                        <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                        <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                            <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                        </Link>
                                    </div>

                                </div>

                            </Form>
                        )}
                    </Formik>

                </>
            );
        }

    };

    const handleCallbackFechaTermino = (childData) => {
        try {
            setFechaTermino(childData)
        } catch (error) {

        }

    }

    // COMPONENTE TERMINOS DE RESPUESTA -- TIPO EXPEDIENTE TUTELA
    const componenteTerminosRespuesta = (termino_respuesta) => {

        //TERMINO TUTELA = DIAS      
        if (termino_respuesta === global.Constants.TERMINOS_RESPUESTA.DIAS) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            termino_rta: termino_respuesta,
                            horasTermino: '',
                            fechaTermino: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.fechaTermino && !fechaTermino) {
                                errores.fechaTermino = 'Debe seleccionar el termino de respuesta'
                            }

                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label>Fecha para termino</label>  <span className="text-danger">*</span>

                                {/*<DatePicker id="fechaTermino" locale='es' name="fechaTermino" dateFormat="DD/MM/YYYY" closeOnSelect={true} placeholder="dd/mm/yyyy" onChange={(date) => setFechaTermino(date)} timeFormat={false} isValidDate={disableCustomDt} />*/}
                                <DatePerson resultDiasNoLaborales={resultDiasNoLaborales} getAnosAtrasInvalidos={getAnosAtrasInvalidos} parentCallback={handleCallbackFechaTermino} id="fechaTermino" name="fechaTermino" />
                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>
                    </Formik>
                </>

            )
        }

        //TERMINO TUTELA = HORAS     
        else if (termino_respuesta === global.Constants.TERMINOS_RESPUESTA.HORAS) {

            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            termino_rta: termino_respuesta,
                            horasTermino: '',
                            fechaTermino: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.horasTermino) {
                                errores.horasTermino = 'Debe seleccionar el termino de respuesta'
                            }

                            return errores
                        }}


                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label>Horas para termino</label>  <span className="text-danger">*</span>
                                <Field type="number" min="1" max='23' id="horasTermino" name="horasTermino" className="form-control" placeholder="" />
                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                            </div>

                        </Form>
                    </Formik>
                </>
            )

        }

    };

        

        return (
                

                <>
                    {
                        (hasAccess('TiposUsuario', 'Control interno disciplinario')) ?
                            (

                                <div className="block-content">

                                    <div className="row">

                                        <div className="col-md-12">
                                            <div className="form-group">
                                                <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                                                <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                                    value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                                                    <option value="">Por favor seleccione</option>
                                                    {respuestaTipoExpediente ? selectTipoExpediente() : null}
                                                </select>
                                            </div>
                                        </div>
                                    </div>

                                    {/*DERECHO DE PETICION*/}
                                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.DERECHO_PETICION) : ''}
                                    {/*PODER REFERENTE A SOLIICTUD*/}
                                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.PODER_REFERENTE ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.PODER_REFERENTE) : ''}
                                    {/*QUEJA*/}
                                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.QUEJA ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.QUEJA) : ''}
                                    {/*TUTELA*/}
                                    {selectedTipoExpediente === global.Constants.TIPOS_EXPEDIENTES.TUTELA ? componenteTipoExpediente(global.Constants.TIPOS_EXPEDIENTES.TUTELA) : ''}

                                </div>
                            ) : null
                    }

                </>
                        
               
        );
 
    
}

export default ClasificacionRadicado;
