import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom'
import ListaBotones from '../../Utils/ListaBotones';
import { hasAccess } from '../../../components/Utils/Common';
import GenericApi from './../../Api/Services/GenericApi';
import DataTable from 'react-data-table-component';

function AntecedentesLista() {

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let id_etapa = from.id_etapa;

    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);
    const [errorApi, setErrorApi] = useState('');
    const [getSeach, setSeach] = useState('');

    const [getRoutes, setRoutes] = useState({
        id_etapa: id_etapa,
        id_fase: 1,
        crear_registro: "/AntencentesForm",
        consultar_registros: "/AntencentesLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: "/SoporteRadicadoLista",
        modulo: "CR_Antecedente",
        funcionalidad_crear: "Crear",
        funcionalidad_consultar: "Consultar"
    });

    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);
    const [getEstadoLista, setEstadoLista] = useState('');

    const columns = [
        {
            name: 'Registrado por',

            cell: antecedente => <div>{antecedente.attributes.nombre_completo}<br />
                <strong>Etapa: </strong>{antecedente.attributes.etapa.nombre}</div>,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Fecha registro',
            cell: antecedente => antecedente.attributes.fecha_creado,
            sortable: true,
            width: "10%"
        },
        {
            name: 'Dependencia',
            cell: antecedente => antecedente.attributes.nombre_dependencia,
            sortable: true,
            width: "15%",
        },
        {
            name: 'DescripciÃ³n',
            cell: antecedente => <div title={antecedente.attributes.descripcion}>{antecedente.attributes.descripcion_corta}...</div>,
            sortable: true,
            width: "25%",
        },
        {
            name: 'Estado',
            selector: antecedente => (antecedente.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "15%",
        },
        {
            name: 'Acciones',
            width: "15%",

        }
    ];


    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });
    const [getListaDetalleCambios, setListaDetalleCambios] = useState({ data: [], links: [], meta: [] });



    useEffect(() => {

        async function fetchData() {

            window.showSpinner(true);
            setEstadoLista("Activos");

            cargarAntecedentes(1, 10, "1");

            // VALIDA CIERRE DE ETAPA EVALUACION
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }



            CierreEtapaApi.getCierreEtapaByIdProcesoDisciplinario(dataCierreEtapa).then(
                datos => {

                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )

        }
        fetchData();
    }, []);

    const cargarAntecedentes = (page, perPage, estado) => {

        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": "antecedentes",
                    "descripcion": "descripcion",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": -1,
                    "estado": estado,
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }
        console.log(JSON.stringify(data));

        if (hasAccess('CR_Antecedente', 'Consultar')) {

            GenericApi.getByDataGeneric('antencedentes/get-antecedentes/' + procesoDisciplinarioId, data).then(
                datos => {
                    if (!datos.error) {

                        setAntecedentesLista(datos);

                        try {
                            if (datos.data.length > 0) {
                                from.antecedente = datos.data[0].attributes.descripcion;
                            }
                        } catch (error) {
                            console.log(error);
                        }



                        window.showSpinner(false);
                    }
                    else {
                        window.showSpinner(false);
                        window.showModal(1);

                    }

                }
            )
        }
        else {
            window.showSpinner(false)
        }
    }

    const handlePageChange = page => {

        window.showSpinner(true);
        setPageActual(page);
        cargarAntecedentes(page, perPage, (getEstadoLista == "Inactivos" ? '0' : "1"));
    }

    const handlePerRowsChange = async (newPerPage, page) => {

            window.showSpinner(true);
            setPerPage(newPerPage);
            setPageActual(page);
            cargarAntecedentes(page, newPerPage, (getEstadoLista == "Inactivos" ? '0' : "1"));
        


    }

    const cargarDetalleCambiosEstado = (id) => {

        GenericApi.getByIdGeneric('log-proceso-disciplinario/get-log-proceso', id).then(
            datos => !datos.error ? (setListaDetalleCambios(datos)) : (window.showModal(1)));
    }

    const listaDetalleCambios = () => {
        if (getListaDetalleCambios.data != null && typeof (getListaDetalleCambios.data) != 'undefined') {
            return (



                getListaDetalleCambios.data.map((cambio, i) => {
                    return (
                        <tr key={cambio.id}>
                            <td>
                                {cambio.attributes.funcionario_registra ? cambio.attributes.funcionario_registra.nombre + ' ' + cambio.attributes.funcionario_registra.apellido : ""}
                            </td>
                            <td>
                                {cambio.attributes.dependencia_origen ? cambio.attributes.dependencia_origen.nombre : ""}
                            </td>
                            <td title={cambio.attributes.descripcion}>
                                {cambio.attributes.descripcion.substring(0, 50)}...
                            </td>
                            <td>
                                {cambio.attributes.created_at}
                            </td>
                        </tr>
                    )
                })
            )
        }
    }

    const handleCallback = (childData) => {
        try {
            window.showSpinner(true);
            setEstadoLista(childData == '0' ? "Inactivos" : "Activos")
            cargarAntecedentes(1, 10, childData);
        } catch (error) {

        }

    }



    return (
        <> <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}

            <Formik>
                <Form>

                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">

                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <small>Antecedentes</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>



                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Consultar lista de Antecedentes {getEstadoLista}</strong></h3>
                        </div>
                        <div className="block-content">
                            <>

                                <div className='row'>

                                    <div className='col-md-3'>
                                        <div className="form-group ">
                                            <Field type="text" id="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} name="search" className="form-control border border-success" placeholder="Buscar" />
                                        </div>
                                    </div>

                                    {/*ListaBotones(getRoutes)*/}
                                    <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />


                                </div>


                                {
                                    (hasAccess('CR_Antecedente', 'Consultar')) ? (
                                        <div className='col-md-12 mt-2 mb-2'>
                                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                columns={columns}
                                                data={getAntecedentesLista.data.filter((suggestion) => {
                                                    if (getSeach === "") {
                                                        return suggestion;
                                                    } else if (

                                                        ((suggestion.attributes.descripcion
                                                            + suggestion.attributes.nombre_completo + suggestion.attributes.nombre_dependencia +
                                                            suggestion.attributes.fecha_creado + suggestion.attributes.etapa.nombre +
                                                            suggestion.attributes.estado + suggestion.attributes.fecha_registro).toLowerCase().includes(getSeach.toLowerCase()))

                                                    ) {
                                                        return suggestion;
                                                    }
                                                })}
                                                perPage={perPage}
                                                page={pageActual}
                                                pagination
                                                noDataComponent="Sin datos"
                                                paginationServer
                                                paginationTotalRows={getAntecedentesLista.meta ? getAntecedentesLista.meta.total : 1}
                                                onChangePage={handlePageChange}
                                                onChangeRowsPerPage={handlePerRowsChange}
                                                defaultSortFieldId="Nombre"
                                                striped
                                                icon
                                                paginationComponentOptions={{ rowsPerPageText: 'Filas por pÃ¡gina' }}
                                                defaultSortAsc={false}
                                            />
                                        </div>
                                    ) : null
                                }
                            </>

                        </div>
                    </div>

                </Form>
            </Formik>
        </>
    );
}


export default AntecedentesLista;
