import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'
import { getUser } from '../../../components/Utils/Common';
import '../../Utils/Constants';
import GenericApi from '../../Api/Services/GenericApi';
import { Link } from "react-router-dom";
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import ActuacionParametrosPlantillaForm from '../Herramientas/ActuacionParametrosPlantillaForm';
import Spinner from '../../Utils/Spinner';

function ActuacionesForm() {

    const [errorApi, setErrorApi] = useState('');
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [listaTipoActuacion, setListaTipoActuacion] = useState("");
    const [getListaUsuariosComisorio, setListaUsuariosComisorio] = useState("");
    const [getValueDocumentoRuta, setValueDocumentoRuta] = useState("");
    const [getPesoTotalArchivos, setPesoTotalArchivos] = useState(0);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [inputListArchivos, setInputListArchivos] = useState([{ archivo: {}, filebase64: "", size: 0, ext: "" }]);
    const [getMasActuacionId, setMasActuacionId] = useState(0);
    const [getUsuarioComisionado, setUsuarioComisionado] = useState(0);
    const [getCantidadActuaciones, setCantidadActuaciones] = useState(0);
    const [getCantidadImpedimentos, setCantidadImpedimentos] = useState(0);
    const [getCantidadComisorios, setCantidadComisorios] = useState(0);

    const location = useLocation();
    const { from, selected_id_etapa, tipoActuacion } = location.state;


    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let idEtapa = from.idEtapa >= 3 ? from.idEtapa : 3;

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            obtenerParametros();
            getAllTipoActuaciones();
            if (tipoActuacion == "Comisorio") {
                getListUsuarios(getUser().id_dependencia);
            }
        }
        fetchData();
    }, []);

    const getListUsuarios = (id_dependencia) => {

        GenericApi.getByIdGeneric("usuario/get-todos-usuarios-dependencia", id_dependencia).then(
            datos => {
                if (!datos.error) {
                    setListaUsuariosComisorio(datos.data);
                } else {
                    setModalState({ title: "SINPROC No " + radicado + " :: Crear " + tipoActuacion, message: datos.error.toString(), show: true, redirect: '/PreguntaImpedimentos', from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )

    }

    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre", data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    /**
     * Trae la lista de todos los tipos de actuaciones.
     */
    const getAllTipoActuaciones = () => {

        GenericApi.getByIdGeneric("mas_actuaciones/actuaciones-etapa", selected_id_etapa).then(
            datos => {

                if (!datos.error) {
                    setListaTipoActuacion(datos.data);

                    let actuaciones = 0;
                    let impedimentos = 0;
                    let comisorios = 0;

                    for (let index = 0; index < datos.data.length; index++) {
                        const element = datos.data[index];

                        if (element.attributes.tipo_actuacion == 0 && tipoActuacion == "Actuación") {
                            actuaciones++;
                        } else if (element.attributes.tipo_actuacion == 1 && tipoActuacion == "Impedimento") {
                            impedimentos++;
                        } else if (element.attributes.tipo_actuacion == 2 && tipoActuacion == "Comisorio") {
                            comisorios++;
                        }
                    }

                    setCantidadActuaciones(actuaciones);
                    setCantidadImpedimentos(impedimentos);
                    setCantidadComisorios(comisorios);

                    window.showSpinner(false);
                } else {
                    setModalState({ title: "SINPROC No " + radicado + " :: Crear Actuaciones", message: datos.error.toString(), show: true, redirect: '/ActuacionesForm', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const actuacionId = datos.id_actuacion;

        if (getUsuarioComisionado != 0) {
            const dataUsuarioComisorio = {
                "data": {
                    "type": "usuario-comisionado",
                    "attributes": {
                        "id_proceso_disciplinario": ""
                    }
                }
            }
            GenericApi.addGeneric("proceso-diciplinario/usuario-comisionado/" + getUsuarioComisionado + "/" + procesoDisciplinarioId, dataUsuarioComisorio).then(
                datos => {
                    if (!datos.error) {
                    } else {
                        setModalState({ title: "SINPROCNo " + radicado + " :: Crear " + tipoActuacion, message: datos.error.toString() + " Error en guardar el usuario comisorio", show: true, redirect: '/ActuacionesLista', from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        }


        const data = {
            "data": {
                "type": 'actuaciones',
                "attributes": {
                    "id_actuacion": datos.id_actuacion,
                    "usuario_accion": getNombreUsuario,
                    "id_estado_actuacion": 3,
                    "created_user": getNombreUsuario,
                    "fileBase64": inputListArchivos[0].filebase64,
                    "nombre_archivo": inputListArchivos[0].archivo.name,
                    "ext": inputListArchivos[0].ext,
                    "peso": inputListArchivos[0].archivo.size,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": selected_id_etapa,
                    "estado": true,
                }
            }
        }

        GenericApi.addGeneric("actuaciones", data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No " + radicado + " :: Crear " + tipoActuacion, message: 'Registro Exitoso', show: true, redirect: '/Transacciones', from: { from, selected_id_etapa: selected_id_etapa, id_actuacion: actuacionId }, alert: global.Constants.TIPO_ALERTA.EXITO });
                } else {
                    setModalState({ title: "SINPROCNo " + radicado + " :: Crear " + tipoActuacion, message: datos.error.toString(), show: true, redirect: '/ActuacionesLista', from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )
    }

    const selectChangeMasActuacion = (value) => {
        setMasActuacionId(value);
    }

    const selectChangeUsuarioComisionado = (value) => {
        setUsuarioComisionado(value);
    }

    const getParamsForm = () => {
        if (getMasActuacionId > 0) {
            return (
                <ActuacionParametrosPlantillaForm from={from} id_mas_actuacion={getMasActuacionId} />
            )
        }
    }

    const selectTipoActuacionesLis = () => {

        return (
            listaTipoActuacion && listaTipoActuacion.length ? listaTipoActuacion.map((actuaciones, i) => {
                if (actuaciones.attributes.tipo_actuacion == 0 && tipoActuacion == "Actuación") {
                    if (getCantidadActuaciones == 1) {
                        return (
                            <option selected key={actuaciones.id} value={actuaciones.id}>{actuaciones.attributes.nombre_actuacion}</option>
                        )
                    } else {
                        return (
                            <option key={actuaciones.id} value={actuaciones.id}>{actuaciones.attributes.nombre_actuacion}</option>
                        )
                    }
                } else if (actuaciones.attributes.tipo_actuacion == 1 && tipoActuacion == "Impedimento") {
                    if (getCantidadImpedimentos == 1) {
                        return (
                            <option selected key={actuaciones.id} value={actuaciones.id}>{actuaciones.attributes.nombre_actuacion}</option>
                        )
                    } else {
                        return (
                            <option key={actuaciones.id} value={actuaciones.id}>{actuaciones.attributes.nombre_actuacion}</option>
                        )
                    }
                } else if (actuaciones.attributes.tipo_actuacion == 2 && tipoActuacion == "Comisorio") {
                    if (getCantidadComisorios == 1) {
                        setMasActuacionId(actuaciones.id);
                        return (
                            <option selected key={actuaciones.id} value={actuaciones.id}>{actuaciones.attributes.nombre_actuacion}</option>
                        )
                    } else {
                        return (
                            <option key={actuaciones.id} value={actuaciones.id}>{actuaciones.attributes.nombre_actuacion}</option>
                        )
                    }
                } else {
                    // setModalState({ title: "SINPROCNo " + radicado + " :: Crear "+tipoActuacion, message: "Error al intentar listar "+tipoActuacion , show: true, redirect: tipoActuacion != "Actuación" ? `/PreguntaImpedimentos/` : `/ActuacionesLista/`, from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }) : ""
        )



    }

    const selectUsuarioComisorio = () => {

        return (
            getListaUsuariosComisorio && getListaUsuariosComisorio.length ? getListaUsuariosComisorio.map((user, i) => {
                return (
                    <option key={user.id} value={user.id}>{user.attributes.nombre + " " + user.attributes.apellido}</option>
                )
            }) : ""
        )
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    }

    function obtenerPesoTotalArchivos(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if (archivo.archivo.size) {
                    peso += archivo.archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setErrorApi('El peso/tamaño de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tamaño y elimine algunos adjuntos, (Si desea enviar mas archivos realice otro proceso de registro de Soportes del radicado)')
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

    const handleInputChangeArchivos = (e, index) => {

        const { name, files } = e.target;
        if (files.length > 0) {
            const extension = getFileExtension(files[0].name);

            if (extension == "docx") {

                const list = [...inputListArchivos];
                list[index][name] = files[0];

                if (files[0]) {
                    list[index][name] = files[0];
                } else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].ext = "";
                    setInputListArchivos(list);
                }

                // Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].ext = extension;
                        setInputListArchivos(list);
                    }
                })
                obtenerPesoTotalArchivos(list);
            } else {
                const list = [...inputListArchivos];
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                setInputListArchivos(list);
                setErrorApi("El archivo seleccionado no tiene un formato permitido")
                window.showModal(1)
            }
        } else {
            const list = [...inputListArchivos];
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            setInputListArchivos(list);
            obtenerPesoTotalArchivos(list);
        }

    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    return (
        <>
            {<InfoErrorApi error={errorApi} />}
            {<ModalGen data={getModalState} />}
            {<Spinner />}

            <Formik
                initialValues={{
                    id_actuacion: '',
                    usuario_comisionado: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    valores.id_actuacion = getMasActuacionId;
                    valores.usuario_comisionado = getUsuarioComisionado;

                    if (tipoActuacion == "Comisorio" && valores.usuario_comisionado == 0) {
                        errores.usuario_comisionado = 'Debe seleccionar una usuario comisionado para registrar'
                    }

                    if (valores.id_actuacion == 0) {
                        errores.id_actuacion = 'Debe seleccionar una actuación para registrar'
                    }

                    // if (valores.id_actuacion != 0 && valores.id_actuacion != "") {
                    //     let id_actuacion = valores.id_actuacion;

                    //     listaTipoActuacion.forEach(element => {
                    //         if (element.id == id_actuacion) {
                    //             setValueDocumentoRuta(element.nombre_plantilla);
                    //         }
                    //     });
                    // }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={tipoActuacion != "Actuación" ? `/PreguntaImpedimentos/` : `/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}><small>{tipoActuacion}</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Agregar {tipoActuacion}</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Crear {tipoActuacion}</strong></h3>
                            </div>

                            <div className="block-content">
                                <div className='text-right'>

                                    <Link to={tipoActuacion != "Actuación" ? `/PreguntaImpedimentos/` : `/ActuacionesLista/`} title='Regresar a lista de Actuaciones' state={{ from: from, selected_id_etapa: selected_id_etapa }}>
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="id_actuacion">{tipoActuacion} <span className="text-danger">*</span></label>
                                            <select className="form-control" id="id_actuacion" name="id_actuacion" onChange={e => selectChangeMasActuacion(e.target.value)}>
                                                <option value="">Por favor seleccione</option>
                                                {selectTipoActuacionesLis()}
                                            </select>
                                            {/* <Field as="select" className="form-control" id="id_actuacion" name="id_actuacion">
                                                <option>Por favor seleccione</option>
                                                {selectTipoActuacionesLis()}
                                            </Field> */}
                                            <ErrorMessage name="id_actuacion" component={() => (<span className="text-danger">{errors.id_actuacion}</span>)} />
                                        </div>
                                    </div>

                                    {tipoActuacion == "Comisorio" ? (
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label htmlFor="usuario_comisionado">Usuario Comisionado <span className="text-danger">*</span></label>
                                                <select className="form-control" id="usuario_comisionado" name="usuario_comisionado" onChange={e => selectChangeUsuarioComisionado(e.target.value)}>
                                                    <option value="">Por favor seleccione</option>
                                                    {selectUsuarioComisorio()}
                                                </select>
                                                <ErrorMessage name="usuario_comisionado" component={() => (<span className="text-danger">{errors.usuario_comisionado}</span>)} />
                                            </div>
                                        </div>
                                    ) : null}

                                </div>

                                {/* {getParamsForm()} */}
                                {getMasActuacionId > 0 ? <ActuacionParametrosPlantillaForm from={from} id_mas_actuacion={getMasActuacionId} /> : null}
                                {/* <div className="col-md-12" style={{ marginLeft: '10px' }}>
                                    <div className="form-group">
                                        <label htmlFor="documento_ruta">Descargar documento diligenciado en Word <span className="text-danger">* </span></label>
                                        <a href={getValueDocumentoRuta} id='documento_ruta' name='documento_ruta' style={{ marginLeft: '10px' }} target="_blank" download class="btn btn-success"><i className="fa fa-download" ></i> </a>
                                    </div>
                                </div> */}

                                {getMasActuacionId > 0 ?
                                    inputListArchivos.map((x, i) => {
                                        return (
                                            <div className="col-md-12" key={i}>
                                                <div className="form-group">
                                                    <div className='row'>
                                                        <div className='col-md-12'>
                                                            <label>Documento adjunto diligenciado (Word)<span className="text-danger">* </span></label>
                                                        </div>
                                                        <div className='col-md-12' style={{ marginLeft: '13px' }}>
                                                            <div className='row'>
                                                                <div className='col-md-6'>
                                                                    <label className="custom-file-label" htmlFor="example-file-input-custom" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                    <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i)} required />
                                                                    <label>Peso del archivo: {formatBytes(x.archivo.size)}</label>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        );
                                    })
                                    : null
                                }
                            </div>
                        </div>
                        <div className="block-content block-content-full text-right bg-light">
                            <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                            <Link to={tipoActuacion != "Actuación" ? `/PreguntaImpedimentos/` : `/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}>
                                <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                            </Link>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );

}

export default ActuacionesForm;
