import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import Spinner from '../../Utils/Spinner';
import { Link, useLocation } from 'react-router-dom';
import 'react-datetime/css/react-datetime.css';
import 'moment/locale/es';
import '../../Utils/Constants';
import GenericApi from '../../Api/Services/GenericApi';
import ModalGen from '../../Utils/Modals/ModalGeneric';


function TipoConductaForm() {
   
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    const [getListaTipoConducta, setListaTipoConducta] = useState({ data: {} });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });


    const [getParametros, setParametros] = useState({
        id_proceso_disciplinario: procesoDisciplinarioId,
        id_etapa: global.Constants.ETAPAS.CAPTURA_REPARTO,
        reclasificacion: false,
        route: "/TipoConductaLista/",
        tipo_clasificacion: global.Constants.TIPO_CLASIFICACION.CLASIFICACION,
    });

    useEffect(() => {
        async function fetchData() {
            cargarTiposConducta();
        }
        fetchData();
    }, []);


    const cargarTiposConducta = () => {    
        GenericApi.getGeneric("lista-tipo-conducta/"+procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    setListaTipoConducta(datos)
                   
                }
                else {
                    setModalState({ title: "SINPROCNo "+radicado+" :: EvaluaciÃ³n del radicado", message: datos.error.toString(), show: true, redirect: '/RamasProceso/', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showSpinner(false);
                }
            }
        )
    }

    const componentSelectTipoConducta = () => {
        return (
            getListaTipoConducta.data.map((conducta, i) => {
                return (
                    <option key={conducta.id} value={conducta.id}>{conducta.attributes.conducta_nombre}</option>
                )
            })
        )
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'tipo_conducta_proceso_disciplinario',
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "estado": true,
                    "tipo_conducta": datos.tipoConducta,
                }
            }
        }
        console.log(JSON.stringify(data));

        GenericApi.addGeneric('tipo-conducta', data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No. "+radicado+" :: Tipo de conducta", message: 'Registro Exitoso', show: true, redirect: '/RamasProceso', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});  
                }
                else {
                    setModalState({ title: "SINPROC No. "+radicado+" :: Tipo de conducta", message: datos.error.toString(), show: true, redirect: null, from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);
            }
        )
    }


    return (
        
        <>
            <Spinner />
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ClasificacionRadicadoLista/`} state={{ from: from }}><small>Clasificacion del radicado Lista</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Nueva clasificacion del radicado</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <Formik
                    initialValues={{
                        tipoConducta: '',
                    }}
                    validate={(valores) => {
                        let errores = {};

                        if (!valores.tipoConducta) {
                            errores.tipoConducta = 'Debe ingresar tipo de conducta'
                        }

                        return errores
                    }}
                    onSubmit={(valores, { resetForm }) => {
                        enviarDatos(valores);
                    }}>

                    {({ errors }) => (
                        <Form>
                             <div className="col-md-12">
                                <div className="block block-themed">
                                    <div className="block-header">
                                        <h3 className="block-title"> SINPROC No {radicado} :: <strong>Crear Tipo de conducta</strong></h3>
                                    </div>

                                    <div className='col-md-12'>
                                        <label htmlFor='tipoConducta'>Tipo de Conducta<span className='text-danger'></span></label>
                                        <Field as='select' className='form-control' id='tipoConducta' name='tipoConducta'>
                                            <option value=''>Por favor seleccione</option>
                                            {getListaTipoConducta.data.length > 0 ? componentSelectTipoConducta() : null}

                                        </Field>
                                        <ErrorMessage name='tipoConducta' component={() => (<span className='text-danger'>{errors.tipoConducta}</span>)} />
                                    </div>

                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>

               
            </div>

        </>
    );

}
export default TipoConductaForm;
