import React, { Component, lazy, Suspense } from 'react';

import UsuarioFormEjemplo from './UsuarioFormEjemplo';
import UsuariosListaEjemplo from './UsuariosListaEjemplo';
import { usuarios } from './Usuarios.json';

//function App() {
class App extends Component {
    
    constructor(){
        super();
        this.state = {
            usuarios
        }
        this.handleAddUsuario = this.handleAddUsuario.bind(this);
        this.handleSetUsuario = this.handleSetUsuario.bind(this);
    }

    handleAddUsuario(usuario) {
        this.setState({
            usuarios: [...this.state.usuarios, usuario]
        })
    }

    handleSetUsuario(index) {
        this.setState({
            usuarios: this.state.usuarios.filter((e, i) => {
                return i !== index
            })
        });
    }    

    render(){
        return(
            <div className="App">
            
            <div className="row">
              <div className="col-md-6">
                <UsuarioFormEjemplo onAddUsuario={this.handleAddUsuario}/>
              </div>
              
              <div className="col-md-6">
                <UsuariosListaEjemplo usuarios={this.state.usuarios} onSetUsuario={this.handleSetUsuario}/>
              </div>
            </div>
          </div>
        );    
    }

}


export default App;
