import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useLocation } from 'react-router-dom'
import LogApi from '../Api/Services/LogApi';


function LogEtapaLista() {


    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    const [getRespuestaLog, setRespuestaLog] = useState(false);
    const [getLogLista, setLogLista] = useState({ data: [], links: [], meta: [] });



    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "log_etapa",
                    "attributes": {
                        "descripcion": "descripcion",
                        "id_etapa": "fecha_registro",
                        "id_fase": "id_dependencia",
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_tipo_log": "id_tipo_log",
                        "id_estado": "id_estado",
                        "created_user": "created_user",
                        "id_dependencia_origen": "id_dependencia_origen"
                    }
                }
            }


            LogApi.getLogEtapaProcesoDisciplinario(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setLogLista(datos), setRespuestaLog(true)) : window.showModal(1)
            )

        }
        fetchData();
    }, []);


    const listaLog = () => {

        if (getLogLista.data != null && typeof (getLogLista.data) != 'undefined') {
            return (

                getLogLista.data.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
        else {
            return (

                getLogLista.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
    }


    const cargarLog = (log, i) => {

        //from.idAntecedente = antecedente.id;

        return (
            <>
                <li class="timeline-event">
                    <div class="timeline-event-icon bg-info"><i class="fas fa-cog"></i></div>
                    <div class="timeline-event-block block block-rounded block-fx-pop" data-toggle="appear">
                        <div class="block-header block-header-default">
                            <h3 class="block-title">{log.attributes.etapa.nombre}</h3>
                            <div class="block-options">
                            
                            </div>
                        </div>
                        <div class="block-content">
                            <div class="media font-size-sm">
                                <div class="media-body">
                                    <strong> Descripci√≥n: </strong>{log.attributes.descripcion}<br/>
                                    <strong> Dependencia: </strong>{log.attributes.dependencia_origen.nombre}<br/>
                                    <strong> Fecha: </strong>{log.attributes.dependencia_origen.nombre}<br/>
                                    <strong> Estado: </strong>{log.attributes.estado_etapa.nombre}<br/><br/>
                                    <strong> Funcionario:</strong><br/>
                                    <strong> Rol:</strong><br/>
                                </div>
                            </div>
                        </div>
                    </div>
                </li>
            </>
        )
    }





    return (
        <> 

        <ul class="timeline timeline-alt">
            {getRespuestaLog ? listaLog() : null}
            
        </ul>



            
        </>
    );
}


export default LogEtapaLista;
