import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import DataTable from 'react-data-table-component';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';

function FaseLista() {
    const [getFaseLista, setFaseLista] = useState({ data: [] });
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getSeach, setSeach] = useState('');

    const [getEstadoLista, setEstadoLista] = useState('1');
    const [getRoutes, setRoutes] = useState({

        crear_registro: "/Fase/Add",
        consultar_registros: "/Fase",
        muestra_atras: false
    });

    const handleCallback = (childData) => {
        try {

            setEstadoLista(childData)
        } catch (error) {

        }

    }

    const columns = [
        {
            name: 'Nombre',
            selector: fase => fase.attributes.nombre,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Etapa',
            selector: fase => fase.attributes.etapa.nombre,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Estado',
            selector: fase => (fase.attributes.estado == global.Constants.ESTADOS.ACTIVO ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`/Fase/${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarFases(1, paganationPerPages);
        }
        fetchData();
    }, []);


    const cargarFases = (page, perPage) => {

        GenericApi.getAllGeneric('mas-fase-sin-estado').then(
            datos => {
                if (!datos.error) {

                    setFaseLista(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal();
                    window.showSpinner(false);
                }

            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);

    }

    const handlePerRowsChange = async (newPerPage, page) => {
        //window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);


    }





    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de fases</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Fases registradas {getEstadoLista == global.Constants.ESTADOS.INACTIVO ? "Inactivas" : "Activas"}</h3>

                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-3'>
                            <div className="form-group ">

                                <input type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>

                        <ListaBotones getRoutes={getRoutes} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={getFaseLista.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        if (suggestion.attributes.estado == getEstadoLista) {
                                            return suggestion;
                                        }
                                    } else if (

                                        ((suggestion.id
                                            + suggestion.attributes.nombre
                                            + (suggestion.attributes.etapa.nombre)
                                            + (suggestion.attributes.estado == global.Constants.ESTADOS.ACTIVO ? 'Activo' : 'Inactivo')).toLowerCase().includes(getSeach.toLowerCase())
                                            && (suggestion.attributes.estado == getEstadoLista))

                                    ) {
                                        return suggestion;
                                    }
                                })}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                paginationTotalRows={getFaseLista.data.length}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                striped
                                paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default FaseLista;
