import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom'
import ListaBotones from '../../Utils/ListaBotones';

function LogEtapa() {


    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    const [getRespuestaAntecedentes, setRespuestaAntecedentes] = useState(false);
    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });



    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": 'antecedente',
                    "attributes": {
                        "antecedentes": "antecedentes",
                        "descripcion": "abc",
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "fecha_registro": Date.now(),
                        "id_dependencia": -1,
                        "estado": 1
                    }
                }
            }

            /*AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setAntecedentesListaTotal(datos), setRespuestaAntecedentesTotal(true),
                    setAntecedentesLista(datos), setRespuestaAntecedentes(true)) : window.showModal(1)
            )*/

        }
        fetchData();
    }, []);


    return (
        <> 

        <ul class="timeline timeline-centered timeline-alt">

        <li class="timeline-event">
                            <div class="timeline-event-icon bg-info">
                                <i class="fab fa-twitter"></i>
                            </div>
                            <div class="timeline-event-block block block-rounded block-fx-pop invisible" data-toggle="appear">
                                <div class="block-header block-header-default">
                                    <h3 class="block-title">Update</h3>
                                    <div class="block-options">
                                        <div class="timeline-event-time block-options-item font-size-sm font-w600">
                                            30 min ago
                                        </div>
                                    </div>
                                </div>
                                <div class="block-content">
                                    <div class="media font-size-sm">
                                        <a class="img-link mr-2" href="javascript:void(0)">
                                            
                                        </a>
                                        <div class="media-body">
                                            <p>
                                                <a class="font-w600" href="javascript:void(0)">Carol Ray</a>
                                                Vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aliquam tincidunt sollicitudin sem nec ultrices. Sed at mi velit.
                                            </p>
                                            <p>
                                                <a class="text-dark mr-2" href="javascript:void(0)">
                                                    <i class="fa fa-reply fa-fw text-muted"></i> Reply
                                                </a>
                                                <a class="text-dark mr-2" href="javascript:void(0)">
                                                    <i class="fa fa-redo fa-fw text-muted"></i> Retweet
                                                </a>
                                                <a class="text-dark mr-2" href="javascript:void(0)">
                                                    <i class="fa fa-heart fa-fw text-muted"></i> Like
                                                </a>
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </li>

        </ul>



            
        </>
    );
}


export default LogEtapa;
