import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import '../../Utils/Constants';

function ModalGen(props) {
    
    const navigate = useNavigate();
    const [show, setShow] = useState(props.data.show);


    const handleClose = () => {
        if (props.data.redirect != undefined)
            navigate(props.data.redirect, { state: props.data.from });
        props.data.show = false;
        setShow(false);
    };

    useEffect(() => {
        setShow(props.data.show);
    }, [props.data.show])


    const tipoAlerta = (id_alerta) => {
       
        if(id_alerta == global.Constants.TIPO_ALERTA.EXITO)
            return "block-header bg-primary-dark";
        else
            return "block-header bg-danger-light";
    };



    return ( 
        <>
            <Modal show={show} onHide={handleClose}>
                <div className="block block-themed block-transparent mb-0">
                    <Modal.Header className={tipoAlerta(props.data.alerta)}>
                        <Modal.Title className='block-title'>{props.data.title}</Modal.Title>
                        <p>{props.data.alerta}</p>
                        <Button variant="primary" onClick={handleClose} className='btn-block-option'>
                            <i className="fa fa-fw fa-times"></i>
                        </Button>
                    </Modal.Header>
                    <Modal.Body>{props.data.message}</Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" onClick={handleClose}>
                            Aceptar
                        </Button>
                    </Modal.Footer>
                </div>
            </Modal>
        </>
    );
}

export default ModalGen;
