import { ErrorMessage, Field, Form, Formik, replace } from 'formik';
import React, { useEffect, useReducer, useState } from 'react';
import { useParams } from "react-router";
import { getUser, hasAccess } from '../../../components/Utils/Common';
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import InfoConfirmarAccion from '../../Utils/InfoConfirmarAccion';
import ModalActuaciones from '../../Utils/Modals/ModalActuaciones';
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';


function ActuacionesVer() {

    const [errorApi, setErrorApi] = useState('');
    const location = useLocation()
    const { from, selected_id_etapa, id, nombre, estadoActualActuacion } = location.state
    let radicado = from.radicado;

    const [getTrazabilidadListaSearch, setTrazabilidadListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getArchivosListaSearch, setArchivosListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoTrazabilidadActuaciones, setRtaInfoTrazabilidadActuaciones] = useState(false);
    const [getRtaInfoArchivoActuaciones, setRtaInfoArchivoActuaciones] = useState(false);
    const [getRtaInfoTrue, setRtaInfoTrue] = useState(false);
    const [getPesoTotalArchivos, setPesoTotalArchivos] = useState(0);
    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [inputListArchivos, setInputListArchivos] = useState([{ archivo: {}, filebase64: "", size: 0, ext: "" }]);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [inputActualizarArchivos, setInputActualizarArchivos] = useState([{ archivo: {}, filebase64: "", size: 0, ext: "", uuid: "" }]);
    const [getValidarAccion, setValidarAccion] = useState(0);
    const [getRtaJefe, setRtaJefe] = useState(false);
    const [getInfoModal, setInfoModal] = useState(false);
    const [getMessageModal, setMessageModal] = useState('');
    const [getNameUsuario, setNameUsuario] = useState("");
    const [getUltimoEstado, setUltimoEstado] = useState("");
    const [getUsuarioSolicitante, setUsuarioSolicitante] = useState("");

    useEffect(() => {
        async function fetchData(params) {
            IdDependenciaAceptaSolicitudesDeAnulacion();
            setNombreUsuario(getUser().nombre);
            window.showSpinner(true);

            // Buscamos el detalle de los documentos
            GenericApi.getGeneric("archivo-actuaciones/get-archivo-actuaciones-by-uuid/" + id).then(
                datos => {

                    if (!datos.error) {
                        if (datos.data.length > 0) {
                            setArchivosListaSearch(datos);
                            setRtaInfoArchivoActuaciones(true);
                        }
                    } else {
                        // cargarHistorialCambios(id);
                        // setModalState({ title: "Datos del interesado", message: datos.error.toString(), show: true, redirect: '/DatosInteresadoLista', from: { from } });
                    }
                }
            )

            // Buscamos el detalle de la trazabilidad
            GenericApi.getGeneric("trazabilidad-actuaciones/get-all-trazabilidad-actuaciones-uuid/" + id).then(
                datos => {

                    if (!datos.error) {
                        if (datos.data.length > 0) {
                            window.showSpinner(false);

                            setTrazabilidadListaSearch(datos);
                            setRtaInfoTrazabilidadActuaciones(true);
                        }
                    } else {
                        // cargarHistorialCambios(id);
                        // setModalState({ title: "Datos del interesado", message: datos.error.toString(), show: true, redirect: '/DatosInteresadoLista', from: { from } });
                    }
                }
            )

            jefeDependencia();
        }
        fetchData()
    }, []);

    // Se consulta si el usuario que se encuentra en sesión es jefe de la dependencia
    const jefeDependencia = () => {

        const data = {
            "data": {
                "type": "usuario",
                "attributes": {
                    "": ""
                }
            }
        }
        GenericApi.getByDataGeneric("usuario/get-jefe-dependencia", data).then(
            datos => {
                if (!datos.error) {
                    setRtaJefe(true);
                } else {
                    setRtaJefe(false);
                }
            }
        )
    }

    const handleClicArchivo = (id_documento, extension, nombre_documento) => {

        try {
            window.showSpinner(true);

            // Se genera la peticion para descargar el archivo
            GenericApi.getGeneric("archivo-actuaciones/get-documento/" + id_documento + "/" + extension).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento);
                    } else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const infoTrazabilidad = () => {
        setUltimoEstado(getTrazabilidadListaSearch.data[0].attributes.nombre_estado_actuacion);
        setUsuarioSolicitante(getTrazabilidadListaSearch.data[0].attributes.created_user);
        return (getTrazabilidadListaSearch.data.map((t) => {
            return (
                <tr key={t.attributes.id}>
                    <td>
                        {t.attributes.created_at}
                    </td>
                    <td>
                        {t.attributes.usuario.nombre+" "+t.attributes.usuario.apellido+" ("+t.attributes.id_dependencia.nombre+")"}
                    </td>
                    <td>
                        {t.attributes.nombre_estado_actuacion}
                    </td>
                    <td>
                        {t.attributes.observacion}
                    </td>
                </tr>
            )
        }))
    }

    const infoArchivos = () => {
        return (getArchivosListaSearch.data.map((t) => {
            return (
                <tr key={t.attributes.id}>
                    <td>
                        {t.attributes.nombre_tipo_archivo}

                    </td>
                    <td>
                        {t.attributes.nombre_archivo}
                    </td>
                    <td>
                        <div className='col-3'>
                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(t.attributes.id, t.attributes.extension, t.attributes.nombre_archivo)}><i className="fas fa-download"></i></button>
                        </div>
                    </td>
                    <td style={{ width: '35%' }}>
                        <div className='col-12'>
                            {inputListArchivos.map((x, i) => {
                                return (
                                    <div className="col-md-12" key={i}>
                                        <label className="custom-file-label" htmlFor="example-file-input-custom" name="input-file" data-toggle="custom-file-input">{x.archivo.name}</label>
                                        <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i, 1, t.attributes.id, t.attributes.uuid_actuacion, t.attributes.nombre_tipo_archivo)} required />
                                        <label >Peso del archivo: {formatBytes(x.archivo.size)}</label>
                                    </div>
                                );
                            })}
                        </div>
                    </td>
                </tr >
            )
        }))
    }

    const handleInputChangeArchivos = (e, index, actualizar = 0, id = 0, uuid_actuacion = 0, tipo_archivo = "") => {

        const { name, files } = e.target;
        if (files.length > 0) {
            const extension = getFileExtension(files[0].name);

            var error = 0;

            if (tipo_archivo == "Documento inicial" && actualizar == 1 && extension != "docx") {
                setErrorApi("El archivo seleccionado no tiene un formato permitido debe ser .docx")
                window.showModal(1)
                error = 1;
                return false;
            } else if (tipo_archivo == "Documento definitivo" && actualizar == 1 && extension != "pdf") {
                setErrorApi("El archivo seleccionado no tiene un formato permitido, debe ser .pdf")
                window.showModal(1)
                error = 1;
                return false;
            }

            if (error == 0) {

                if (actualizar == 0) {
                    var list = [...inputListArchivos];
                    list[index][name] = files[0];
                } else {
                    var list = [...inputActualizarArchivos];
                    list[index][name] = files[0];
                    setValidarAccion(1);
                }

                if (files[0]) {
                    list[index][name] = files[0];
                } else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].ext = "";
                    list[index].uuid = "";
                    if (actualizar == 0) {
                        setInputListArchivos(list);
                    } else {
                        setInputActualizarArchivos(list);
                    }
                    return false;
                }

                // Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].ext = extension;
                        list[index].uuid = id;
                        if (actualizar == 0) {
                            setInputListArchivos(list);
                        } else {
                            list[index].uuid_actuacion = uuid_actuacion;
                            setInputActualizarArchivos(list);
                            setValidarAccion(1);
                        }
                        setRtaInfoTrue(true);
                        window.showModal(9)
                    }
                })
                obtenerPesoTotalArchivos(list);
            } else {
                if (actualizar == 0) {
                    var list = [...inputListArchivos];
                } else {
                    var list = [...inputActualizarArchivos];
                }
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                list[index].uuid = "";
                if (actualizar == 0) {
                    setInputListArchivos(list);
                } else {
                    list[index].uuid_actuacion = uuid_actuacion;
                    setInputActualizarArchivos(list);
                }
            }
        } else {
            if (actualizar == 0) {
                var list = [...inputListArchivos];
            } else {
                var list = [...inputActualizarArchivos];
            }
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            list[index].uuid = "";
            if (actualizar == 0) {
                setInputListArchivos(list);
            } else {
                setInputActualizarArchivos(list);
            }
            obtenerPesoTotalArchivos(list);
            return false;
        }
    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function obtenerPesoTotalArchivos(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if (archivo.archivo.size) {
                    peso += archivo.archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setErrorApi('El peso/tamaño de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tamaño y elimine algunos adjuntos, (Si desea enviar mas archivos realice otro proceso de registro de Soportes del radicado)')
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    }

    function subirArchivo(e, accion) {

        if (e.type == "click") {

            window.showSpinner(true);

            if (accion == 0) {
                const data = {
                    "data": {
                        "type": 'archivo-actuaciones/up-documento',
                        "attributes": {
                            "uuid_actuacion": getArchivosListaSearch.data[0].attributes.uuid_actuacion,
                            "fileBase64": inputListArchivos[0].filebase64,
                            "nombre_archivo": inputListArchivos[0].archivo.name,
                            "extension": inputListArchivos[0].ext,
                            "peso": inputListArchivos[0].archivo.size,
                            "id_tipo_archivo": 2
                        }
                    }
                }

                GenericApi.addGeneric("archivo-actuaciones/up-documento", data).then(
                    datos => {

                        if (!datos.error) {
                            setModalState({ title: "SINPROC No " + radicado + " :: Crear Actuaciones", message: 'Registro Exitoso', show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.EXITO });
                        } else {
                            setModalState({ title: "SINPROCNo " + radicado + " :: Crear Actuaciones", message: datos.error.toString(), show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                        }
                        window.showSpinner(false);
                    }
                )
            } else if (accion == 1) {

                const data = {
                    "data": {
                        "type": 'archivo-actuaciones/update-documento',
                        "attributes": {
                            "uuid_actuacion": inputActualizarArchivos[0].uuid,
                            "fileBase64": inputActualizarArchivos[0].filebase64,
                            "nombre_archivo": inputActualizarArchivos[0].archivo.name,
                            "extension": inputActualizarArchivos[0].ext,
                            "peso": inputActualizarArchivos[0].archivo.size,
                            "id_tipo_archivo": 2
                        }
                    }
                }

                GenericApi.getByDataGeneric("archivo-actuaciones/update-documento/" + inputActualizarArchivos[0].uuid + "/" + inputActualizarArchivos[0].uuid_actuacion, data).then(
                    datos => {

                        if (!datos.error) {
                            setModalState({ title: "SINPROC No " + radicado + " :: Crear Actuaciones", message: 'Registro actualizado correctamente', show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.EXITO });
                        } else {
                            setModalState({ title: "SINPROCNo " + radicado + " :: Crear Actuaciones", message: datos.error.toString(), show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                        }
                        window.showSpinner(false);
                    }
                )
            }


        }
    }

    const aprobarActuacion = (e) => {
        setInfoModal(true);
        setMessageModal("¿Confirma aprobación de la actuacion " + nombre + "?");
        window.showModal(10);
    }

    const rechazarActuacion = (e) => {
        setInfoModal(true);
        setMessageModal("¿Confirma rechazo de la actuacion " + nombre + "?");
        window.showModal(10);
    }

    const IdDependenciaAceptaSolicitudesDeAnulacion = () => {
        const data = {
            "data": {
                "type": "mas_parametro",
                "attributes": {
                    "nombre": "id_dependencia_que_atiende_solicitud_de_anulacion"
                }
            }
        }

        //buscamos el parametro
        GenericApi.getByDataGeneric("parametro/parametro-nombre", data).then(
            datos => {

                if (!datos.error) {
                    IdUsuarioJefeDependencia(datos.data[0].attributes.valor);
                } else {
                    // Aqui tengo que colocar algun mensaje de que no existe una dependencia asignada
                    // para atender las solicitudes de anulacion
                }
            }
        )
    }

    const IdUsuarioJefeDependencia = (id) => {
        GenericApi.getByIdGeneric("mas-dependencia-origen", id).then(
            datos => {

                if (!datos.error) {
                    consultarUsuario(datos.data.attributes.id_usuario_jefe);
                } else {

                }

            }
        )
    }

    const consultarUsuario = (id) => {
        GenericApi.getByIdGeneric("usuario", id).then(
            datos => {

                if (!datos.error) {
                    setNameUsuario(datos.data.attributes.name);
                } else {

                }
            }
        )
    }

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            {<InfoErrorApi error={errorApi} />}
            {getRtaInfoTrue ? <InfoConfirmarAccion mensaje={'¿Esta seguro que desea subir este archivo?'} function={e => subirArchivo(e, getValidarAccion == 0 ? getValidarAccion : 1)} /> : null}
            {<InfoExitoApi />}
            {getInfoModal ? <ModalActuaciones accionActuacion={getMessageModal} /> : null}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    //enviarDatos(valores);
                }}
            >
                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}><small>Actuaciones</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Ver detalle actuación</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Actuación detalle</strong></h3>
                            </div>
                            <div className="block-content">

                                <h3 className="block-title mb-4"> <b>Actuación: </b>{nombre}</h3>

                                {(estadoActualActuacion != "Aprobada" || (getArchivosListaSearch.data.length == 2 || getArchivosListaSearch.data.length > 2)) ? null :
                                    <div className='col-md-12'>
                                        <table className="table table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    {inputListArchivos.map((x, i) => {
                                                        return (
                                                            <div className="col-md-12" key={i}>
                                                                <div className="form-group" style={{
                                                                    display: 'flex',
                                                                    justifyContent: 'flex-end'
                                                                }}>
                                                                    <div className='row'>
                                                                        <div className='col-md-12'>
                                                                            <div className='col-md-12' style={{
                                                                                textAlign: 'center',
                                                                                marginLeft: '14%'
                                                                            }}>
                                                                                <label>Cargar archivo definitivo<span className="text-danger">* </span></label>
                                                                            </div>
                                                                        </div>
                                                                        <div className='col-md-12' style={{
                                                                            display: 'flex',
                                                                            justifyContent: 'flex-end'
                                                                        }}>
                                                                            <div className='col-md-6'>
                                                                                <label className="custom-file-label" htmlFor="example-file-input-custom" name="input-file" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                                <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i)} required />
                                                                                <label >Peso del archivo: {formatBytes(x.archivo.size)}</label>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        );
                                                    })}
                                                </tr>
                                            </thead>
                                        </table>
                                    </div>
                                }

                                <div className='col-md-12'>
                                    <h3 className="block-title" style={{ marginBottom: '12px' }}><b>Archivos actuación</b></h3>
                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                        <thead>
                                            <tr>
                                                <th>Tipo </th>
                                                <th>Documento</th>
                                                <th>Descarga </th>
                                                <th>Actualizar</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {getRtaInfoArchivoActuaciones ? infoArchivos() : null}
                                        </tbody>
                                    </table>
                                </div>

                                {
                                    (hasAccess('Administrador', 'SolicitudDeAprobación')) && getUltimoEstado == "Pendiente aprobación" && getRtaJefe ?
                                        <div className='col-md-12' style={{ marginTop: '45px' }}>
                                            <table>
                                                <thead>
                                                    <tr>
                                                        <th style={{ width: '30%' }}>Estado de aprobación: </th>
                                                        <th style={{ paddingLeft: '50px' }}>
                                                            <button type="button" className="btn btn-primary" onClick={(e) => aprobarActuacion(e)}> Aprobar</button>
                                                            <button type="button" className="btn btn-danger" onClick={(e) => rechazarActuacion(e)}> Rechazar</button>
                                                        </th>
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div> : null
                                }
                                {
                                    (hasAccess('Administrador', 'SolicitudDeAnulación')) && getUltimoEstado == "Solicitud anulación" ? (
                                        <div className='col-md-12'>
                                            <label><b>Estado de aprobación de anulación: </b></label>

                                            <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Acepta", solicitante: getUsuarioSolicitante }}>
                                                <button type="button" className="btn btn-primary ml-3">Aprobar</button>
                                            </Link>
                                            <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Rechaza", solicitante: getUsuarioSolicitante }}>
                                                <button type="button" className="btn btn-danger">Rechazar</button>
                                            </Link>
                                        </div>) : null
                                }

                                <div className='col-md-12' style={{ marginTop: '45px' }}>
                                    <h3 className="block-title" style={{ marginBottom: '12px' }}><b>Historial de aprobaciónes de la actuación</b></h3>
                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                        <thead>
                                            <tr>
                                                <th style={{ width: '23%' }}>Fecha</th>
                                                <th>Usuario</th>
                                                <th>Estado</th>
                                                <th>Observaciones</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {getRtaInfoTrazabilidadActuaciones ? infoTrazabilidad() : null}
                                        </tbody>
                                    </table>
                                </div>
                            </div >
                            
                                    <div className="block-content block-content-full text-left bg-light">
                                        <Link to="/Transacciones" state={{ from: from }}>
                                            <button type="button" className="btn btn-primary"><i class="fa fa-arrow-right"></i> Transacciones</button>
                                        </Link>
                                        {getUltimoEstado == "Solicitud anulación" || getUltimoEstado == "Solicitud anulación aceptada" ?
                                            null
                                        : (
                                            <Link to={`/ActuacionesSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion }}>
                                                <button type="button" className="btn btn-primary"><i class="fas fa-trash-alt"></i> Solicitar Anulación</button>
                                            </Link>
                                        )}
                                    </div>
                                
                            

                            <div className="block-content block-content-full text-right bg-light">
                                <Link to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div >

                    </Form >
                )
                }
            </Formik >
        </>
    );

}

export default ActuacionesVer;
