import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';

function AntecedentesCambiarEstadoForm() {

   
    const [isRedirect, setIsRedirect] = useState(false);

    const [countTextArea, setCountTextArea] = useState(0);
    const [nombreUsuario, setNombreUsuario] = useState("");

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    let antecedentesId = from.idAntecedente;


    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/AntecedentesLista/`} state={{ from: from}}/>;
    }

    const [getErrorApi, setErrorApi] = useState('');
    const [getAntecedente, setAntecedente] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoAntecedente, setRtaInfoAntecedente] = useState(false);
    const [getEstadoAntecedente, setEstadoAntecedente] = useState("Activar");

    useEffect(() => {
        async function fetchData() {

            console.log(antecedentesId);
            setNombreUsuario(getUser().nombre);
            AntecedentesApi.getAntecedenteById(antecedentesId).then(
                datos => {
                    if (!datos.error) {

                        if(datos.data.attributes.estado==true){
                            setEstadoAntecedente("Inactivar");
                        }
                      
                    
                        setAntecedente(datos);
                        setRtaInfoAntecedente(true);
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()

                    }
                      
                }
            )

        }
        fetchData();
    }, []);


    const CargarInfoAntecedente = () => {

        if (getAntecedente.data != null && typeof (getAntecedente.data) != 'undefined') {
            return (
                <tr>
                    <td style={{width: "15%"}}>
                        {getAntecedente.data.attributes.created_user}
                        {getAntecedente.data.attributes.etapa.nombre}
                    </td>
                    <td style={{width: "15%"}}>{getAntecedente.data.attributes.fecha_registro}</td>
                    <td style={{width: "15%"}}>{getAntecedente.data.attributes.nombre_dependencia}</td>
                    <td  data-toggle="popover" data-placement="top" title={getAntecedente.data.attributes.descripcion}>{getAntecedente.data.attributes.descripcion_corta}</td>
                </tr>
            )
        }

    }


    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
        
            {<InfoErrorApi error={getErrorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)
                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < 10) {
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    else if (valores.descripcion.length > 4000) {
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    //enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from}}><small>Ramas del proceso</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/AntecedentesLista/`} state={{ from: from}}><small>Antecedentes</small></Link></li>
                                            <li class="breadcrumb-item"> <small>Cambiar estado de antecedente</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">{getEstadoAntecedente} el registro de Antecedente. SINPROC No {radicado} :: Antecedentes</h3>
                            </div>
                            <div className="block-content">

                            
                            <div className='col-md-12'>

                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th>Registrado por</th>
                                            <th>Fecha registro</th>
                                            <th>Dependencia</th>
                                            <th>Descripción</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                    {getRtaInfoAntecedente ? CargarInfoAntecedente() : null}

                                    </tbody>
                                </table>
                            </div>
                             



                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Motivo por el que {getEstadoAntecedente} el antecedente <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Escriba en este espacio los antencedentes"></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / 4000</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/AntecedentesLista/`} state={{ from: from}}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default AntecedentesCambiarEstadoForm;
