import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import ParametrosMasApi from '../Api/Services/ParametrosMasApi';
import { Navigate } from "react-router-dom";
import { useParams } from "react-router";
import Autocomplete from "../Autocomplete/Autocomplete";





function EntidadesInteresado() {

    const [errorApi, setErrorApi] = useState('');
    const [listaEntidad, setListaEntidad] = useState({ data: {} });
    const [respuestaEntidad, setRespuestaEntidad] = useState(false);
    const [idEntidad, setIdEntidad] = useState('');

    useEffect(() => {
        async function fetchData() {

            window.showSpinner(true);

            ParametrosMasApi.getAllEntidades().then(
                datos => !datos.error ? (setListaEntidad(datos), setRespuestaEntidad(true)) : window.showModal(1)
            )
        }
        fetchData();
    }, []);

    const handleCallback = (childData) => {
        try {
            setIdEntidad(childData)
        } catch (error) {

        }

    }

    return (
        <>

            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    //enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>


                        <div className="block block-rounded block-bordered">

                            <div className="block-content block-content-full">
                                <div className="row">

                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor='idEntidad'>Entidad pública <span className="text-danger">*</span></label>

                                            <Autocomplete suggestions={listaEntidad} parentCallback={handleCallback} />

                                            <ErrorMessage name="idEntidad" component={() => (<span className="text-danger">{errors.idEntidad}</span>)} />
                                        </div>

                                    </div>


                                </div>

                            </div>

                        </div>
                    </Form>
                )}
            </Formik>

            <div className="col-md-15">
                <div className="block block-rounded block-bordered">
                    <div className="block-header block-header-default">
                        <h3 className="block-title">Entidades permitidas para comunicación quejoso</h3>
                    </div>

                    <div className="block-content block-content-full">
                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <thead>
                                <tr>
                                    <th className="text-center">Entidad</th>
                                    <th className="text-center">Estado</th>
                                    <th className="text-center">Fecha registro</th>
                                    <th className="text-center">Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    )

}

export default EntidadesInteresado;
