import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import DataTable from 'react-data-table-component';

function DependenciasLista() {
    const [dependenciasLista, setDependenciasLista] = useState({ data: [] });
    const [dependenciasListaTotal, setDependenciasListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);
    const columns = [
        {
            name: 'Nombre',
            selector: dependencia => dependencia.attributes.nombre,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Usuario Jefe',
            selector: dependencia => dependencia.attributes.nombre_usuario_jefe,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Estado',
            selector: dependencia => (dependencia.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarDependencias(1, 10);
        }
        fetchData();
    }, []);


    const cargarDependencias = (page, perPage) => {

        GenericApi.getAllGeneric('mas-dependencia-origen/dependencia-paginate' + '/' + page + '/' + perPage).then(
            datos => {
                if (!datos.error) {
                    setDependenciasLista(datos);
                    setDependenciasListaTotal(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal();
                    window.showSpinner(false);
                }

            }
        )
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarDependencias(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarDependencias(page, newPerPage);

    }

    const cambiarEstado = (objeto, estado) => {

        const data = {
            "data": {
                "type": "mas_dependencia_origen",
                "attributes": {
                    "nombre": objeto.attributes.nombre,
                    "estado": estado,
                    "id_usuario_jefe": ""
                }
            }
        }

        GenericApi.updateGeneric('mas-dependencia-origen', objeto.id, data).then(
            datos => {
                if (!datos.error) {
                    cargarDependencias();
                }
                else
                    window.showModal();
            }
        )

    }

    const dependencias = () => {
        return (
            dependenciasLista.data.map((dependencia, i) => {
                return (
                    <tr key={dependencia.id}>
                        <td style={{ width: "50%" }}>
                            {dependencia.attributes.nombre}
                        </td>
                        <td style={{ width: "20%" }}>
                            {dependencia.attributes.nombre_usuario_jefe}
                        </td>
                        <td>
                            {dependencia.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${dependencia.id}`}>
                                <button type="button" className="btn btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>

                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setDependenciasLista(dependenciasListaTotal);
            }
            else {
                var filteredData = {
                    data: dependenciasListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + suggestion.attributes.nombre_usuario_jefe
                            + (suggestion.attributes.dependencia ? suggestion.attributes.dependencia.nombre : '')
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setDependenciasLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de dependencias</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Dependencias registradas</h3>
                    <Link to={`/Dependencia/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar dependencia</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={dependenciasLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={dependenciasLista.meta ? dependenciasLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default DependenciasLista;
