import axios from 'axios'
import { getToken, getUser, removeUserSession, setUserSession } from '../../../components/Utils/Common';

const baseUrl = process.env.REACT_APP_API_URL;

const headers = {
    'Content-Type': 'application/vnd.api+json',
    'Accept': 'application/vnd.api+json'
}

async function getAllPendientes(){

    const url = baseUrl + "mis-pendientes";
    const token = getToken();
    headers['Authorization'] = `Bearer ${token}`;

    return await axios.get(url, {
        headers: headers
    })
    .then(response => {
        console.log(response.data);
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });
}

async function getAllPendientesFilter(data){

    const url = baseUrl + "mis-pendientes-filter";
       const token = getToken();
    headers['Authorization'] = `Bearer ${token}`;
    
    return await axios.post(url,data, {
        headers: headers
    })
    .then(response => {
        //console.log(JSON.stringify(response.data));
        return response.data;
    })
    .catch(error => {
        //console.log(error);
        return {estado: false, error: error}
    });
}

