,/*#__PURE__*/_jsx("input",{className:"custom-file-input manual","data-toggle":"custom-file-input2",type:"file",name:"archivoManual",onChange:function onChange(e){return handleInputChangeArchivosManual(e,i);}}),/*#__PURE__*/_jsxs("label",{children:["Peso del archivo: ",formatBytes(x.archivoManual.size)," "]}),/*#__PURE__*/_jsx(ErrorMessage,{name:"archivoManual",component:function component(){return/*#__PURE__*/_jsx("span",{className:"text-danger",children:errors.archivo});}})]}),from!=null?/*#__PURE__*/_jsx("div",{className:"col-2",children:/*#__PURE__*/_jsx("button",{type:"button",title:"Descargar documento",className:"btn btn-sm btn-primary",onClick:function onClick(){return handleClicArchivoManual();},children:/*#__PURE__*/_jsx("i",{className:"fas fa-download"})})}):""]})})]})})},i);}),/*#__PURE__*/_jsxs("div",{className:"block-content block-content-full text-right bg-light",children:[/*#__PURE__*/_jsx("button",{type:"submit",className:"btn btn-rounded btn-primary",children:from!=null?"Actualizar":"Crear"}),/*#__PURE__*/_jsx(Link,{to:'/ActuacionesAdministracion',className:"font-size-h5 font-w600",children:/*#__PURE__*/_jsx("button",{type:"button",className:"btn btn-rounded btn-outline-primary",children:"Cancelar"})})]})]})]})]})});}})]});};

