    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function obtenerPesoTotalArchivos(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if (archivo.archivo.size) {
                    peso += archivo.archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setErrorApi('El peso/tama帽o de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tama帽o y elimine algunos adjuntos.')
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

