function Spinner() {

    const style1 = {
        "display": "flex"
    }

    return (
        <div className="modal" id="modal-block-spinner" tabIndex="-1" role="dialog" aria-labelledby="modal-block-spinner" aria-hidden="true" data-keyboard="false" data-backdrop="static">
            <div className="modal-dialog modal-dialog-centered" role="document">
                <div className="modal-content">
                    <div className="block block-themed block-transparent mb-0">                        
                        <div className="block-content">
                            <div className="row">
                                <div className="swal2-icon swal2-warning swal2-animate-warning-icon" style={style1}></div>
                            </div>
                            <div className="row-w2d">
                                <h3 className="text-center">Cargando la información, espera un momento...</h3>
                            </div>
                            <div className="text-center">
                                <div className="spinner-border text-primary" role="status">
                                    <span className="sr-only">Cargando...</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

//Cargando la información, espera un momento...

export default Spinner;
