                        {selectCargosInvestigados(parametro, tempCampo.id_campo)}
                    </div>
                )
            } else if (itemCampo.type == "Entidad") {

                var validarElemento = document.getElementById('id' + tempCampo.parametro);
                if ((getEntidadInvestigadoLista[0] && getEntidadInvestigadoLista.length == 1) && validarElemento && !validarElemento.hasAttribute("disabled")) {
