                item.id_campo = id_campo;
                var itemCampo = getListaParametrosBaseDatos[id_campo];

                item.valor = (itemCampo.value != null) ? itemCampo.value : value;
                item.valorTemporal = (itemCampo.value != null) ? itemCampo.value : value;
            }
            return item;
        });

        //setListaCamposPorParametroDefinitiva({ data: newParams });
    }

    // Metodo encargado de generar las columnas de los parametros de la plantilla
    const columnsParametros = [
        {
            name: 'PARAMETROS DE LA PLANTILLA',
            cell: parametro => <div><strong>{parametro.toUpperCase()}</strong></div>,
            sortable: true,
            width: "15%"
        },
        {
            name: 'CAMPO',
            cell: parametro =>
                <select className="form-control" id={`id${parametro}`} name={`name${parametro}`}
                    onChange={e => selectChangeListaCampos(parametro, e.target.value, "")} disabled>
                    <option value="">Por favor seleccione</option>
                    {selectCampos(parametro)}
                </select>,
            sortable: false,
            width: "25%",
        },
        {
            name: 'VALOR SELECCIONADO',
            width: "50%",
            cell: parametro =>
                <div className='row'>
                    <div className='col'>
                        {cargarValoresDeParametro(parametro)}
                    </div>
                </div>
        },
        // {
        //     name: 'Activar/Inactivar',
        //     width: "10%",
        //     cell: parametro =>
        //         <div className='row'>
        //             <div className='col'>
        //                 {cargarCheckParametro(parametro)}
        //             </div>
        //         </div>
        // }
    ];

    // Metodo encargado de generar las columnas de los parametros de la plantilla
    const columnsCampos = [
        {
            name: 'CAMPO',
            cell: campo =>
                <div>
                    <strong>{campo.nombreCampo ? campo.nombreCampo : ""}</strong>
                </div>,
            sortable: true,
            width: "15%"
        },
        {
            name: 'TIPO',
            cell: campo =>
                <select className="form-control" id={`idCampo${campo.nombreCampo}`} name={`name${campo.nombreCampo}`} disabled>
                    <option value={validarTipoCampo(campo.tipoCampo)} selected> {validarTipoCampo(campo.tipoCampo)} </option>
                </select>
            ,
            sortable: false,
            width: "25%",
        },
        {
            name: 'ITEMS',
            width: "50%",
            cell: campo =>
                <div className='row'>
                    <div className='col'>
                        {cargarValoresCampos(campo)}
                    </div>
                </div>
        },
    ];

    // Metodo encargado de setear el valor al mismo input
    const changeTextInput = (e, arrayDatos) => {

        // Se setea temporalmente
        let elementoHtml = document.getElementById(e.target.name);
        let nombreElemento = e.target.name;

        // Se captura el valor escrito
        let valor = e.target.value;

        // El elemento existe
        if (elementoHtml) {

            // Se setea el nuevo valor
            elementoHtml.title = valor;

            // Se asigna el item dentro del array para enviarlo
            getCamposAdicionales.forEach(e => {

                // Se captura la key
                let key = e.nombreCampo;

                // Se valida que la key sea igual al elemento modificado
                if (key == nombreElemento) {

                    // Se añade el item
                    e.items = [valor];
                }
            })
        }

        // Se setea en el from cada que vez que finaliza la acción
        from.getCamposAdicionales = getCamposAdicionales;
    }

    // Metodo encargado de cambiar el valor de la fecha
    const changeDateInput = (e) => {

        // Se formatea
        const newDate = moment(e.target.value).format('DD/MM/YYYY');

        // Se captura el nombre del elemento
        let nombreElemento = e.target.name;

        // Se setea los valores
        setValue(newDate);

        // Se asigna el item dentro del array para enviarlo
        getCamposAdicionales.forEach(e => {

            // Se captura la key
            let key = e.nombreCampo;

            // Se valida que la key sea igual al elemento modificado
            if (key == nombreElemento) {

                // Se añade el item
                e.items = [newDate];
            }
        })

        // Se setea en el from cada que vez que finaliza la acción
        from.getCamposAdicionales = getCamposAdicionales;
    };

    // Metodo encargado de cambiar el valor del input
    const changeCheckInput = (e, value) => {

        // Se captura el elemento html
        let nombreElemento = value;

        // Se captura el elemento del check true o false
        let checkeado = e.target.checked;

        // Se captura el valor chequeado html
        let valorCheck = e.target.value;

        // Se valida cuando esta chequeado
        if (checkeado == true) {

            // Se asigna el item dentro del array para enviarlo
            getCamposAdicionales.forEach(e => {

                // Se captura la key
                let key = e.nombreCampo;

                // Se valida que la key sea igual al elemento modificado
                if (key == nombreElemento) {

                    // Se valida la primera vez
                    if (!e.itemsSeleccionado) {

                        // Se añade el item
                        e.itemsSeleccionado = [valorCheck];
                    } else {

                        // Se añade el item al array
                        e.itemsSeleccionado.push(valorCheck);
                    }
                }
            })
        } else if (checkeado == false) {

            // Se asigna el item dentro del array para quitarlo
            getCamposAdicionales.forEach(e => {

                // Se captura la key
                let key = e.nombreCampo;
                let items = e.itemsSeleccionado ? e.itemsSeleccionado : [];

                // Continua el proceso para quitarlo del array
                if (items.length > 0 && (key == nombreElemento)) {

                    // Se busca la posicion del elemento
                    var index = items.indexOf(valorCheck);

                    // Se quita el elemento
                    items.splice(index, 1);
                }
            })
        }

        // Se setea en el from cada que vez que finaliza la acción
        from.getCamposAdicionales = getCamposAdicionales;
    }

    // Metodo encargado de cargar los valores de los campos
    const cargarValoresCampos = (campo) => {

        // Se inicializa la variable de campos
        const campoParametro = campo;

        // Se capturan la informacion del campo
        const tipoCampo = campoParametro.tipoCampo;
        const nombreCampo = campoParametro.nombreCampo;
        const itemCampo = tipoCampo == 2 ? campoParametro.items : "";

        // Se valida cuando el tipo campo es un listado
        if (tipoCampo == 2) {

            // Se retorna el mapeo
            return (

                // Se recorre el elemento
                itemCampo.map((item, i) => {

                    // Se retorna el HTML
                    return (
                        <div className="col-md-12 mt-1 ml-2 mb-1" key={item}>
                            <input type="checkbox"
                                className="form-check-input"
                                id={item}
                                name={item}
                                value={item}
                                onChange={(e) => changeCheckInput(e, nombreCampo)}
                                defaultChecked={item.checked} /> {item}
                        </div>
                    )
                })
            )
        } else if (tipoCampo == 1) {

            // Se retorna el html del tipo fecha 
            return (
                <div className="custom-control custom-switch custom-control-lg mt-2 mb-2">
                    <input type="date"
                        className="form-control"
                        id={nombreCampo}
                        name={nombreCampo}
                        onChange={changeDateInput}
                        max={getMaxDate}
                    />
                </div>
            )
        } else if (tipoCampo == 0) {

            // Se retorna el html del tipo texto
            return (
                <div className="custom-control custom-switch custom-control-lg mt-2 mb-2">
                    <input type="text"
                        className="form-control"
                        id={nombreCampo}
                        name={nombreCampo}
                        placeholder={nombreCampo}
                        onChange={e => changeTextInput(e, campo)} />
                </div>
            )
        }
    }

    // Metodo encargado de retorna los items separados
    const validarItems = (item) => {

        // Se valida que exista y tenga un elemento
        if (item && item.length == 0) {

            // Se retorna el primer elemento
            return item;
        } else if (item && item.length > 0) {

            // Se retorna por comas
            return item.join(", ");
        }
    }

    // Metodo encargado de validar el tipo del campo
    const validarTipoCampo = (tipo) => {

        // Se inicializa la variable
        let nombreRetornado;

        // Se valida el tipo
        if (tipo == 0) {

            // Se redeclara la variable
            nombreRetornado = "Texto";
        } else if (tipo == 1) {

            // Se redeclara la variable
            nombreRetornado = "Fecha";
        } else if (tipo == 2) {

            // Se redeclara la variable
            nombreRetornado = "Lista";
        }

        // Se retorna el valor
        return nombreRetornado;
    }

    // Metodo encargado de cargar los parametros de la base de datos
    const cargarParametros = (listaParametrosBaseDatos) => {

        // Se setean los valores en null y vacios para que no salga el limitante de las hooks
        setListaParametros(null);
        setAntecedentesLista({ data: [] });
        setInteresadosLista({ data: [] });
        setEntidadesInvestigadoLista({ data: [] });
        setCargosInvestigadoLista({ data: [] });
        setEntidadInvestigadoLista({ data: [] });
        setListaCamposPorParametroTemporal({ data: [] });
        setListaParametros({ data: [] });

        // Se inicializa el array
        const data = {
            "data": {
                "type": 'mas_actuaciones',
                "attributes": {
                    "id": props.id_mas_actuacion
                }
            }
        }

        // Se inicializa la API
        GenericApi.getByDataGeneric('mas_actuaciones/parametros-plantilla/' + props.id_mas_actuacion, data).then(

            // Se inicializa la variable de respuesta
            datos => {

                validacionSpinner()

                // Se valida que no haya error
                if (!datos.error) {

                    // Se setean los valores a la lista general de parametros
                    setListaParametros(datos);

                    // Se recorre los parametros generales de la plantilla
                    var parametrosCampos = datos.params.map((item, i) => {

                        // Se busca el parametro con el de la base de datos
                        var indexCampo = listaParametrosBaseDatos.findIndex(

                            // Se inicializa la variable
                            x =>

                                // Se valida que el parametro de la base de datos sea igual al parametro de la plantilla
                                x.title == item
                        );

                        // Se valida que haya encontrado un valor para asignarle el id
                        if (indexCampo >= 0) {

                            // Se retorna el array
                            return { parametro: item, id_campo: indexCampo, valor: "" };
                        } else {

                            // Se retorna el array
                            return { parametro: item, id_campo: "", valor: "" };
                        }
                    });

                    // Parametros de los campos e items
                    var parametrosItems = datos.campos && datos.campos.map((item, i) => {

                        // Se captura el tipo de campo
                        let tipoCampo = item.tipoCampo;

                        // Se valida cuando es tipo lista
                        if (tipoCampo == 2) {

                            // Se setea la lista
                            return { item: true, parametro: item.nombreCampo, id_campo: item.nombreCampo + i, valor: item.items };
                        } else {

                            // Se setea la lista
                            return { item: true, parametro: item.nombreCampo, id_campo: item.nombreCampo + i, valor: "" };
                        }
                    });

                    // Se setean los valores de las listas
                    setListaCamposPorParametroTemporal({ data: parametrosCampos });
                    setListaCamposPorCamposTemporal({ data: parametrosItems });

                    // Se cargan los antecedentes, interesados y entidades del investigado
                    cargarEntidadesInvestigado();
                    cargarAntecedentes();
                    cargarInteresados();
                }
            }
        )

        // Se inicializa la API
        GenericApi.getGeneric('mas_actuaciones/' + props.id_mas_actuacion).then(

            // Se inicializa la variable de respuesta
            datos => {
                
                validacionSpinner()

                // Se valida que no haya error
                if (!datos.error) {

                    // Se valida que hayan campos adicionales
                    if (datos.data.attributes.campos.length > 0) {

                        // Se setean los valores a la lista
                        setCamposAdicionales(datos.data.attributes.campos);

                        // Se setean los campos en el from
                        from.campos = datos.data.attributes.campos;
                    } else if (from.campos) {

                        // Se setean los campos en el from
                        delete from.campos;
                    }
                }
            }
        )

        // Se quita el cargando
        window.showSpinner(false);
    }

    const cargarAntecedentes = () => {
        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": "antecedentes",
                    "descripcion": "descripcion",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": -1,
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }
        GenericApi.getByDataGeneric('antecedentes/get-antecedentes/' + procesoDisciplinarioId, data).then(
            datos => {

                validacionSpinner()

                if (!datos.error) {

                    var parametrosCampos = datos.data.map((item) => {
                        item.checked = false;
                        return item;
                    });

                    setAntecedentesLista(parametrosCampos);
                }
            }
        )
    }

    const cargarInteresados = () => {
        const data = {
            "data": {
                "type": "interesado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "tipo_documento": "1",
                    "numero_documento": "1",
                    "primer_nombre": "1",
                    "segundo_nombre": "1",
                    "primer_apellido": "1",
                    "segundo_apellido": "1",
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }

        GenericApi.getByDataGeneric('datos-interesado/datos-interesado/' + procesoDisciplinarioId, data).then(
            datos => {

                validacionSpinner()

                if (!datos.error) {
                    var parametrosCampos = datos.data.map((item) => {
                        item.checked = false;
                        return item;
                    });
                    setInteresadosLista(parametrosCampos);
                } else {
                    setModalState({ title: "Datos del interesado", message: datos.error.toString(), show: true, redirect: null, from: { from } });
                }
            }
        )
    }

    const cargarEntidadesInvestigado = () => {
        const data = {
            "data": {
                "type": "entidad_investigado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": "1",
                    "requiere_registro": true,
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }

        GenericApi.getByDataGeneric("entidad-investigado/get-entidad-investigado/" + procesoDisciplinarioId, data).then(
            datos => {

                validacionSpinner()

                if (!datos.error) {
                    var parametrosCampos = datos.data.map((item) => {

                        var nombreInvestigado = item.attributes.nombre_investigado;
                        var cargoInvestigado = item.attributes.cargo;
                        var entidadInvestigado = item.attributes.nombre_entidad;

                        if (nombreInvestigado == null) {
                            item.attributes.nombre_investigado = "NO_APLICA";
                        }

                        if (cargoInvestigado == null) {
                            item.attributes.cargo = "NO_APLICA";
                        }

                        if (entidadInvestigado == null) {
                            item.attributes.nombre_entidad = "NO_APLICA";
                        }

                        item.checked = false;
                        return item;
                    });
                    setEntidadesInvestigadoLista(parametrosCampos);
                    setCargosInvestigadoLista(parametrosCampos);
                    setEntidadInvestigadoLista(parametrosCampos);
                }
                else {
                    setModalState({ title: "Entidades del investigado", message: datos.error.toString(), show: true, redirect: null, from: { from } });
                }

            }
        )
    }

    const descargarArchivo = () => {

        try {
            window.showSpinner(true);

            var params = getListaCamposPorParametroTemporal.data.map(item => {
                return {
                    "param": item.parametro,
                    "value": item.valor
                };
            });

            const data = {
                "data": {
                    "type": "mas_actuaciones",
                    "attributes": {
                        "params": params,
                    }
                }
            }
            GenericApi.getByDataGeneric('mas_actuaciones/plantilla-diligenciada/' + props.id_mas_actuacion, data).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, datos.file_name, global.Constants.TIPO_DOCUMENTO_PERMITIDO_ACTUACIONES.DOCX);
                        window.showSpinner(false);
                    }
                    else {
                        setModalState({ title: "SINPROC No " + radicado + " :: ", message: datos.error.toString(), show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName, extension) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const cargarCheckParametro = (parametro) => {

        return (
            <div className="col-md-12" key={'keyCheck' + parametro + '1'} >
                <input type="checkbox"
                    className="form-check-input"
                    id={'check' + parametro + '1'}
                    name={'check' + parametro + '1'}
                    value={parametro + '1'}
                    onChange={(e) => handleChangeStatus(e, parametro)}
                />
            </div >
        )
    }

    const handleChangeStatus = (e, parametro) => {

        // Se captura el elemento enviado por parametros
        var itemFilter = getListaCamposPorParametroTemporal.data.filter(x => x.parametro == parametro);

        // Se valida si esta checkeado
        if (e.target.checked) {

            // Se devuelve el valor a vacio
            var newParams = getListaCamposPorParametroTemporal.data.map(item => {

                // Se valida que el item recorrido sea igual al parametro checkeado
                if (item.parametro == parametro) {

                    // Se captura el id del campo
                    item.id_campo = itemFilter[0].id_campo;
                    var itemCampo = getListaParametrosBaseDatos[itemFilter[0].id_campo];

                    // Se redefine el valor del item a vacio
                    item.valor = "";
                    item.valorTemporal = (itemCampo.value != null) ? itemCampo.value : itemFilter[0].value;
                }

                // Se retorna el item
                return item;
            });


        } else {

            // Se devuelve el valor a vacio
            var newParams = getListaCamposPorParametroTemporal.data.map(item => {

                // Se valida que el item recorrido sea igual al parametro checkeado
                if (item.parametro == parametro) {

                    // Se captura el id del campo
                    item.id_campo = itemFilter[0].id_campo;
                    var itemCampo = getListaParametrosBaseDatos[itemFilter[0].id_campo];

                    // Se redefine el valor del item a su valor original
                    item.valor = item.valorTemporal;
                    item.valorTemporal = (itemCampo.value != null) ? itemCampo.value : itemFilter[0].value;
                }

                // Se retorna el item
                return item;
            });
        }
    }

    const validacionSpinner = () => {
        numeroLlamados++
        if(numeroLlamados >= numeroTotalLlamados){
            setIsLoading(false)
        }
    }

    return (
        <>
            <Spinner />
            <ModalGen data={modalState} />
            {
                getIsLoading
                ?
                    <div className="text-center">
                        <div className="spinner-border text-primary" role="status">
                            <span className="sr-only">CARGANDO...</span>
                        </div>
                    </div>
                :
                    <>
                        {
                            getListaParametros != null 
                            ?
                                <>
                                    <div className='col-md-12'>
                                        <label>PARÁMETROS DE LA PLANTILLA</label>
                                    </div>
                                    <div className='col-md-12 mt-2 mb-2'>
                                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                            columns={columnsParametros}
                                            data={getListaParametros.params}
                                            noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                            striped
                                        />
                                    </div>
                                    {getCamposAdicionales && getCamposAdicionales.length > 0 ?
                                        <>
                                            <div className='col-md-12' style={{ marginTop: '50px' }}>
                                                <label>CAMPOS ADICIONALES</label>
                                            </div>
                                            <div className='col-md-12 mt-2 mb-2'>
                                                <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                    columns={columnsCampos}
                                                    data={getCamposAdicionales}
                                                    noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                                    striped
                                                />
                                            </div>
                                        </>
                                        : null
                                    }
                                    <div className='col-md-12 mt-4 mb-2'>
                                        <label className='mr-2' htmlFor="documento_ruta">DESCARGAR DOCUMENTO PRE-DILIGENCIADO CON LOS VALORES SELECCIONADOS</label>
                                        <button type="button" title='Descargar documento diligenciado' onClick={() => descargarArchivo()} className="btn btn-rounded btn-primary"> <i className="fas fa-file-word"></i></button>
                                    </div>
                                </>
                            :
                            <div className='col-md-12 mt-4'>
                                <label>CARGANDO PARÁMETROS DE LA PLANTILLA...</label>
                            </div>
                        }
                    </>
            }
        </>
    );
}

export default ActuacionParametrosPlantillaForm;
