import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";

function AntecedentesLista() {
    
    const [respuestaAntecedentes, setRespuestaAntecedentes] = useState(false);
    const [antecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');

    const [isRedirect, setIsRedirect] = useState(false);
    const [listaDependenciaOrigen, setListaDependenciaOrigen] = useState({ data:{} });
    const [respuestaDependenciaOrigen, setDependenciaOrigen] = useState(false);


    const redirectToRoutes = () => {
        return <Navigate to={`/AntecedentesLista/${procesoDisciplinarioId}`} />;
    }

    
    let { radicado, procesoDisciplinarioId } = useParams();

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "antecedentes": 2,
                    }
                }
            }

            /*AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setAntecedentesLista(datos)
                        setRespuestaAntecedentes(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )*/

            ParametrosMasApi.getAllTiposDependenciaOrigen().then(
                datos => !datos.error ? (setListaDependenciaOrigen(datos), setDependenciaOrigen(true) ) : window.showModal(1)
            )    
        }
        fetchData();
    }, []);
    

    const selectDependenciaOrigen = () => {
        return (
            listaDependenciaOrigen.data.map((dependencia, i) => {
                return (
                    <option key={dependencia.id} value={dependencia.id}>{dependencia.attributes.nombre}</option>
                )
            })
        )
    }

    const listaAntecedentes = () => {

        return(

            antecedentesLista.data.map((antecedente, i) => {
                return (
                    <tr key={antecedente.id}>
                        <td className="text-center">{i+1}</td>
                        <td>{ antecedente.attributes.descripcion }</td>
                        <td>{ antecedente.attributes.dependencia_origen }</td>
                        <td>{ antecedente.attributes.estado ? 'Activo' : 'Inactivo' }</td>
                        <td>PENDIENTE</td>
                        <td>{ antecedente.attributes.etapa }</td>
                        <td>{ antecedente.attributes.fecha_registro}</td>
                        <td>PENDIENTE</td>
                    </tr>
                )
            })
        )
    }


    const enviarDatos = (datos) => {
        window.showSpinner(true);
        setAntecedentesLista({ data: [], links: [], meta: [] });
        setRespuestaAntecedentes(false);

        const data = {
            "data" : {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 1,
                    "descripcion": datos.descripcion,
                    "fecha_registro": datos.fechaRegistro,
                    "id_dependencia": datos.dependenciaOrigenDesglose,
                    "created_user": datos.registradoPor,
                }
            }
        }

        console.log(data);

        AntecedentesApi.getAllAntecedentesByFilter(data, procesoDisciplinarioId).then(
            datos => {
                
                if(!datos.error){
                    setAntecedentesLista(datos)
                    setRespuestaAntecedentes(true)
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
          
                }
                window.showSpinner(false);
            }
        )

    }



    return (
        <>
                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
    
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
									<li class="breadcrumb-item"> <small>Antecedentes</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                { isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }
                <Formik
                    initialValues={{
                        Id: '',
                        registradoPor: '',
                        fechaRegistro: '',
                        dependenciaOrigenDesglose: '',
                        descripcion: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {}
       
                        if((!valores.Id || typeof(valores.Id) == 'undefined')
                        && (!valores.registradoPor || typeof(valores.registradoPor) == 'undefined')
                        && (!valores.fechaRegistro || typeof(valores.fechaRegistro) == 'undefined')
                        && (!valores.dependenciaOrigenDesglose || typeof(valores.dependenciaOrigenDesglose) == 'undefined')
                        && (!valores.descripcion || typeof(valores.descripcion) == 'undefined')){
                            errores.descripcion = 'Debe ingresar al menos un campo'
                        }


                        return errores
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        enviarDatos(valores);
                    }}
                >

                {({ errors }) => (
                    <Form>
            
                           
            <div className="block-header block-header-default">
                    <h3 className="block-title">Filtro de búsqueda</h3>
                    </div> 
                    <div className="block block-rounded block-bordered">
              
                        <div className="block-content block-content-full">
                            <div className="row">
                                
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="registradoPor">Registrado por </label>
                                        <Field as="select" className="form-control" id="registradoPor" name="registradoPor">
                                            <option value="">Por favor seleccione</option>
                                            { respuestaDependenciaOrigen ? selectDependenciaOrigen() : null }
                                        </Field>
                                        <ErrorMessage name="dependenciaOrigenDesglose" component={() => (<span className="text-danger">{errors.registradoPor}</span>)} />
                                    </div>

                                </div>
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="dependenciaOrigenDesglose">Dependencia origen </label>
                                        <Field as="select" className="form-control" id="dependenciaOrigenDesglose" name="dependenciaOrigenDesglose">
                                            <option value="">Por favor seleccione</option>
                                            { respuestaDependenciaOrigen ? selectDependenciaOrigen() : null }
                                        </Field>
                                        <ErrorMessage name="dependenciaOrigenDesglose" component={() => (<span className="text-danger">{errors.dependenciaOrigenDesglose}</span>)} />
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor='fechaRegistro'>Fecha Registro </label>
                                        <Field type="date" id="fechaRegistro" name="fechaRegistro" className="form-control" />
                                        <ErrorMessage name="fechaRegistro" component={() => (<span className="text-danger">{errors.fechaRegistro}</span>)} />
                                    </div>  
                                </div>
                               
                                <div className="col">
                                    <div className="form-group">
                                        <label>Descripción</label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" 
                                                placeholder="Escriba en este espacio la descripción"></Field>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                               
                              
                            </div>
                            
                        </div>
                        <div className="block-content block-content-full text-right bg-light">
                            <button type="submit" className="btn btn-sm btn-primary" >Buscar</button>           
                        </div>
                
                    </div>
                    </Form>
                )}
            </Formik> 






            <div className="block-header block-header-default">
            <h3 className="block-title">Lista de antecedentes</h3>

            <Link to={`/AntencentesForm/${radicado}/${procesoDisciplinarioId}`}>
                <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                    <i className="fa fa-fw fa-archive mr-1"></i> Agregar Antecedente
                </button>
            </Link>
                
            </div> 
        
            <div className="block block-rounded block-bordered">
              
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">No</th>
                                        <th>Descripción</th>
                                        <th>Dependencia</th>
                                        <th>Estado</th>
                                        <th>Registrado por</th>
                                        <th>Etapa</th>
                                        <th>Fecha registro</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    { respuestaAntecedentes ? listaAntecedentes() : null }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>                 
    );
}


export default AntecedentesLista;
