import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom'
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';


function DatosInteresadoCambiarEstadoForm() {


    const [countTextArea, setCountTextArea] = useState(0);
    const [nombreUsuario, setNombreUsuario] = useState("");

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    let { idDatosInteresado } = useParams();

    const [getDatosInteresado, setDatosInteresado] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoDatos, setRtaInfoDatos] = useState(false);
    const [getEstado, setEstado] = useState("activa");
    const [getNuevoEstado, setNuevoEstado] = useState(true);
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });

    useEffect(() => {
        async function fetchData() {

            setNombreUsuario(getUser().nombre);
            GenericApi.getGeneric("datos-interesado/datos-interesado-id/"+idDatosInteresado).then(
                datos => {

                    if (!datos.error) {

                        if (datos.data[0].attributes.estado == true) {
                            setEstado("inactiva");
                            setNuevoEstado(false);
                        }

                        setDatosInteresado(datos);
                        setRtaInfoDatos(true);
                    }
                    else {
                        setModalState({ title: "SINPROC No "+radicado+" :: Datos del interesado", message: datos.error.toString(), show: true, redirect: '/DatosInteresadoLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }

                }
            )
            obtenerParametros();
        }
        fetchData();
    }, []);

    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
          if (str.includes(specialChar)) {
            return true;
          }
      
          return false;
        });
      
        return result;
      }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro

            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "SINPROC No "+radicado+" :: Datos del interesado", message: datos.error.toString(), show: true, redirect: '/DatosInteresadoLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const enviarDatos = (datos) => {

        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'interesado',
                "attributes": {
                    "estado_observacion": datos.descripcion,
                    "estado": getNuevoEstado,
                    "id_proceso_disciplinario": procesoDisciplinarioId
                }
            }
        }

        GenericApi.updateGeneric("datos-interesado",idDatosInteresado,data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Datos del interesado", message: 'Registro Exitoso', show: true, redirect: '/DatosInteresadoLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO });
                    window.showSpinner(false);
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Datos del interesado", message: datos.error.toString(), show: true, redirect: '/DatosInteresadoLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showSpinner(false);
                }
                
            }
        )
    }

    const CargarInfoDocumentoRadicado = () => {

        if (getDatosInteresado.data != null && typeof (getDatosInteresado.data) != 'undefined') {
            return (

                <tr>
                    <td style={{ width: "15%" }}>
                        {getDatosInteresado.data[0].attributes.nombre_completo}
                    </td>
                    <td style={{ width: "15%" }}>{getDatosInteresado.data[0].attributes.created_at}</td>
                    <td style={{ width: "50%" }}>
                        {(getDatosInteresado.data[0].attributes.primer_apellido)?(getDatosInteresado.data[0].attributes.primer_apellido + ' ' +
                            (getDatosInteresado.data[0].attributes.segundo_apellido!=null?getDatosInteresado.data[0].attributes.segundo_apellido:"")+ ' ' +
                            getDatosInteresado.data[0].attributes.primer_nombre + ' ' +
                            (getDatosInteresado.data[0].attributes.segundo_nombre!=null?getDatosInteresado.data[0].attributes.segundo_nombre:"")):""}
                    </td>
                    <td>{getDatosInteresado.data[0].attributes.entidad?getDatosInteresado.data[0].attributes.entidad:(getDatosInteresado.data[0].attributes.nombre_entidad?getDatosInteresado.data[0].attributes.nombre_entidad:"SIN ENTIDAD2")}</td>

                   
                </tr>

            )
        }

    }


    return (
        <>
            {<Spinner/>}
            {<ModalGen data={getModalState} />}

            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)

                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar una  descripción'
                    }
                    else if (valores.descripcion.length < getMinimoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMinimoTextArea + ' caracteres'
                    }
                    else if (valores.descripcion.length > getMaximoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMaximoTextArea + ' caracteres'
                    }


                    if (valores.descripcion) {
                        if(containsSpecialChars(valores.descripcion))
                        errores.descripcion = 'Tiene caracteres inválidos'
                    }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/DatosInteresadoLista/`} state={{ from: from }}><small>Datos del interesado</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Cambiar estado de datos del interesado</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Inactivar datos del interesado</strong></h3>
                            </div>
                            <div className="block-content">


                                <div className='col-md-12'>
                                    <div className="table-responsive-md">
                                         <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th>Registrado por</th>
                                                    <th>Fecha registro</th>
                                                    <th>Persona</th>
                                                    <th>Entidad</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {getRtaInfoDatos ? CargarInfoDocumentoRadicado() : null}

                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Motivo por el que {getEstado} el registro del interesado <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Escriba en este espacio la observación"
                                            maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/DatosInteresadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary"> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default DatosInteresadoCambiarEstadoForm;
