import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";
import { hasAccess } from '../../../components/Utils/Common';
import ClasificacionRadicado from '../ClasificacionRadicado/ClasificacionRadicado';
import GenericApi from '../../Api/Services/GenericApi';

import InfoExitoApiTextsCustom from '../../Utils/InfoExitoApiTextsCustom';


function ValidarClasificacionRadicadoForm() {

    const [errorApi, setErrorApi] = useState('');
   
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let id_etapa = from.id_etapa;


    const [listaTipoRespuesta, setListaTipoRespuesta] = useState({ data: {} });
    const [selectedTipoRespuesta, setSelectedTipoRespuesta] = useState("");
    const [respuestaTipoRespuesta, setRespuestaTipoRespuesta] = useState(false);
    const [getUltimaReclasificacion, setUltimaReclasificacion] = useState('#000000');


    const [getJefe, setJefe] = useState({ data: {} });
    const [getRtaJefe, setRtaJefe] = useState(false);

    const [isRedirect, setIsRedirect] = useState(false);
    const [getExitoApiTextsCustom, setExitoApiTextsCustom] = useState({ titulo: null, cuerpo: null, boton: null });


    const [getParametros, setParametros] = useState({
        id_proceso_disciplinario: procesoDisciplinarioId,
        id_etapa: 2, // from.idEtapa
        reclasificacion: true,
        route: "/MisPendientes/",
        tipo_clasificacion: global.Constants.TIPO_CLASIFICACION.VALIDAR_CLASIFICACION,
    });

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/`} />;
    }

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "id_etapa",
                        "id_tipo_expediente": "id_tipo_expediente",
                        "observaciones": "observaciones",
                        "id_tipo_queja": "id_tipo_queja",
                        "id_termino_respuesta": "id_termino_respuesta",
                        "fecha_termino": "fecha_termino",
                        "hora_termino": "hora_termino",
                        "gestion_juridica": "gestion_juridica",
                        "estado": true,
                        "id_estado_reparto": "id_estado_reparto",
                    }
                }
            }

            GenericApi.getByDataGeneric("log-proceso-disciplinario/get-reclasificacion-expediente/"+procesoDisciplinarioId, data).then(

            //ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setClasificacionRadicadoLista(datos)
                        setRespuestaClasificacionRadicado(true)
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )


            ParametrosMasApi.getAllTipoRespuesta().then(
                datos => !datos.error ? (setListaTipoRespuesta(datos), setRespuestaTipoRespuesta(true)) : window.showModal(1)
            )

            const data2 = {
                "data":{
                   "type":"usuario",
                   "attributes":{
                      "":""
                   }
                }
             }

            GenericApi.getByDataGeneric("usuario/get-jefe-dependencia", data2).then(

                datos => {
                    if (!datos.error) {
                        console.log("CON JEFE");
                        console.log(datos.data[0]['attributes']['name']);
                        setJefe(datos.data[0]['attributes']['name'])
                        setRtaJefe(true)
                    }
                    else {
                        console.log("SIN JEFE");
                        setJefe(null);
                        console.log(getJefe);
                        setRtaJefe(false);
                    }
                    window.showSpinner(false);
                }
            )



        }
        fetchData();
    }, []);


    let selectChangeTipoRespuesta = (e) => {
        setSelectedTipoRespuesta(e);
    }

    const selectTipoRespuesta = () => {
        return (
            listaTipoRespuesta.data.map((tipo_respuesta, i) => {
                return (
                    <option key={tipo_respuesta.id} value={tipo_respuesta.id}>{tipo_respuesta.attributes.nombre}</option>
                )
            })
        )
    }

    const enviarDatos = () => {
        window.showSpinner(true);


        let data;

        data = {
            "data": {
                "type": "clasificacion_radicado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": 2,
                    "id_tipo_expediente": "datos.selectedTipoExpediente",
                    "observaciones": "observaciones",
                    "id_tipo_queja": "id_tipo_queja",
                    "id_termino_respuesta": "id_termino_respuesta",
                    "fecha_termino": "fecha_termino",
                    "hora_termino": "hora_termino",
                    "gestion_juridica": "gestion_juridica",
                    "estado": "estado",
                    "id_estado_reparto": "id_estado_reparto",
                    "id_derecho_peticion": "id_derecho_peticion",
                    "oficina_control_interno": "oficina_control_interno",
                    "created_user": "created_user",
                    "reclasificacion": false,
                }
            }
        }

        console.log(JSON.stringify(data));

        ClasificacionRadicadoApi.addClasificacionRadicado(data).then(
            datos => {
                if (!datos.error) {
                    window.showModal(2);
                    //setIsRedirect(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )

    }


     // COMPONENTE CONFIRMACION TIPO DE CLASIFICADO
     const componenteTipoRespuesta = (tipo_respuesta) => {

        // CONFIRMACION TIPO DE CLASIFICADO = SI
        if (tipo_respuesta === 1) {
            return (
                <>
                    <Formik

                        initialValues={{
                            aceptacion_clasificacion: true,
                        }}
                        enableReinitialize

                        onSubmit={({ resetForm }) => {
                            enviarDatos();
                        }}
                        >

                        <Form>
                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/ValidarClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i className="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </Form>

                    </Formik>

                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === 2) {
            return (
                <>
                    <ClasificacionRadicado getParametros={getParametros} id="cr_componente" name="cr_componente" />
                   
                </>
            );
        }
    };


    const [respuestaClasificacionRadicado, setRespuestaClasificacionRadicado] = useState(false);
    const [clasificacionRadicadoLista, setClasificacionRadicadoLista] = useState({ data: [], links: [], meta: [] });

    const listaClasificacionRadicado = () => {

        if (clasificacionRadicadoLista.data != null && typeof (clasificacionRadicadoLista.data) != 'undefined') {
            return (


                clasificacionRadicadoLista.data.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado, i)
                    )
                })
            )
        }
        else {
            return (

                clasificacionRadicadoLista.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado, i)
                    )
                })
            )
        }

    }

    const cargarTabla = (clasificacion_radicado, i) => {
        return (
            <>
                {(i === 0 ? <tr style={{ backgroundColor: '#D0D1D9' }}>{cargarColumnasTabla(clasificacion_radicado, "Asignar")}</tr> : null) }
                {(i === 1 ? <tr>{cargarColumnasTabla(clasificacion_radicado, "Asignar", false)}</tr>:null)}
                {(i === 2 ? <tr>{cargarColumnasTabla(clasificacion_radicado, "Reparto Aleatorio", true)}</tr>:null)}    
            </>   
        )
    }


    const cargarColumnasTabla = (clasificacion_radicado, nombre_boton, reparto) => {

        return (
        <>
            <td>{clasificacion_radicado.attributes.etapa.nombre}</td>
            <td>{clasificacion_radicado.attributes.id_tipo_expediente.nombre}<br/> 
            </td>
            <td>{clasificacion_radicado.attributes.dependencia_origen.nombre}</td>
            <td>{clasificacion_radicado.attributes.funcionario_registra.nombre} {clasificacion_radicado.attributes.funcionario_registra.apellido}</td>
            <td>{clasificacion_radicado.attributes.created_at}</td>
            <td>
                <Formik
                    initialValues={{
                        id_clasificacion_radicado: clasificacion_radicado.id,
                        id_proceso_disciplinario: clasificacion_radicado.attributes.id_proceso_disciplinario,
                        id_tipo_expediente: clasificacion_radicado.attributes.id_tipo_expediente.id,
                        id_tipo_sub_expediente: clasificacion_radicado.attributes.id_tipo_sub_expediente.id,
                        funcionario_registra_id: clasificacion_radicado.attributes.funcionario_registra.id,
                        funcionario_registra_name: clasificacion_radicado.attributes.funcionario_registra.name

                    }}
                    enableReinitialize

                    validate={(valores) => {
                        let errores = {}
                        return errores
                    }}

                    onSubmit={(valores, { resetForm }) => {
                        console.log(valores);
                        asignarCaso(valores, reparto);
                    }}
                >
                    <Form>
                        <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> {nombre_boton}</button>
                    </Form>
                </Formik>
            </td>
        </>
        
        );

    }



    

    const asignarCaso = (datos, reparto) => {

        window.showSpinner(true);
        let data;

        data = {
            "data": {
                "type": "clasificacion_radicado",
                "attributes": {
                    "id_proceso_disciplinario": datos.id_proceso_disciplinario,
                    "id_etapa": 2,
                    "id_tipo_expediente": datos.id_tipo_expediente,
                    "observaciones": "",
                    "id_tipo_queja": null,
                    "id_termino_respuesta": null,
                    "fecha_termino": null,
                    "hora_termino": null,
                    "gestion_juridica": false,
                    "estado": true,
                    "id_estado_reparto": null,
                    "id_tipo_derecho_peticion": null,
                    "oficina_control_interno": false,
                    "created_user": datos.funcionario_registra_name,
                    "reclasificacion": null,
                    "id_tipo_sub_expediente": datos.id_tipo_sub_expediente,
                    "reparto": reparto
                }
            }
        }

        console.log(JSON.stringify(data));

        GenericApi.addGeneric("clasificacion-radicado/asignar-caso-por-jefe", data).then(

            datos => {
                if (!datos.error) {

                    setExitoApiTextsCustom(
                        {
                            titulo: 'Asignación del proceso disciplinario',
                            cuerpo: 'El proceso disciplinario es asignado ',
                            boton: 'Aceptar',
                            destino: 'MisPendientes',
                        }
                    );

                    setIsRedirect(true);
                    window.showModal(7);

                }
                else {
                    console.log(datos.error.toString());
                    setErrorApi(datos.error.toString())
                    window.showModal(1)

                }
                window.showSpinner(false);
            }
        )
    }



    return (
        
        <>
           {<Spinner />}
            {isRedirect ? redirectToRoutes() : null}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            {<InfoExitoApiTextsCustom success={getExitoApiTextsCustom} />}


            <div className="row">

            <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ClasificacionRadicadoLista/`} state={{ from: from }}><small>Validar Clasificación</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title"> SINPROC No {radicado} :: <strong>Validar Clasificación</strong></h3>
                        </div>

                        <div className="block-content">
                            <>
                                {
                                    (hasAccess('EI_ValidarClasificacion', 'Consultar')) ? (

                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th className="text-center">No.</th>
                                                    <th>Etapa</th>
                                                    <th>Tipo de expediente</th>
                                                    <th>Registrado En</th>
                                                    <th>Registrado por</th>
                                                    <th>Fecha de registro</th>
                                                    {getRtaJefe ? (
                                                        <th>Asignación</th>
                                                    ):null}
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {respuestaClasificacionRadicado ? listaClasificacionRadicado() : null}
                                            </tbody>
                                        </table>
                                    ) : null

                                }
                            </>


                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="ingresoTipoExpediente">Confirma la clasificación del expediente? <span className="text-danger">*</span></label>
                                            <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                                value={selectedTipoRespuesta} onChange={e => selectChangeTipoRespuesta(e.target.value)}>
                                                <option value="">Por favor seleccione</option>
                                                {respuestaTipoRespuesta ? selectTipoRespuesta() : null}
                                            </select>
                                        </div>
                                    </div>
                                </div>

                                {/*SI APLICA RECLASIFIFCACION*/}
                                {selectedTipoRespuesta === '1' ? componenteTipoRespuesta(1) : ''}
                                {/*NO APLICA RECLASIFIFCACION*/}
                                {selectedTipoRespuesta === '2' ? componenteTipoRespuesta(2) : ''}


                            </div>

                        </div>

                        

                    </div>
                </div>
            </div>

        </>
    );

}
export default ValidarClasificacionRadicadoForm;
