import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
import Spinner from '../Utils/Spinner';
import EntidadInvestigadoApi from '../Api/Services/EntidadInvestigadoApi';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
import DocumentoSiriusApi from '../Api/Services/DocumentoSiriusApi';
import { Navigate } from "react-router-dom";
import moment from 'moment';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../Models/ParametroModel';
import ProcesoDiciplinarioApi from '../Api/Services/ProcesoDiciplinarioApi';

function RamasProceso() {
    const location = useLocation()
    const { from } = location.state

    const [getRespuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });

    const [errorApi, setErrorApi] = useState('');
    
    // Color Fases
    const [getColorAntecedente, setColorAntecedente] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorDatosInteresado, setColorDatosInteresado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorClasificacionRadicado, setColorClasificacionRadicado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorEntidadInvestigado, setColorEntidadInvestigado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorSoporteRadicado, setColorSoporteRadicado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorValidarClasificacion, setColorValidarClasificacion] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorComunicacionInteresado, setColorComunicacionInteresado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");

    // Color Etapas
    const [getColorCapturaReparto, setColorCapturaReparto] = useState("#328DBE");
    const [getEvaluacionReparto, setEvaluacionReparto] = useState("#328DBE");

    const [getEtapaEvaluacionRepartoCompleta, setEtapaEvaluacionRepartoCompleta] = useState(false);
    const [getEtapaEvaluacionCompleta, setEtapaEvaluacionCompleta] = useState(false);

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;



    const [isRedirect, setIsRedirect] = useState(false);

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }} />;
    }

    useEffect(() => {
        async function fetchData() {
            //console.log(radicado + "-" + procesoDisciplinarioId + "-" + vigencia);
            window.showSpinner(true);


            const proceso_disciplinario = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                    }
                }
            }

            ProcesoDiciplinarioApi.getConsultarFasesProcesoDisciplinario(proceso_disciplinario, procesoDisciplinarioId ).then(
                datos => {

                    if (!datos.error) {

                        if(datos["data"]["attributes"]["etapa"]==1){
                            setColorCapturaReparto("#82b54b");

                            // Etapa: Captura y reparto Fase: Antecendentes
                            if(datos["data"]["attributes"]["antecedentes"]===true){
                                setColorAntecedente("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionRepartoCompleta(true);
                            }else{
                                setEtapaEvaluacionRepartoCompleta(false);
                            }

                            // Etapa: Captura y reparto Fase: Datos del interesado
                            if(datos["data"]["attributes"]["datos_interesado"]===true){
                                setColorDatosInteresado("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionRepartoCompleta(true);
                            }else{
                                setEtapaEvaluacionRepartoCompleta(false);
                            }

                            // Etapa: Captura y reparto Fase: Clasificación del radicado
                            if(datos["data"]["attributes"]["clasificacion_radicado"]===true){
                                setColorClasificacionRadicado("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionRepartoCompleta(true);
                            }else{
                                setEtapaEvaluacionRepartoCompleta(false);
                            }

                            // Etapa: Captura y reparto Fase: Entidad del investigado
                            if(datos["data"]["attributes"]["entidad_investigado"]===true){
                                setColorEntidadInvestigado("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionRepartoCompleta(true);
                            }else{
                                setEtapaEvaluacionRepartoCompleta(false);
                            }

                            // Etapa: Captura y reparto Fase: Soporte radicado
                            if(datos["data"]["attributes"]["soporte_radicado"]===true){
                                setColorSoporteRadicado("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionRepartoCompleta(true);
                            }else{
                                setEtapaEvaluacionRepartoCompleta(false);
                            }

                        }

                        if(datos["data"]["attributes"]["etapa"]==2){
                            // Etapa: Evaluación Fase: validar clasificación
                            if(datos["data"]["attributes"]["validar_clasificacion"]===true){
                                setColorValidarClasificacion("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionCompleta(true);
                            }else{
                                setEtapaEvaluacionCompleta(false);
                            }

                            // Etapa: Evaluación Fase: comunicacion interesado
                            if(datos["data"]["attributes"]["validar_clasificacion"]===true){
                                setColorComunicacionInteresado("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");
                                setEtapaEvaluacionCompleta(true);
                            }else{
                                setEtapaEvaluacionCompleta(false);
                            }

                        }

                        setRespuestaAntecedente(true)
                    }
                    else{
                        window.showModal()
                    }

                }

            )

            const dataAntecedente = {
                "data": {
                    "type": "antecedente",
                    "attributes": {
                        "descripcion": "descripcion",
                        "fecha_registro": "fecha_registro",
                        "id_dependencia": "id_dependencia",
                        "estado": "estado",
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "dependencia_origen": "dependencia_origen",
                        "vigencia": "vigencia",
                        "fecha_creado": "fecha_creado",
                        "etapa": "etapa"
                    }
                }
            }

            AntecedentesApi.getAllAntecedente(dataAntecedente, procesoDisciplinarioId).then(
                datos => {
                   
                    console.log("DATOS: "+datos);

                    if (!datos.error) {
                        if (datos["data"].length > 0) {

                            
                            setAntecedentesLista(datos)
                            setRespuestaAntecedente(true)
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                }
            )
       


        }
        fetchData();
    }, []);








     const cerrarEtapa = (datos) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "id_delegada": 1,
                    "id_funcionario": 1
                }
            }
        }

        CierreEtapaApi.addCierreEtapa(data).then(
            datos => {
                if (!datos.error) {
                    window.showModal(2)
                    window.location.reload(false);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }


    const componentAntecedente = () => {

        var index = getAntecedentesLista.data.length - 1;

        var fecha_ultimo_antecedente = getAntecedentesLista.data.length > 0 ? (getAntecedentesLista.data[0].attributes.fecha_creado) : "";


        return (
            <>
                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) :: {radicado}</h3>
                        <span><strong>Antecente: </strong></span><br />
                        {
                            (getAntecedentesLista.data.length > 0) ? (
                                <>
                                    <span>{getAntecedentesLista.data[0].attributes.descripcion}</span><br />
                                </>
                            ) : <><span>
                            </span><br /></>
                        }




                        <br />
                        <div className="row">
                            <div className="col-lg-6"><span><strong>Registrado Por: </strong>PENDIENTE</span></div>
                            <hr />

                            {
                                (getAntecedentesLista.data.length > 0) ? (
                                    <>
                                        <div className="col-lg-6"><span><strong>Dependencia registro: </strong> {getAntecedentesLista.data[0].attributes.nombre_dependencia}</span></div>
                                    </>
                                ) : <><div className="col-lg-6"><span><strong>Dependencia registro: </strong></span></div></>
                            }


                            <hr />
                        </div>

                        <div className="row">
                            {
                                (getAntecedentesLista.data.length > 0) ? (
                                    <>
                                        <div className="col-lg-4"><span><strong>Vigencia: </strong>{vigencia}</span></div>
                                        <div className="col-lg-4"><span><strong>Fecha de registro: </strong> {fecha_ultimo_antecedente}</span></div>
                                    </>
                                ) : <>

                                    <div className="col-lg-4"><span><strong>Vigencia: {vigencia}</strong></span></div>
                                    <div className="col-lg-4"><span><strong>Fecha de registro: </strong></span></div>

                                </>
                            }

                            <div className="col-lg-4"><span><strong>Fecha de ingreso: </strong> {getAntecedentesLista.data[index].attributes.fecha_creado}</span></div>
                        </div>

                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            <div className="row">

                <div className="col-md-12">
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li className="breadcrumb-item">
                                    <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                            </ol>
                        </nav>
                      
                    </div>
                </div>


                {/*CAPTURA Y REPARTO*/}
                <div class="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">

                        <div className="block-header block-header-default" style={{background: getColorCapturaReparto}}>
                            <h3 className="block-title">Captura y Reparto</h3>
                        </div>

                        <div class="block-content">

                            <ul className="nav-items push">

                                {/*ANTECEDENTES*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/AntecedentesLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorAntecedente}><i className="fas fa-clipboard-list"></i> Antecedentes</button>
                                        </Link>
                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>

                                </li>

                                {/*DATOS DEL INTERESADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/DatosInteresadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorDatosInteresado}><i className="fas fa-users"></i> Datos del interesado</button>
                                        </Link>
                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>
                                </li>

                                {/*CLASIFICACION DEL RADICADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/ClasificacionRadicadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorClasificacionRadicado}><i class="fas fa-sitemap"></i> Clasificación del radicado</button>
                                        </Link>
                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>
                                </li>

                                {/*ENTIDAD DEL INVESTIGADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/EntidadInvestigadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorEntidadInvestigado}><i class="fas fa-hotel"></i> Entidad del investigado</button>
                                        </Link>
                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>

                                </li>

                                {/*SOPORTE RADICADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/SoporteRadicadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorSoporteRadicado}><i class="fas fa-archive"></i> Soporte Radicado</button>
                                        </Link>
                                    </div>
                                </li>

                                {/*CERRAR ETAPA*/}
                                <li>
                                    {getEtapaEvaluacionRepartoCompleta && (

                                        <div className="form-group">
                            
                                            <Formik
                                                initialValues={{
                                                    inputCierreEtapa: '',
                                                        inputEtapa: 1,
                                                    }}
                                                    enableReinitialize
                                                    validate={(valores) => {

                                                        let errores = {}
                                                        if (!valores.inputCierreEtapa) {
                                                            errores.inputCierreEtapa = 'Debe seleccionar la opción cierre de etapa'
                                                        }
                                                        return errores
                                                    }}

                                                    onSubmit={(valores, { resetForm }) => {
                                                        cerrarEtapa(valores);
                                                    }}
                                                >

                                                    <Form>
                                                        <div className="col-md-12">
                                                            <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa" />
                                                            <label className="form-check-label" htmlFor="example-checkbox-inline1">¿Con la información actual se puede dar cierre a esta etapa?</label>
                                                        </div>

                                                        <div className="col-md-12">
                                                            <div className="block-content block-content-full text-center">
                                                                <button type="submit" className="btn btn-danger"> Cerrar Etapa</button>
                                                            </div>
                                                        </div>
                                                    </Form>
                                                </Formik>
                                        </div>
                                     )

                                    }
                                </li>

                            </ul>
                        </div>
                    </div>
                </div>

                {/*EVALUACIÓN*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default" style={{background: getEvaluacionReparto}}>
                            <h3 className="block-title">Evaluación</h3>
                        </div>
                        <div className="block-content">
                            <div className="media-body">

                                <ul className="nav-items push">

                                    <li>
                                        <Link to={`/ValidarClasificacionRadicadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorValidarClasificacion}><i className="fas fa-archive"></i> Validar Clasificación</button>
                                        </Link>
                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </li>

                                    <li>
                                        <Link to={`/ComunicacionInteresadoForm/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            <button type="button" className={getColorComunicacionInteresado}><i className="fas fa-hotel"></i> Comunicación Interesado(s)</button>
                                        </Link>
                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>

                                    </li>

                                </ul>




                            </div>



                        </div>
                    </div>
                </div>

                {/*EVALUACIÓN EN PD*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Evaluación en PD</h3>
                        </div>
                        <div className="block-content">

                        </div>
                    </div>
                </div>

                {/*EVALUACIÓN EN PRELIMINAR*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Preliminar</h3>
                        </div>
                        <div className="block-content">

                        </div>
                    </div>
                </div>

                {/*EVALUACIÓN EN DISCIPLINARIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Disciplinaria</h3>
                        </div>
                        <div className="block-content">

                        </div>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Causa / Juzgamiento</h3>
                        </div>
                        <div className="block-content">

                        </div>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Proceso Verbal</h3>
                        </div>
                        <div className="block-content">

                        </div>
                    </div>
                </div>

                {/*SEGUNDA INSTANCIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Segunda Instancia</h3>
                        </div>
                        <div className="block-content">

                        </div>
                    </div>
                </div>

                <div class="modal" id="modal-archivos" tabIndex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div class="modal-dialog modal-lg" role="document">
                        <div class="modal-content">
                            <div class="block block-themed block-transparent mb-0">
                                <div class="block-header bg-primary-dark">
                                    <h3 class="block-title">Adicionar Documento a (TIPO DE RADICADO): {radicado}</h3>
                                    <div class="block-options">
                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i class="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div class="block-content">


                                    <Formik>

                                        <Form>
                                            <div className="row">
                                                <div className="col-md-12">
                                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                        <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   (PARAMETRIZAR) </p>
                                                        <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                                                        <p className="mb-0">
                                                            NO puede superar QUINCE (15) Mb de peso/tamaño.
                                                            Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                                                        </p>
                                                        <p className="mb-0">
                                                            El nombre del archivo debe ser de máximo 40 caracteres.
                                                        </p>
                                                    </div>
                                                </div>
                                            </div>


                                            <div className="row">

                                                <div className="col-md-4">
                                                    <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                                </div>

                                                <div className="col-md-6">
                                                    <div class="custom-file">
                                                        <input type="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom" />
                                                        <label class="custom-file-label" htmlFor="example-file-input-custom">Selecciona el archivo</label>
                                                    </div>
                                                </div>

                                                <div className="col-md-2">
                                                    <button type="submit" className="btn btn-primary"><i class="fas fa-minus-circle"></i></button>
                                                </div>

                                            </div>


                                            <div className="row" style={{ paddingTop: "10px" }}>

                                                <div className="col-md-4">
                                                    <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                                </div>

                                                <div className="col-md-6">
                                                    <div class="custom-file">
                                                        <input type="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom" />
                                                        <label class="custom-file-label" htmlFor="example-file-input-custom">Selecciona el archivo</label>
                                                    </div>
                                                </div>

                                                <div className="col-md-2">
                                                    <button type="button" className="btn btn-primary"><i class="fas fa-minus-circle"></i></button>
                                                </div>

                                            </div>


                                            <div className="row" style={{ paddingTop: "10px" }}>

                                                <div className="col-md-4">
                                                    <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                                </div>

                                                <div className="col-md-6">
                                                    <div class="custom-file">
                                                        <input type="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom" />
                                                        <label class="custom-file-label" htmlFor="example-file-input-custom">Selecciona el archivo</label>
                                                    </div>
                                                </div>

                                                <div className="col-md-2">
                                                    <button type="button" className="btn btn-primary"><i class="fas fa-minus-circle"></i></button>
                                                </div>

                                            </div>

                                            <div className="col-md-12">
                                                <div className="block-content block-content-full text-right">
                                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                                </div>
                                            </div>



                                        </Form>

                                    </Formik>


                                </div>
                            </div>
                        </div>
                    </div>



                </div>


            </div>

        </>
    );
}


export default RamasProceso;
