import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import EntidadInvestigadoApi from "../../Api/Services/EntidadInvestigadoApi";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';

function EntidadInvestigadoLista() {

    const [respuestaEntidadInvestigado, setRespuestaEntidadInvestigado] = useState(false);
    const [entidadInvestigadoLista, setEntidadInvestigadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    let { radicado, procesoDisciplinarioId } = useParams();

    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/${procesoDisciplinarioId}`} />;
    }
    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "requiere_registro": true
                    }
                }
            }

            /*EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setEntidadInvestigadoLista(datos)
                        setRespuestaEntidadInvestigado(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )*/
        }
        fetchData();
    }, []);


    const listaEntidadInvestigado = () => {

        return(
            entidadInvestigadoLista.data.map((entidad, i) => {
                return (
                    <tr key={entidad.id}>
                        <td>{ entidad.attributes.nombre_entidad }</td>
                        <td>
                            <small><strong>Fecha de registro:</strong> { entidad.attributes.created_at }<br/></small>
                            <small><strong>Sector:</strong> Pendiente<br/></small>
                            <small><strong>Nombre investigado:</strong> { entidad.attributes.nombre_investigado }<br/></small>
                            <small><strong>Cargo investigado:</strong> { entidad.attributes.cargo }<br/></small>
                            <small><strong>Observaciones:</strong> { entidad.attributes.observaciones }<br/></small>
                            <small><strong>Registrado por:</strong> { entidad.attributes.nombre_entidad }<br/></small>
                            <small><strong>Registrado en:</strong> { entidad.attributes.nombre_entidad }<br/></small>
                        </td>
                    </tr>
                )
            })
        )
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        setEntidadInvestigadoLista({ data: [], links: [], meta: [] });
        setRespuestaEntidadInvestigado(false);

        const data = {
            "data": {
                "type": "entidad_investigado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": 1,
                    "nombre_entidad": datos.entidadInvestigado,
                    "nombre_investigado": datos.nombreInvestigado,
                    "cargo": datos.cargoInvestigado,
                    "codigo": "",
                    "observaciones": "",
                    "estado": true,
                    "requiere_registro":true

                }
            }
        }

        //console.log(JSON.stringify(data));


        EntidadInvestigadoApi.getAllEntidadInvestigadoFilter(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setEntidadInvestigadoLista(datos)
                        setRespuestaEntidadInvestigado(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
        )

        

    }


    return(
        <>
            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/ClasificacionRadicadoForm/${radicado}/${procesoDisciplinarioId}`}><small>Clasificación del radicado</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-primary" aria-current="page"  to={`/ClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}><small> Historial</small></Link></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

            { isRedirect ? redirectToRoutes() : null }
            <Spinner />
            { <InfoErrorApi error={errorApi}/> }
            { <InfoExitoApi /> }
            <Formik
                initialValues={{
                    entidadInvestigado: '',
                    nombreInvestigado: '',
                    cargoInvestigado: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
    
                    if((!valores.entidadInvestigado || typeof(valores.entidadInvestigado) == 'undefined')
                    && (!valores.nombreInvestigado || typeof(valores.nombreInvestigado) == 'undefined')
                    && (!valores.cargoInvestigado || typeof(valores.cargoInvestigado) == 'undefined')){
                        
                        errores.cargoInvestigado = 'Debe ingresar al menos un campo'
                    }


                    return errores
                }}
                onSubmit={(valores, {resetForm}) => {
                    enviarDatos(valores);
                }}
            >

            {({ errors }) => (
                <Form>
            
            <div className="col-md-12 text-right">

            <Link to={`/EntidadInvestigadoForm/${radicado}/${procesoDisciplinarioId}`}>
                <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                    <i className="fa fa-fw fa-archive mr-1"></i> Crear entidad Investigado
                </button>
            </Link>
            </div>               
            <div className="block-header block-header-default">
                    <h3 className="block-title">Filtro de búsqueda</h3>
                    </div> 
                    <div className="block block-rounded block-bordered">
              
                        <div className="block-content block-content-full">
                            <div className="row">
                                
                               
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="entidadInvestigado">Entidad del investigado <span className="text-danger">*</span></label>
                                        <Field as="input" className="form-control" type="text" id="entidadInvestigado" name="entidadInvestigado" placeholder="Entidad del investigado" />
                                    </div>
                                </div>

                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="nombreInvestigado">Nombre del Investigado <span className="text-danger">*</span></label>
                                        <Field as="input" className="form-control" type="text" id="nombreInvestigado" name="nombreInvestigado" placeholder="Nombre del Investigado" />
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="cargoInvestigado">Cargo del Investigado <span className="text-danger">*</span></label>
                                        <Field as="input" className="form-control" type="text" id="cargoInvestigado" name="cargoInvestigado" placeholder="Cargo del Investigado" />
                                        <ErrorMessage name="cargoInvestigado" component={() => (<span className="text-danger">{errors.cargoInvestigado}</span>)} />
                                    </div>
                                </div>

                            </div>
                            
                        </div>
                        <div className="block-content block-content-full text-right bg-light">
                            <button type="submit" className="btn btn-sm btn-primary" >Buscar</button>           
                        </div>
                
                    </div>
                    </Form>
                )}
            </Formik> 
                
            

            <div className="block-header block-header-default">
                <h3 className="block-title">Historial clasificaciones del rádicado (RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>

                
            </div> 

            <div className="block block-rounded block-bordered">
                        
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th>Entidad</th>
                                        <th>Detalle</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    { respuestaEntidadInvestigado ? listaEntidadInvestigado() : null }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    );

  
}
export default EntidadInvestigadoLista;
