import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import { Navigate } from "react-router-dom";
import DataTable from 'react-data-table-component';

function RolesLista() {

    const [getSeach, setSeach] = useState('');
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [getRolesListaSearch, setRolesListaSearch] = useState({ data: [], links: [], meta: [] });
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    
    const [getRolesList, setRolesList] = useState([]);

    const redirectToRoutes = () => {
        return <Navigate to={`/RolesLista/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            GenericApi.getAllGeneric('role').then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"] != "") {
                            setRolesListaSearch(datos)
                            window.showSpinner(false);
                        }
                        else {
                            setErrorApi(datos.error.toString())
                            window.showModal(1)
                            window.showSpinner(false);    
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                        window.showSpinner(false);
                    }
                }
            )
        }
        fetchData();
    }, []);

    const agregarUsuario = (valores) => {        
        setRolesList([...getRolesList], valores.rol);
    }

    const eliminarUsuario = (index) => {
        setRolesList(
            ...getRolesList.slice(0, index),
            ...getRolesList.slice(index + 1, getRolesList.length)
        );
    }

    /*
    * Componentes
    */

    const componentSelectRol = () => {
        return (
            getRolesListaSearch.data.map((rol, i) => {
                return (
                    <option key={rol.id} value={rol.id}>{rol.attributes.nombre}</option>
                )
            })
        )
    }

    const componentRolForm = () => {
        return (
            <>
                <Formik
                    initialValues={{
                        rol: ''
                    }}
                    enableReinitialize
                    validate={(valores) => { 
                        let errores = {};

                        if(!valores.rol){
                            errores.rol = 'Debe seleccionar el rol';
                        }

                        return errores;
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        agregarUsuario(valores);
                    }}
                >
                    {({ errors }) => (
                        <Form>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-10">
                                        <label htmlFor='rol'>Rol <span className='text-danger'>*</span></label>
                                        <Field as='select' className='form-control' id='rol' name='rol'>
                                            <option value=''>Seleccione un rol</option>                                            
                                            {getRolesListaSearch ? componentSelectRol() : null}
                                        </Field>
                                        <ErrorMessage name='rol'component={() => (<span className='text-danger'>{errors.rol}</span>)} />
                                    </div>

                                    <div className="col-md-12">
                                        <div className="block-content block-content-full text-right bg-light">
                                            <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        );
    }

    const componentContenidoTablaRoles = () => {
        return (
            getRolesList.map((rol, i) => {
                return (
                    <tr key={i}>
                        <td>
                            {i}
                        </td>
                        <td>
                            { rol.attributes.nombre }
                        </td>
                        <td>
                            {
                                getRolesList.length > 1 ?
                                (
                                    <>
                                        <button type='button' title='Inactivar Soporte' className='btn btn-sm btn-danger' onClick={() => eliminarUsuario(i)}><i className="fas fa-minus-circle"></i></button>
                                    </>
                                )
                                :
                                null
                            }
                        </td>
                    </tr>
                )
            })
        )
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="block block-themed">
                <div className="col-md-12">

                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li className="breadcrumb-item"> <small>Lista de Roles</small></li>
                            </ol>
                        </nav>
                    </div>

                </div>
                <div className="block-header">
                    <h3 className="block-title">Roles Parametrizados</h3>

                    <Link to={`/Rol/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i className="fas fa-plus"></i> Agregar Rol</button>
                    </Link>
                </div>

                { componentRolForm() }

                <div className="block-content">
                    <div className="row">
                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Rol</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                { getRolesList.data ? componentContenidoTablaRoles() : null }
                            </tbody>
                        </table>

                        <div className="col-md-12">
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default RolesLista;
