import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useEffect, useState } from 'react';
import RemisionQuejaApi from '../../Api/Services/RemisionQuejaApi';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import { Navigate, useParams } from "react-router";
import { useLocation } from 'react-router-dom';
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";
import InfoExitoApiCustom from '../../Utils/InfoExitoApiCustom';
import InfoExitoApiTextsCustom from '../../Utils/InfoExitoApiTextsCustom';
import GenericApi from '../../Api/Services/GenericApi';
import '../../Utils/Constants';
function RemisionQuejaForm() {

    const [errorApi, setErrorApi] = useState('');
    const [errorVariablesApi, setErrorVariablesApi] = useState({});
    const [respuestaVariablesApi, setRespuestaVariablesApi] = useState(false);
    const [expedienteApi, setExpedienteApi] = useState([]);
    const [respuestaExpedienteApi, setRespuestaExpedienteApi] = useState(false);
    const [datosFormExpediente, setDatosFormExpediente] = useState({});

    const [respuestaDependenciaOrigen, setRespuestaDependenciaOrigen] = useState(false);
    const [listaDependenciaOrigen, setListaDependenciaOrigen] = useState({ data: {} });


    const [existeIncorporacion, setExisteIncorporacion] = useState(false);
    const [remisionQuejaIncorporacion, setRemisionQuejaIncorporacion] = useState({ data: {} });

    const [exitoApiTextsCustom, setExitoApiTextsCustom] = useState({ titulo: null, cuerpo: null, boton: null });

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let fase = 'Remision queja incorporacion'; /*Este dato se establece para obtener la información necesaria del API teniendo en cuenta la fase*/
    const [isRedirect, setIsRedirect] = useState(false);

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso`} state={{ from: from }} />;
    }

    useEffect(() => {

        async function fetchData() {
            RemisionQuejaApi.consultarIncorporacionExistente(procesoDisciplinarioId).then(
                datosRemision => {
                    if (!datosRemision.error) {
                        console.log("Daticos remision queja", datosRemision.data.attributes);
                        if (datosRemision.data.attributes) {
                            setRemisionQuejaIncorporacion(datosRemision.data);
                            setExisteIncorporacion(true);
                        }
                        else {
                            setExisteIncorporacion(false);

                            //ParametrosMasApi.getDependenciaOrigeByFase(fase).then(
                            //en la tabla MAS_DEPENDENCIA_ACCESO el 3 corresponde a Crear remision queja
                            GenericApi.getByIdGeneric('mas-dependencia-filtrado', global.Constants.ACCESO_DEPENDENCIA.REMISION_QUEJA_INCORPORACION).then(
                                datos => !datos.error ? (setListaDependenciaOrigen(datos), setRespuestaDependenciaOrigen(true)) : window.showModal(1)
                            )
                        }
                    }
                    else {
                        window.showModal(1);
                    }
                }
            )
        }

        fetchData();
    }, []);

    const validarDatos = (datos) => {

        setDatosFormExpediente(datos);

        const data = {
            "data": {
                "type": "remision_queja",
                "attributes": {
                    "accion": 1,
                    "dependencia": datos.dependencia,
                    "expediente": datos.expediente,
                    "vigencia": datos.vigencia,
                    "id_proceso_disciplinario": procesoDisciplinarioId
                }
            }
        }

        RemisionQuejaApi.consultarValidacionExpediente(data).then(
            datos => {
                if (!datos.error) {
                    setExpedienteApi(datos.data);
                    setRespuestaVariablesApi(false);
                    setRespuestaExpedienteApi(true);
                }
                else {

                    setRespuestaExpedienteApi(false);
                    setRespuestaVariablesApi(false);

                    if (datos.variables) {
                        setErrorVariablesApi(datos.variables);
                        setRespuestaVariablesApi(true);
                    }
                    else {
                        setErrorApi(datos.error);
                        window.showModal(1);
                    }
                }
            }
        )
    }

    const guardarDatos = () => {

        const data = {
            "data": {
                "type": "remision_queja",
                "attributes": {
                    "accion": 2,
                    "dependencia": datosFormExpediente.dependencia,
                    "expediente": datosFormExpediente.expediente,
                    "vigencia": datosFormExpediente.vigencia,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_proceso_disciplinario_expediente": expedienteApi.id,
                    "version": 305
                }
            }
        }

        RemisionQuejaApi.addIncorporacionExpediente(data).then(
            datos => {
                if (!datos.error) {
                    setExitoApiTextsCustom(
                        {
                            titulo: 'Expediente incorporado',
                            cuerpo: 'El expediente ' + radicado + ' fue incorporado el expediente ' + datosFormExpediente.expediente,
                            boton: 'Aceptar'
                        }
                    );
                    window.showModal(6);
                    setIsRedirect(true);
                }
                else {
                    console.log(datos.error);
                    //setErrorApi(datos.error);
                    window.showModal(1);
                }
            }
        )

    }

    /*
    * Componentes
    */

    const componentSelectDependenciaOrigen = () => {
        return (
            listaDependenciaOrigen.data.map((dependencia, i) => {
                return (
                    <option key={dependencia.id} value={dependencia.id}>{dependencia.attributes.nombre}</option>
                )
            })
        )
    }

    const componentExpedienteForm = () => {
        return (
            <>
                <Formik
                    initialValues={{
                        dependencia: '',
                        expediente: '',
                        vigencia: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {};

                        if (!valores.dependencia) {
                            errores.dependencia = 'Debe seleccionar la dependencia';
                        }

                        if (!valores.expediente) {
                            errores.expediente = 'Debe digitar el número de expediente';
                        }

                        if (!valores.vigencia) {
                            errores.vigencia = 'Debe digitar la vigencia';
                        }

                        return errores;
                    }}
                    onSubmit={(valores, { resetForm }) => {
                        validarDatos(valores);
                    }}>
                    {({ errors }) => (
                        <Form>
                            <div className='row'>
                                <div className='col-md-12'>
                                    <div className='alert alert-primary' role='alert'>
                                        <p className='mb-0'> ATENCIÓN: </p>
                                        <p className='mb-0'> POR PARÁMETROS DEL SISTEMA, PARA UNA QUEJA CLASIFICADA EN LA ETAPA DE EVALUACIÓN COMO: <b><u>INCOPORACION</u></b>; SE DEBE SELECCIONAR LA DEPENDENCIA DEL EJE DE POTESTAD DISCIPLINARIA QUE CONTINUARÁ CON EL TRÁMITE DEL EXPEDIENTE.</p>
                                    </div>
                                </div>

                                <div className='col-md-12'>
                                    <label htmlFor='dependencia'>Dependencia <span className='text-danger'>*</span></label>
                                    <Field as='select' className='form-control' id='dependencia' name='dependencia'>
                                        <option value=''>Seleccione la dependencia</option>
                                        {respuestaDependenciaOrigen ? componentSelectDependenciaOrigen() : null}
                                    </Field>
                                    <ErrorMessage name='dependencia' component={() => (<span className='text-danger'>{errors.dependencia}</span>)} />
                                </div>

                                <div className='col-md-6'>
                                    <label htmlFor='expediente'>No de expediente <span className='text-danger'>*</span></label>
                                    <Field className='form-control' id='expediente' name='expediente'></Field>
                                    <ErrorMessage name='expediente' component={() => (<span className='text-danger'>{errors.expediente}</span>)} />
                                </div>

                                <div className='col-md-6'>
                                    <label htmlFor='vigencia'>Vigencia del expediente <span className='text-danger'>*</span></label>
                                    <Field className='form-control' id='vigencia' name='vigencia'></Field>
                                    <ErrorMessage name='vigencia' component={() => (<span className='text-danger'>{errors.vigencia}</span>)} />
                                </div>

                                <div className='block-content block-content-full text-right'>
                                    <button type='submit' className='btn btn-danger'>
                                        <i className='fa fa-fw fa-search'></i> Validar Expediente
                                    </button>
                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        );
    }

    const componentErrorExpendiente = () => {
        return (
            <>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='alert alert-danger' role='alert'>
                            <p className='mb-0 text-center'><b>INCONSISTENCIA EN LOS DATOS INGRESADOS</b></p>
                            <p className='mb-0'>No se encontró registrado ningún Proceso Disciplinario asociado a los datos ingresados, es necesario que confirme la información para continuar con el trámite del expediente. <br></br>Si posterior a la revisión de los datos del expediente a incorporar persiste el presente mensaje, por favor contacte a la Dirección de TIC e informe de la presente alerta.</p><br></br>
                            <b>* Dependencia cosultada: </b>{errorVariablesApi.dependencia ? errorVariablesApi.dependencia : null}<br></br>
                            <b>* SINPROC consultado: </b>{errorVariablesApi.expediente ? errorVariablesApi.expediente : null}<br></br>
                            <b>* Para la vigencia: </b>{errorVariablesApi.vigencia ? errorVariablesApi.vigencia : null}<br></br>
                        </div>
                    </div>
                </div>
            </>
        );
    }

    const componentInformeExpendiente = () => {

        console.log("Daticos", expedienteApi);

        return (
            <>
                <div className='row'>
                    <div className='col-md-12 text-center'>
                        <label>DATOS DEL EXPEDIENTE A INCORPORAR</label>
                    </div>

                    <div className='col-md-12'>
                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <tbody>
                                <tr>
                                    <th>Datos consultados</th>
                                    <td>SINPROC {datosFormExpediente.expediente} - VIGENCIA {datosFormExpediente.vigencia}</td>
                                </tr>
                                <tr>
                                    <th>Hechos</th>
                                    <td>
                                        Fecha radicación: {expedienteApi?.attributes?.fecha_registro}<br></br>
                                        Anexos: {expedienteApi?.attributes?.anexos}<br></br>
                                        Asunto: {expedienteApi?.attributes?.antecedente?.descripcion}<br></br>
                                        Observaciones: {expedienteApi?.attributes?.usuario?.nombre + " " + expedienteApi?.attributes?.usuario?.apellido }
                                    </td>
                                </tr>
                                <tr>
                                    <th>Dependencia de gestión</th>
                                    <td>{expedienteApi?.attributes?.etapa_log?.dependencia?.nombre ? expedienteApi?.attributes?.etapa_log?.dependencia?.nombre : 'SIN INFORMACIÓN'}</td>
                                </tr>
                                <tr>
                                    <th>Etapa actual en SINPROC</th>
                                    <td>{expedienteApi?.attributes?.etapa?.nombre ? expedienteApi?.attributes?.etapa?.nombre : null}</td>
                                </tr>
                                <tr>
                                    <th>Vigencia</th>
                                    <td>{'PENDIENTE POR CONSTRUIR'}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>

                    {
                        (!!expedienteApi?.attributes?.etapa_log?.dependencia?.id && !!datosFormExpediente.dependencia && parseInt(expedienteApi?.attributes?.etapa_log?.dependencia?.id) == parseInt(datosFormExpediente.dependencia)) ?
                            (
                                <div className='col-md-12'>
                                    <div className='block-content block-content-full text-center'>
                                        <button onClick={() => guardarDatos()} className='btn btn-primary'>
                                            <i className='fa fa-fw fa-save'></i> Guardar y asignar
                                        </button>
                                    </div>
                                </div>
                            ) :
                            (
                                <div className='col-md-12'>
                                    <div className='alert alert-danger' role='alert'>
                                        <p className='mb-0 text-center'><b>ATENCIÓN</b></p>
                                        <p className='mb-0 text-center'> La dependencia seleccionada <b>NO</b> corresponde con la dependencia actual del expediente.</p>
                                    </div>
                                </div>
                            )
                    }

                </div>
            </>
        );
    }

    const componentInformacionIncorporacion = () => {
        return (
            <>
                <div className='content'>
                    <div className='row'>
                        <div className='col-md-12'>
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <tbody>
                                    <tr>
                                        <th width="20%">Evaluación de la queja</th>
                                        <td>{'Incorporación'}</td>
                                    </tr>
                                    <tr>
                                        <th>Fecha registro</th>
                                        <td>{remisionQuejaIncorporacion?.attributes?.fecha_creacion}</td>
                                    </tr>
                                    <tr>
                                        <th>Asignada a la delegada</th>
                                        <td>{remisionQuejaIncorporacion?.attributes?.dependencia?.nombre}</td>
                                    </tr>
                                    <tr>
                                        <th>Asignada a</th>
                                        <td>{remisionQuejaIncorporacion?.attributes?.usuario}</td>
                                    </tr>
                                    <tr>
                                        <th>Expediente a incorporar</th>
                                        <td>{remisionQuejaIncorporacion?.attributes?.expediente + ' de ' + remisionQuejaIncorporacion?.attributes?.vigencia_expediente}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApiTextsCustom success={exitoApiTextsCustom} />}
            {
                existeIncorporacion ?
                    (
                        componentInformacionIncorporacion()
                    )
                    :
                    (
                        <div className='content'>
                            <div className='row'>
                                <div className='col-md-12'>
                                    <div className='block block-themed'>
                                        <div className='block-header'>
                                            <h3 className='block-title'>SINPROC NO {radicado} ::: REMISIÓN QUEJA</h3>
                                        </div>
                                        <div className='block-content'>
                                            {componentExpedienteForm()}
                                            {respuestaVariablesApi ? componentErrorExpendiente() : null}
                                            {respuestaExpedienteApi ? componentInformeExpendiente() : null}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )


            }
        </>
    );

}

export default RemisionQuejaForm;
