import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import ParametrosMasApi from '../Api/Services/ParametrosMasApi';
import InteresadoEntidadPermitidaApi from '../Api/Services/InteresadoEntidadPermitidaApi';
import moment from 'moment';
import { Navigate } from "react-router-dom";
import { useParams } from "react-router";
import Autocomplete from "../Autocomplete/Autocomplete";
import Spinner from '../Utils/Spinner';
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';
import GenericApi from '../Api/Services/GenericApi';


function EntidadesInteresadoLista() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [respuesEntidadesPermitidas, setRespuestaEntidadesPermitidas] = useState(false);
    const [entidadesPermitidasLista, setEntidadesPermitidasLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaPermitidasTotal, setRespuestaEntidadesPermitidasTotal] = useState(false);
    const [entidadesPermitidasListaTotal, setEntidadesPermitidasListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const columns = [
        {
            name: 'Entidad',
            selector: entidadPerm => entidadPerm.attributes.id_entidad + " " + entidadPerm.attributes.nombre_entidad,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Fecha Registro',
            selector: entidadPerm => entidadPerm.attributes.created_at,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Estado',
            selector: entidadPerm => entidadPerm.attributes.estado == '1' ? 'Activo' : 'Inactivo',
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => (hasAccess('EI_EntidadesPermitidasInteresado', 'Inactivar')) ? (
                <div>
                    {
                        (row.attributes.estado == 0) ? (
                            <button type='button' title='Activar' onClick={() => enviarDatos(1, row.id)} className='btn btn-sm btn-success'><span className="fas fa-toggle-off"> </span></button>
                        ) : null
                    }

                    {
                        (row.attributes.estado == 1) ? (
                            <button type='button' title='Inacivar' onClick={() => enviarDatos(0, row.id)} className='btn btn-sm btn-danger'><span className="fas fa-toggle-off"> </span></button>
                        ) : null
                    }
                </div>
            ) : null
        }
    ];

    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadesInteresadoLista/`} />;
    }

    useEffect(() => {
        window.showSpinner(true);
        async function fetchData() {
            cargarEntidadesPermitidas(1, 10);
            //window.showSpinner(true);




        }
        fetchData();
    }, []);

    const cargarEntidadesPermitidas = (page, perPage) => {

        if (hasAccess('EI_EntidadesPermitidasInteresado', 'Consultar')) {

            GenericApi.getAllGeneric('mas-entidad-permitida/entidad-permitida-paginate' + '/' + page + '/' + perPage).then(
                datos => {
                    if (!datos.error) {
                        setEntidadesPermitidasListaTotal(datos);
                        setRespuestaEntidadesPermitidasTotal(true);
                        setEntidadesPermitidasLista(datos);
                        setRespuestaEntidadesPermitidas(true);
                        window.showSpinner(false);
                    }
                    else {
                        window.showSpinner(false);
                        window.showModal(1);

                    }

                }
            )

        }
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarEntidadesPermitidas(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarEntidadesPermitidas(page, newPerPage);
    }


    const enviarDatos = (estado, id) => {

        try {


            //actualizamos el estado
            window.showSpinner(true);
            const data = {

                "data": {
                    "type": "mas_entidad_permitida",
                    "attributes": {
                        "estado": estado
                    }
                }
            }


            InteresadoEntidadPermitidaApi.updateInteresadoEntidadPermitida(data, id).then(
                datos => {

                    if (!datos.error) {
                        cargarEntidadesPermitidas(1, 10);
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showSpinner(false)
                    }
                    //window.showSpinner(false);
                }
            )

        } catch (error) {
            window.showSpinner(false);
        }
    }

    const listaEntidadesPermitidas = () => {

        if (entidadesPermitidasLista.data != null && typeof (entidadesPermitidasLista.data) != 'undefined') {
            return (


                entidadesPermitidasLista.data.map((entidadPerm, i) => {
                    return cargarTabla(entidadPerm, i)
                })
            )
        }
        else {
            return (

                entidadesPermitidasLista.map((entidadPerm, i) => {
                    return cargarTabla(entidadPerm, i)
                })
            )
        }

    }

    const cargarTabla = (entidadPerm) => {
        return (
            <tr key={entidadPerm.id}>
                <td>{entidadPerm.attributes.id_entidad + " " + entidadPerm.attributes.nombre_entidad}</td>
                <td>{moment(entidadPerm.attributes.created_at).format("DD/MM/YYYY")}</td>
                <td>{entidadPerm.attributes.estado == '1' ? 'Activo' : 'Inactivo'}</td>
                <td>
                    <>
                        {
                            (hasAccess('EI_EntidadesPermitidasInteresado', 'Inactivar')) ? (
                                <div>
                                    {
                                        (entidadPerm.attributes.estado == 0) ? (
                                            <button type='button' title='Activar' onClick={() => enviarDatos(1, entidadPerm.id)} className='btn btn-sm btn-success'><span className="fas fa-toggle-off"> </span></button>
                                        ) : null
                                    }

                                    {
                                        (entidadPerm.attributes.estado == 1) ? (
                                            <button type='button' title='Inacivar' onClick={() => enviarDatos(0, entidadPerm.id)} className='btn btn-sm btn-danger'><span className="fas fa-toggle-off"> </span></button>
                                        ) : null
                                    }
                                </div>
                            ) : null
                        }
                    </>
                </td>
            </tr>
        )
    }


    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setEntidadesPermitidasLista(entidadesPermitidasListaTotal);
            }
            else {

                var filteredData = {
                    data: entidadesPermitidasListaTotal.data.filter(
                        suggestion => ((suggestion.attributes.id_entidad
                            + suggestion.attributes.nombre_entidad
                            + (suggestion.attributes.created_at)
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setEntidadesPermitidasLista(filteredData)
            }


        } catch (error) {
            console.error(error);
        }

    };


    return (
        <>
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>
                    <div className="block block-themed">
                        <div className="block-header">

                            <h3 className="block-title">Entidades permitidas</h3>
                            <>
                                {
                                    (hasAccess('EI_EntidadesPermitidasInteresado', 'Crear')) ? (
                                        <Link to={`/EntidadesInteresadoForm/`}>
                                            <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar entidad permitida</button>
                                        </Link>
                                    ) : null
                                }
                            </>

                        </div>
                        <div className="block-content">

                            <>
                                {
                                    (hasAccess('EI_EntidadesPermitidasInteresado', 'Consultar')) ? (
                                        <div>
                                            <div className='row'>

                                                <div className='col-md-12'>
                                                    <div className="form-group ">
                                                        <label htmlFor='search'>Buscar: </label>
                                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                    </div>
                                                </div>
                                            </div>


                                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                columns={columns}
                                                data={entidadesPermitidasLista.data}
                                                perPage={perPage}
                                                page={pageActual}
                                                pagination
                                                noDataComponent="Sin datos"
                                                paginationServer
                                                paginationTotalRows={entidadesPermitidasLista.meta ? entidadesPermitidasLista.meta.total : 1}
                                                onChangePage={handlePageChange}
                                                onChangeRowsPerPage={handlePerRowsChange}
                                                defaultSortFieldId="Nombre"
                                                striped
                                                icon
                                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                                defaultSortAsc={false}
                                            />

                                        </div>

                                    ) : null
                                }
                            </>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    )

}

