import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { useLocation } from 'react-router-dom';
import { ParametroModel } from '../../Models/ParametroModel';

function ValidarClasificacionRadicadoForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [listaTipoRespuesta, setListaTipoRespuesta] = useState({ data: {} });
    const [selectedTipoRespuesta, setSelectedTipoRespuesta] = useState("");
    const [respuestaTipoRespuesta, setRespuestaTipoRespuesta] = useState(false);

    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data: {} });
    const [respuestaTipoExpediente, setRespuestaTipoExpediente] = useState(false);
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");

    const [listaTipoDerechoPeticion, setListaDerechoPeticion] = useState({ data: {} });
    const [selectedTipoDerechoPeticion, setSelectedDerechoPeticion] = useState("");
    const [respuestaTipoDerechoPeticion, setRespuestaDerechoPeticion] = useState(false);


    const [listaTerminosRespuesta, setListaTerminosRespuesta] = useState({ data: {} });
    const [selectedTerminosRespuesta, setSelectedTerminosRespuesta] = useState("");
    const [respuestaTerminosRespuesta, setRespuestaTerminosRespuesta] = useState(false);

    const [countTextArea, setCountTextArea] = useState(0);
    const [listaTiposQueja, setListaTiposQueja] = useState({ data: {} });
    const [selectedTiposQueja, setSelectedTiposQueja] = useState("");
    const [respuestaTiposQueja, setRespuestaTiposQueja] = useState(false);

    const [fechaTermino, setFechaTermino] = useState(null);
    const [resultDiasNoLaborales, setResultDiasNoLaborales] = useState([]);

    const [startDate, setStartDate] = useState(new Date());

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
	let vigencia = from.vigencia;

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to="/MisPendientes" />;
    }

    useEffect(() => {
        async function fetchData() {

            ParametrosMasApi.getAllTipoRespuesta().then(
                datos => !datos.error ? (setListaTipoRespuesta(datos), setRespuestaTipoRespuesta(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposExpediente().then(
                datos => !datos.error ? (setListaTipoExpediente(datos), setRespuestaTipoExpediente(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposDerechoPeticion().then(
                datos => !datos.error ? (setListaDerechoPeticion(datos), setRespuestaDerechoPeticion(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTerminosRespuesta().then(
                datos => !datos.error ? (setListaTerminosRespuesta(datos), setRespuestaTerminosRespuesta(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposQueja().then(
                datos => !datos.error ? (setListaTiposQueja(datos), setRespuestaTiposQueja(true)) : window.showModal(1)
            )

            setResultDiasNoLaborales([]);
            ParametrosMasApi.getAllDiasNoLaborales().then(
                datos => {
                    if (!datos.error) {
                        var data = [];
                        for (var i in datos.data) {
                            var date = datos.data[i]["attributes"]["fecha"].split(' ')[0];
                            var result = new Date(date);
                            result.setDate(result.getDate() + 1);
                            data.push(i, date);
                        }

                        setResultDiasNoLaborales(data);
                        //console.log(resultDiasNoLaborales);
                    } else {
                        window.showModal(1);
                    }

                }

            )
        }
        fetchData();
    }, []);


    const disableCustomDt = current => {
        return !resultDiasNoLaborales.includes(current.format('YYYY-MM-DD'));
    }


    let selectChangeTipoRespuesta = (e) => {
        setSelectedTipoRespuesta(e);
    }

    let selectChangeTipoExpediente = (e) => {
        setSelectedTipoExpediente(e);
    }

    let selectChangeTipoDerechoPeticion = (e) => {
        setSelectedDerechoPeticion(e);
    }

    let selectChangeTerminoRespuesta = (e) => {
        setSelectedTerminosRespuesta(e);
    }

    let selectChangeTiposQueja = (e) => {
        setSelectedTiposQueja(e);
    }


    //Componetes

    const selectTipoRespuesta = () => {
        return (
            listaTipoRespuesta.data.map((tipo_respuesta, i) => {
                return (
                    <option key={tipo_respuesta.id} value={tipo_respuesta.id}>{tipo_respuesta.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoExpediente = () => {
        return (
            listaTipoExpediente.data.map((expediente, i) => {
                return (
                    <option key={expediente.id} value={expediente.id}>{expediente.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoDerechoPeticion = () => {
        return (
            listaTipoDerechoPeticion.data.map((derecho_peticion, i) => {
                return (
                    <option key={derecho_peticion.id} value={derecho_peticion.id}>{derecho_peticion.attributes.nombre}</option>
                )
            })
        )
    }

