                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 2,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": (datos.gestion_juridica === 'true'),
                        "estado": true,
                        "id_estado_reparto": 2,
                    }
                }
            }
        }
        //TIPO EXPEDIENTE => DERECHO DE PETICION => DERECHO DE PETICION => ALERTA CONTROL POLITICO
        else if (selectedTipoExpediente === '1' && selectedTipoDerechoPeticion === '3') {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 2,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": (datos.gestion_juridica === 'true'),
                        "estado": true,
                        "id_estado_reparto": 2,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => PODER REFERENTE A SOLICITUD
        if (selectedTipoExpediente === '2') {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 2,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.tipo_queja,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => QUEJA
        if (selectedTipoExpediente === '3') {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 2,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.id_tipo_queja,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
