                    </select>

                    {/*DIAS TERMINO*/}
                    {selectedTerminosRespuesta === '1' ? componenteTerminosRespuesta(1) : ''}
                    {/*HORAS TERMINO*/}
                    {selectedTerminosRespuesta === '2' ? componenteTerminosRespuesta(2) : ''}

                </>
            );
        }
    };

    // COMPONENTE TIPO DERECHO DE PETICION
    const componenteTipoDerechoPeticion = (tipo_derecho_peticion) => {

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  COPIAS
