                                    <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                </div>

                                <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
