import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import Spinner from '../Utils/Spinner';
import DataTable from 'react-data-table-component';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';

function ActuacionesAdminLista() {
    const [actuacionesLista, setActuacionesLista] = useState({ data: [] });
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getSeach, setSeach] = useState('');
    const [getEstadoLista, setEstadoLista] = useState('1');

    const [getRoutes, setRoutes] = useState({

        crear_registro: "/ActuacionesAdministracion/Add",
        consultar_registros: "/ActuacionesAdministracion",
        muestra_atras: false,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
    });

    const handleCallback = (childData) => {
        try {

            setEstadoLista(childData)
        } catch (error) {

        }

    }
    const columns = [
        {
            name: 'Información actuacion',
            selector: mas_actuaciones =>
                <div className='mb-3'>
                    <small><b>Nombre Actuacion:</b> {mas_actuaciones.attributes.nombre_actuacion ? mas_actuaciones.attributes.nombre_actuacion : "-"}<br /></small>
                    <small><b>Etapa:</b> {mas_actuaciones.attributes.nombre_etapa ? mas_actuaciones.attributes.nombre_etapa : "-"}<br /></small>
                    <small><b>Etapa despues de aprobación:</b> {mas_actuaciones.attributes.nombre_etapa_despues_aprobacion ? mas_actuaciones.attributes.nombre_etapa_despues_aprobacion : "-"}<br /></small>
                    <small><b>Anula otras actuaciones:</b> {mas_actuaciones.attributes.despues_aprobacion_listar_actuacion == "1" ? 'Si' : 'No'}</small> <br />
                    <small><b>Tipo actuacion:</b> {(mas_actuaciones.attributes.tipo_actuacion == "0" ? "Actuacion" : (mas_actuaciones.attributes.tipo_actuacion == "1" ? 'Impedimento' : 'Comisorio'))}</small> <br />
                    <small><b>Excluyente:</b> {(mas_actuaciones.attributes.excluyente == "1" ? 'Si' : 'No')}</small>
                </div>,
            sortable: true,
            width: '50%'
        },
        // {
        //     name: 'Plantilla',
        //     selector: mas_actuaciones => mas_actuaciones.attributes.nombre_plantilla,
        //     sortable: true,
        // },
        // {
        //     name: 'Etapa',
        //     selector: mas_actuaciones => mas_actuaciones.attributes.nombre_etapa,
        //     sortable: true,
        //     width: "10%"
        // },
        // {
        //     name: 'Etapa despues de aprobación',
        //     selector: mas_actuaciones => mas_actuaciones.attributes.nombre_etapa_despues_aprobacion,
        //     sortable: true,
        //     width: "15%"
        // },
        // {
        //     name: 'Anula otras actuaciones',
        //     selector: mas_actuaciones => (mas_actuaciones.attributes.despues_aprobacion_listar_actuacion == "1" ? 'Si' : 'No'),
        //     sortable: true,
        //     width: "10%"
        // },
        {
            name: 'Estado',
            selector: mas_actuaciones => (mas_actuaciones.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "25%"
        },
        // {
        //     name: 'Tipo actuacion',
        //     selector: mas_actuaciones => (mas_actuaciones.attributes.tipo_actuacion == "0" ? "Actuacion" : (mas_actuaciones.attributes.tipo_actuacion == "1" ? 'Impedimento' : 'Comisorio')),
        //     sortable: true,
        // },
        // {
        //     name: 'Excluyente',
        //     selector: mas_actuaciones => (mas_actuaciones.attributes.excluyente == "1" ? 'Si' : 'No'),
        //     sortable: true,
        //     width: "10%"
        // },
        {
            name: 'Acciones',
            cell: row => <div><Link to={`${row.id}`} state={{ from: row }}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>,
            width: "25%"
        }
    ];


    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarActuaciones(1, paganationPerPages);
        }
        fetchData();
    }, []);


    const cargarActuaciones = (page, perPage) => {

        //GenericApi.getAllGeneric('departamento/departamento-paginate' + '/' + page + '/' + perPage).then(
        GenericApi.getAllGeneric('mas_actuaciones').then(
            datos => {
                if (!datos.error) {
                    setActuacionesLista(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal()
                    window.showSpinner(false);
                }
            }
        )
    }

    const handlePageChange = page => {
        //window.showSpinner(true);
        setPageActual(page);
        //cargarDepartamentos(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        //window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        //cargarDepartamentos(page, newPerPage)

    }




    return (
        <div>
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <small>Administración</small></li>
                        <li class="breadcrumb-item"> <small>Configuración</small></li>
                        <li class="breadcrumb-item"> <small>Lista de actuaciones</small></li>
                    </ol>
                </nav>
            </div>
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Actuaciones registradas {getEstadoLista == global.Constants.ESTADOS.INACTIVO ? "inactivas" : "activas"}</h3>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-3'>
                            <div className="form-group ">
                                <input type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                        <ListaBotones getRoutes={getRoutes} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />

                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}

                                data={actuacionesLista.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        if (suggestion.attributes.estado == getEstadoLista) {
                                            return suggestion;
                                        }
                                    } else if (
                                        ((suggestion.id
                                            + suggestion.attributes.nombre_actuacion
                                            + suggestion.attributes.nombre_plantilla
                                            + suggestion.attributes.nombre_etapa
                                            + suggestion.attributes.nombre_etapa_despues_aprobacion
                                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(getSeach.toLowerCase())
                                            + (suggestion.attributes.despues_aprobacion_listar_actuacion == "1" ? 'Si' : 'No').toLowerCase().includes(getSeach.toLowerCase())
                                            + (suggestion.attributes.tipo_actuacion == "0" ? 'Actuación' : (suggestion.attributes.tipo_actuacion == "1" ? 'Impedimento' : 'Comisorio')).toLowerCase().includes(getSeach.toLowerCase())
                                            + (suggestion.attributes.excluyente == "1" ? 'Si' : 'No').toLowerCase().includes(getSeach.toLowerCase())
                                            && (suggestion.attributes.estado == getEstadoLista))
                                    ) {
                                        return suggestion;
                                    }
                                })}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationTotalRows={actuacionesLista.data.length}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ActuacionesAdminLista;
