import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useLocation } from 'react-router-dom'
import LogApi from '../Api/Services/LogApi';
import moment from 'moment';
import { Link } from "react-router-dom";
import DataTable from 'react-data-table-component';
import GenericApi from '../Api/Services/GenericApi';
import DocumentoSiriusApi from '../Api/Services/DocumentoSiriusApi';


function LogEtapaLista() {


    const [errorApi, setErrorApi] = useState('');
    const [getSeach, setSeach] = useState('');
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    const [getRespuestaLog, setRespuestaLog] = useState(false);
    const [getLogLista, setLogLista] = useState({ data: [], links: [], meta: [] });
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getListaDocumentos, setListaDocumentos] = useState({ data: [], links: [], meta: [] });

    const columns = [

        {
            name: 'Etapa',
            selector: log => log.attributes.etapa.nombre,
            sortable: true,
            width: "10%"
        },

        {
            name: 'Fecha',
            cell: log => log.attributes.created_at,
            selector: log => log.attributes.created_at,
            sortable: true,
            width: "10%"
        },

        {
            name: 'Funcionario Registra',

            cell: log => <div>{log.attributes.funcionario_registra.name}<br />
                {log.attributes.dependencia_origen.nombre}</div>,
            sortable: true,
            selector: log => log.attributes.funcionario_registra.name,
            width: "15%"
        },

        {
            name: 'Funcionario Actual',

            cell: log => <div>{log.attributes.funcionario_actual!= null?log.attributes.funcionario_actual.name:null}<br /></div>,
            sortable: true,
            selector: log => log.attributes.funcionario_registra.name,
            width: "15%"
        },

        {
            name: 'Proceso',
            cell: log => <div>{log.attributes.tipo_trasaccion.nombre}<br/> <br/>{log.attributes.descripcion}</div>,
            sortable: true,
            selector: log => log.attributes.tipo_trasaccion.nombre, 
            width: "20%"
        },
        {
            name: 'Estado Etapa',
            selector: log => log.attributes.estado_etapa.nombre,
            sortable: true,
            width: "15%",
        }
    ];


    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [getLista, setLista] = useState({ data: [], links: [], meta: [] });
    const [getListaTotal, setListaTotal] = useState({ data: [], links: [], meta: [] });



    /* */

    const handlePageChange = page => {
        //window.showSpinner(true);
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        //window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);

    }



    useEffect(() => {
        async function fetchData() {

            cargarLogProcesoDisciplinario(1, paganationPerPages, "1")

        }
        fetchData();
    }, []);

    const cargarLogProcesoDisciplinario = (page, perPage, estado) => {

        const data = {
            "data": {
                "type": "log_etapa",
                "attributes": {
                    "descripcion": "descripcion",
                    "id_etapa": "fecha_registro",
                    "id_fase": "id_dependencia",
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_tipo_log": "id_tipo_log",
                    "id_estado": "id_estado",
                    "created_user": "created_user",
                    "id_dependencia_origen": "id_dependencia_origen",
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }

        LogApi.getLogEtapaProcesoDisciplinario(data, procesoDisciplinarioId).then(
            datos => !datos.error ? (setLogLista(datos), setRespuestaLog(true)) : window.showModal(1)
        )

    }


    const documentosAsociados = (data) => {

        const data_documento = {
            "data": {
                "type": "documento_sirius",
                "attributes": {
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                }
            }
        }

        GenericApi.getByDataGeneric('log-proceso-disciplinario/get-documentos/' + data.data.id, data).then(

            datos => {

                console.log('log-proceso-disciplinario/get-documentos/'+data.data.id)

                if (!datos.error) {

                    
                    try {
                        if (datos.data.length > 0) {
                            setListaDocumentos(datos);
                        }
                    } catch (error) {
                        console.log(error);
                    }



                   // window.showSpinner(false);
                }
                else {
                    //window.showSpinner(false);
                    //window.showModal(1);

                }

            }
        )

       return (
        <>
       
    
            <table>
                <thead>
                    <tr>
                        <th>Fecha</th>
                        <th>Etapa</th>
                        <th>Fase</th>
                        <th>Registrado Por</th>
                        <th>Descripción</th> 
                        <th>Descargar Documento</th>                   
                    </tr>
                </thead>
                <tbody>
                    {tableDocumentos()}
                   
                </tbody>
            </table>
        </>
        
       )


    }


    const tableDocumentos = () => {


        return (
            getListaDocumentos.data.map((aux, i) => {
                return (
                    <tr key={aux.id}>
                        <td>{aux.attributes.created_at}</td>
                        <td>{aux.attributes.etapa.nombre}</td>
                        <td>{aux.attributes.fase.nombre}</td>
                        <td>{aux.attributes.nombre_completo}</td>
                        <td>{aux.attributes.descripcion}</td>
                        <td>{aux.attributes.nombre_archivo}</td>
                        <td>
                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' 
                                onClick={() => handleClicArchivo(aux.attributes.nombre_archivo)}><i className="fas fa-download"></i>
                            </button>
                        </td>
                    </tr>
                )
            })
        )
    }


    function downloadBase64File(contentType, base64Data, fileName, extension) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName + "." + extension;
        downloadLink.click();
    }

    const handleClicArchivo = (documento_sirius) => {
        try {
            let nombre_documento = documento_sirius.id;
            let extension = documento_sirius.attributes.extension;
            let es_compulsa = false;

            if (documento_sirius.attributes.compulsa) {
                es_compulsa = true;
            }

            const data = {
                "data": {
                    "type": "documeto_sirius",
                    "attributes": {
                        "radicado": radicado,
                        "vigencia": vigencia,
                        "id_documento_sirius": nombre_documento,
                        "extension": extension,
                        "es_compulsa": es_compulsa
                    }
                }
            }

            DocumentoSiriusApi.getDocumento(data).then(
                datos => {
                    if (!datos.error) {
                        //console.log(datos.content_type);
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento, extension);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };




    


    return (
        <>
            <Formik>
                <div className="block block-themed">
                    <div className="block-header">
                        <h3 className="block-title">Log Proceso disciplinario :: {radicado} </h3>
                    </div>
                    <div className="block-content">

                        <div className='row'>

                            <div className='col-md-3'>
                                <div className="form-group ">
                                    <Field type="text" id="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} name="search" className="form-control border border-success" placeholder="Buscar" />
                                </div>
                            </div>


                        </div>

                        <div className='col-md-12'>

                            <div className='col-md-12 mt-2 mb-2'>
                                <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                    columns={columns}
                                    data={getLogLista.data.filter((suggestion) => {
                                        if (getSeach === "") {
                                            return suggestion;
                                        } else if (

                                            ((suggestion.attributes.descripcion
                                                + suggestion.attributes.nombre_completo + suggestion.attributes.nombre_dependencia +
                                                suggestion.attributes.fecha_creado + suggestion.attributes.etapa.nombre +
                                                suggestion.attributes.estado + suggestion.attributes.fecha_registro).toLowerCase().includes(getSeach.toLowerCase()))

                                        ) {
                                            return suggestion;
                                        }
                                    })}
                                    perPage={perPage}
                                    page={pageActual}
                                    pagination
                                    noDataComponent="Sin datos"
                                    paginationTotalRows={getLogLista.data.length}
                                    onChangePage={handlePageChange}
                                    onChangeRowsPerPage={handlePerRowsChange}
                                    defaultSortFieldId="Nombre"
                                    striped
                                    paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                    defaultSortAsc={false}
                                    expandableRows expandableRowsComponent={documentosAsociados}
                                />
                            </div>
                           
                        </div>
                    </div>
                </div>
            </Formik>
        </>
    );
}

export default LogEtapaLista;
