import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import EntidadInvestigadoApi from "../../Api/Services/EntidadInvestigadoApi";
import { Navigate } from "react-router-dom";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom';
import Spinner from '../../Utils/Spinner';
import { hasAccess } from '../../../components/Utils/Common';
import ListaBotones from '../../Utils/ListaBotones';
import GenericApi from './../../Api/Services/GenericApi';
import moment from 'moment';
import DataTable from 'react-data-table-component';

function EntidadInvestigadoLista() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);
    const [getEstadoLista, setEstadoLista] = useState('');
    const [getListaDetalleCambios, setListaDetalleCambios] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let id_etapa = from.id_etapa;
    let id_fase = from.id_fase;

    const columns = [
        {
            name: 'Entidad',
            selector: entidad => entidad.attributes.nombre_entidad,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Detalle',
            cell: entidad => <div><small><strong>Fecha de registro:</strong> {entidad.attributes.created_at}<br /></small>
                <small><strong>Sector:</strong> Pendiente<br /></small>
                <small><strong>Nombre investigado:</strong> {entidad.attributes.nombre_investigado}<br /></small>
                <small><strong>Cargo investigado:</strong> {entidad.attributes.cargo}<br /></small>
                <small><strong>Observaciones:</strong> {entidad.attributes.observaciones}<br /></small>
                <small><strong>Registrado por:</strong> {entidad.attributes.nombre_completo}<br /></small>
                <small><strong>Registrado en:</strong> {entidad.attributes.nombre_entidad}<br /></small>
                <small><strong>Estado:</strong> {entidad.attributes.estado == '1' ? 'Activo' : 'Inactivo'}<br /></small>
                {
                    (entidad.attributes.observaciones_estado) ? (
                        <small><strong>Justificación cambio de estado:</strong> {entidad.attributes.observaciones_estado}<br /></small>
                    ) : null
                }</div>,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Acciones',


            cell: entidad =>
                <div className='row'>
                    <div className='col-4'>
                        <button type='button' title='Ver detalle Entidad del Investigado' className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => cargarDetalleCambiosEstado(entidad.id)} data-target={'#modal-consultar-detalle'}><i class="fas fa-search"></i></button>
                    </div>
                    <div className='col'>

                        {
                            (hasAccess('CR_EntidadInvestigado', 'Inactivar')) ? (
                                <div>
                                    {
                                        (entidad.attributes.estado == 0) ? (
                                            <Link to={`/EntidadInvestigadoCambiarEstadoForm/${entidad.id}`} state={{ from: from }}>
                                                <button type='button' title='Activar Entidad' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado'}><i className="fas fa-plus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                    {
                                        (entidad.attributes.estado == 1 &&  entidadInvestigadoLista.data.length > 1) ? (
                                            <Link to={`/EntidadInvestigadoCambiarEstadoForm/${entidad.id}`} state={{ from: from }}>
                                                <button type='button' title='Inactivar Entidad' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                    <div className="modal fade" id={'modal-consultar-detalle'} tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                                        <div className="modal-dialog modal-lg" role="document" >
                                            <div class="modal-content">
                                                <div class="block block-themed block-transparent mb-0">
                                                    <div class="block-header bg-primary-dark">
                                                        <h3 class="block-title">Detalle del cambio en la entidad del investigado</h3>
                                                        <div class="block-options">
                                                            <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                                <i class="fa fa-fw fa-times"></i>
                                                            </button>
                                                        </div>
                                                    </div>
                                                    <div class="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                        Observaciones generales: {entidad.attributes.observaciones}<br /><br />
                                                    </div>
                                                    {
                                                        (getListaDetalleCambios.data.length > 0) ? (
                                                            <div>
                                                                <div className="block-content">
                                                                    <spam class="block-title">Historial de cambios en el antecedente</spam>
                                                                </div>

                                                                <div class="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                        <thead>
                                                                            <tr>
                                                                                <th>Usuario Registra</th>
                                                                                <th>Descripción</th>
                                                                                <th>Dependencia</th>
                                                                                <th>Fecha</th>
                                                                            </tr>
                                                                        </thead>
                                                                        <tbody>
                                                                            {listaDetalleCambios()}
                                                                        </tbody>
                                                                    </table>
                                                                </div>
                                                            </div>
                                                        ) : null
                                                    }


                                                    <div class="block-content block-content-full text-right bg-light">
                                                        <button type="button" class="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>


                                </div>
                            ) : null
                        }
                    </div>
                </div>
        }
    ];

    const [getRoutes, setRoutes] = useState({
        id_etapa: 1,
        id_fase: 3,
        crear_registro: "/EntidadInvestigadoForm",
        consultar_registros: "/EntidadInvestigadoLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: "/SoporteRadicadoLista",
        modulo: "CR_EntidadInvestigado",
        funcionalidad_crear: "Crear",
        funcionalidad_consultar: "Consultar"
    });


    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/`} state={{ from: from }} />;
    }
    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setEstadoLista("Activos")


            cargarDatosInvestigado(1, 10, true);

            // VALIDA CIERRE DE ETAPA EVALUACION
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }

            CierreEtapaApi.getCierreEtapaByIdProcesoDisciplinario(dataCierreEtapa).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);



    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaEntidadInvestigado, setRespuestaEntidadInvestigado] = useState(false);
    const [entidadInvestigadoLista, setEntidadInvestigadoLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaEntidadInvestigadoTotal, setRespuestaEntidadInvestigadoTotal] = useState(false);
    const [entidadInvestigadoListaTotal, setEntidadInvestigadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setEntidadInvestigadoLista(entidadInvestigadoListaTotal);
            }
            else {


                var filteredData = {
                    data: entidadInvestigadoListaTotal.data.filter(
                        suggestion => ((suggestion.attributes.created_at
                            + suggestion.attributes.nombre_investigado + suggestion.attributes.cargo +
                            suggestion.attributes.observaciones + suggestion.attributes.nombre_entidad).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setEntidadInvestigadoLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }

    };

    const listaDetalleCambios = () => {
        if (getListaDetalleCambios.data != null && typeof (getListaDetalleCambios.data) != 'undefined') {
            return (



                getListaDetalleCambios.data.map((cambio, i) => {
                    return (
                        <tr key={cambio.id}>
                            <td>
                                {cambio.attributes.funcionario_registra?cambio.attributes.funcionario_registra.nombre + ' '+cambio.attributes.funcionario_registra.apellido:""}
                            </td>
                            <td>
                                {cambio.attributes.dependencia_origen ? cambio.attributes.dependencia_origen.nombre : ""}
                            </td>
                            <td>
                                {cambio.attributes.descripcion}
                            </td>
                            <td>
                                {cambio.attributes.created_at}
                            </td>
                        </tr>
                    )
                })
            )
        }
    }

    const cargarDetalleCambiosEstado = (id) => {

        GenericApi.getByIdGeneric('log-proceso-disciplinario/get-log-proceso', id).then(
            datos => !datos.error ? (setListaDetalleCambios(datos)) : (window.showModal(1)));
    }

    const listaEntidadInvestigado = () => {

        if (entidadInvestigadoLista.data != null && typeof (entidadInvestigadoLista.data) != 'undefined') {
            return (


                entidadInvestigadoLista.data.map((entidad, i) => {
                    return (
                        cuerpoTabla(entidad, i)
                    )
                })
            )
        }
        else {
            return (

                entidadInvestigadoLista.map((entidad, i) => {
                    return (
                        cuerpoTabla(entidad)
                    )
                })
            )
        }

    }


    const cuerpoTabla = (entidad, i) => {

        return (
            <tr key={entidad.id}>
                <td>{entidad.attributes.nombre_entidad}</td>
                <td>
                    <small><strong>Fecha de registro:</strong> {entidad.attributes.created_at}<br /></small>
                    <small><strong>Sector:</strong> Pendiente<br /></small>
                    <small><strong>Nombre investigado:</strong> {entidad.attributes.nombre_investigado}<br /></small>
                    <small><strong>Cargo investigado:</strong> {entidad.attributes.cargo}<br /></small>
                    <small><strong>Observaciones:</strong> {entidad.attributes.observaciones}<br /></small>
                    <small><strong>Registrado por:</strong> {entidad.attributes.nombre_completo}<br /></small>
                    <small><strong>Registrado en:</strong> {entidad.attributes.nombre_entidad}<br /></small>
                    <small><strong>Estado:</strong> {entidad.attributes.estado == '1' ? 'Activo' : 'Inactivo'}<br /></small>
                    {
                        (entidad.attributes.observaciones_estado) ? (
                            <small><strong>Justificación cambio de estado:</strong> {entidad.attributes.observaciones_estado}<br /></small>
                        ) : null
                    }

                </td>
                <td>

                    {
                        (hasAccess('CR_EntidadInvestigado', 'Inactivar')) ? (
                            <div>
                                <button type='button' title='Ver detalle Entidad del Investigado' className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => cargarDetalleCambiosEstado(entidad.id)} data-target={'#modal-consultar-detalle' + i}><i class="fas fa-search"></i></button>
                                {
                                    (entidad.attributes.estado == 0) ? (
                                        <Link to={`/EntidadInvestigadoCambiarEstadoForm/${entidad.id}`} state={{ from: from }}>
                                            <button type='button' title='Activar Entidad' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado' + i}><i className="fas fa-plus-circle"></i></button>
                                        </Link>
                                    ) : null
                                }

                                {
                                    (entidad.attributes.estado == 1) ? (
                                        <Link to={`/EntidadInvestigadoCambiarEstadoForm/${entidad.id}`} state={{ from: from }}>
                                            <button type='button' title='Inactivar Entidad' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                        </Link>
                                    ) : null
                                }


                            </div>
                        ) : null
                    }
                </td>

                <div className="modal fade" id={'modal-consultar-detalle'} tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div className="modal-dialog modal-lg" role="document" >
                        <div class="modal-content">
                            <div class="block block-themed block-transparent mb-0">
                                <div class="block-header bg-primary-dark">
                                    <h3 class="block-title">Detalle del cambio en la entidad del investigado</h3>
                                    <div class="block-options">
                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i class="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div class="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                    Observaciones generales: {entidad.attributes.observaciones}<br /><br />
                                </div>
                                {
                                    (getListaDetalleCambios.data.length > 0) ? (
                                        <div>
                                            <div className="block-content">
                                                <spam class="block-title">Historial de cambios en el antecedente</spam>
                                            </div>

                                            <div class="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                    <thead>
                                                        <tr>
                                                            <th>Usuario Registra</th>
                                                            <th>Descripción</th>
                                                            <th>Dependencia</th>
                                                            <th>Fecha</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {listaDetalleCambios()}
                                                    </tbody>
                                                </table>
                                            </div>
                                        </div>
                                    ) : null
                                }


                                <div class="block-content block-content-full text-right bg-light">
                                    <button type="button" class="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////


    const cargarDatosInvestigado = (page, perPage, estado) => {

        //console.log("Sandra: " + procesoDisciplinarioId)

        const data = {
            "data": {
                "type": "entidad_investigado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": "1",
                    "requiere_registro": true,
                    "estado": estado,
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }

        if (hasAccess('CR_EntidadInvestigado', 'Consultar')) {
            EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setEntidadInvestigadoLista(datos)
                        setRespuestaEntidadInvestigado(true)
                        setEntidadInvestigadoListaTotal(datos)
                        setRespuestaEntidadInvestigadoTotal(true)
                        window.showSpinner(false);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal();
                        window.showSpinner(false);
                    }

                }
            )
        }
        else {
            window.showSpinner(false)
        }
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarDatosInvestigado(page, perPage, '1');
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);

        setPerPage(newPerPage);
        setPageActual(page);
        cargarDatosInvestigado(page, newPerPage, '1');

    }



    const handleCallback = (childData) => {
        try {
            window.showSpinner(true)
            setEstadoLista(childData == '0' ? "Inactivos" : "Activos")
            cargarDatosInvestigado(1, 10, childData);
        } catch (error) {

        }

    }


    return (
        <>

            {<Spinner />}
            <Formik>
                <Form>
                    <div className="col-md-12">
                        <div className="block block-rounded block-bordered">
                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">

                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                        <li class="breadcrumb-item"> <small>Entidad del investigado Lista</small></li>

                                    </ol>
                                </nav>
                            </div>
                        </div>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Consultar lista de Entidad del Investigado {getEstadoLista}</strong></h3>
                        </div>

                        <div className="block-content">
                            <>

                                <div className='row'>

                                    <div className='col-md-3'>
                                        <div className="form-group ">
                                            <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>

                                    {/*ListaBotones(getRoutes)*/}
                                    <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />


                                </div>


                                {
                                    (hasAccess('CR_EntidadInvestigado', 'Consultar')) ? (
                                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                            columns={columns}
                                            data={entidadInvestigadoLista.data}
                                            perPage={perPage}
                                            page={pageActual}
                                            pagination
                                            noDataComponent="Sin datos"
                                            paginationServer
                                            paginationTotalRows={entidadInvestigadoLista.meta ? entidadInvestigadoLista.meta.total : 1}
                                            onChangePage={handlePageChange}
                                            onChangeRowsPerPage={handlePerRowsChange}
                                            defaultSortFieldId="Nombre"
                                            striped
                                            icon
                                            paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                            defaultSortAsc={false}
                                        />
                                    ) : null
                                }
                            </>

                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default EntidadInvestigadoLista;
