sourcesContent:
import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";

function ClasificacionRadicadoForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data: {} });

    const [respuestaTipoExpediente, setRespuestaTipoExpediente] = useState(false);
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");

    const [countTextArea, setCountTextArea] = useState(0);

    const [listaTipoDerechoPeticion, setListaDerechoPeticion] = useState({ data: {} });
    const [selectedTipoDerechoPeticion, setSelectedDerechoPeticion] = useState("");
    const [respuestaTipoDerechoPeticion, setRespuestaDerechoPeticion] = useState(false);

    const [listaTipoRespuesta, setListaTipoRespuesta] = useState({ data: {} });
    const [selectedTipoRespuesta, setSelectedTipoRespuesta] = useState("");
    const [respuestaTipoRespuesta, setRespuestaTipoRespuesta] = useState(false);

    const [listaTerminosRespuesta, setListaTerminosRespuesta] = useState({ data: {} });
    const [selectedTerminosRespuesta, setSelectedTerminosRespuesta] = useState("");
    const [respuestaTerminosRespuesta, setRespuestaTerminosRespuesta] = useState(false);

    const [listaTiposQueja, setListaTiposQueja] = useState({ data: {} });
    const [selectedTiposQueja, setSelectedTiposQueja] = useState("");
    const [respuestaTiposQueja, setRespuestaTiposQueja] = useState(false);

    const [fechaTermino, setFechaTermino] = useState(null);
    const [resultDiasNoLaborales, setResultDiasNoLaborales] = useState([]);

    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [getNombreRol, setNombreRol] = useState("");
    const [getAnosAtrasInvalidos, setAnosAtrasInvalidos] = useState(0);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/ClasificacionRadicadoLista/`} state={{ from: from }} />;
    }


    useEffect(() => {
        async function fetchData() {

            //window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            setNombreRol(getUser().roles);

            ParametrosMasApi.getAllTipoRespuesta().then(
                datos => !datos.error ? (setListaTipoRespuesta(datos), setRespuestaTipoRespuesta(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposExpediente().then(
                datos => !datos.error ? (setListaTipoExpediente(datos), setRespuestaTipoExpediente(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposDerechoPeticion().then(
                datos => !datos.error ? (setListaDerechoPeticion(datos), setRespuestaDerechoPeticion(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTerminosRespuesta().then(
                datos => !datos.error ? (setListaTerminosRespuesta(datos), setRespuestaTerminosRespuesta(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposQueja().then(
                datos => !datos.error ? (setListaTiposQueja(datos), setRespuestaTiposQueja(true)) : window.showModal(1)
            )

            setResultDiasNoLaborales([]);
            ParametrosMasApi.getAllDiasNoLaborales().then(
                datos => {
                    if (!datos.error) {
                        var data = [];
                        for (var i in datos.data) {
                            var date = datos.data[i]["attributes"]["fecha"].split(' ')[0];
                            var result = new Date(date);
                            result.setDate(result.getDate() + 1);
                            data.push(i, date);
                        }

                        setResultDiasNoLaborales(data);
                        //console.log(resultDiasNoLaborales);
                    } else {
                        window.showModal(1);
                    }

                }

            )

            //buscamos el parametro
            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "limite_anos_calendar"
                    }
                }
            }
            ParametrosMasApi.getParametroPorNombre(data).then(
                datos => {

                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setAnosAtrasInvalidos(datos["data"][0]["attributes"]["valor"]);
                        }

                    } else {
                        window.showModal(1);
                    }
                }
            )
        }
        fetchData();
    }, []);

    const disableCustomDt = current => {
        return !resultDiasNoLaborales.includes(current.format('YYYY-MM-DD'));
    }



    let selectChangeTipoRespuesta = (e) => {
        setSelectedTipoRespuesta(e);
    }

    let selectChangeTipoExpediente = (e) => {
        setCountTextArea(0)
        setSelectedTipoExpediente(e);
    }

    let selectChangeTipoDerechoPeticion = (e) => {
        setCountTextArea(0)
        setSelectedDerechoPeticion(e);
    }

    let selectChangeTerminoRespuesta = (e) => {
        setSelectedTerminosRespuesta(e);
    }

    let selectChangeTiposQueja = (e) => {
        setSelectedTiposQueja(e);
    }


    //Componetes

    const selectTipoRespuesta = () => {
        return (
            listaTipoRespuesta.data.map((tipo_respuesta, i) => {
                return (
                    <option key={tipo_respuesta.id} value={tipo_respuesta.id}>{tipo_respuesta.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoExpediente = () => {
        return (
            listaTipoExpediente.data.map((expediente, i) => {
                return (
                    <option key={expediente.id} value={expediente.id}>{expediente.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoDerechoPeticion = () => {
        return (
            listaTipoDerechoPeticion.data.map((derecho_peticion, i) => {
                return (
                    <option key={derecho_peticion.id} value={derecho_peticion.id}>{derecho_peticion.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTerminosRespuesta = () => {
        return (
            listaTerminosRespuesta.data.map((termino_respuesta, i) => {
                return (
                    <option key={termino_respuesta.id} value={termino_respuesta.id}>{termino_respuesta.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTiposQueja = () => {
        return (
            listaTiposQueja.data.map((tipo_queja, i) => {
                return (
                    <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
                )
            })
        )
    }


    const alertaPorTipoDerechoPeticion = (i) => {
        return (
            listaTipoDerechoPeticion.data[i - 1].attributes.observacion
        )
    }


    const enviarDatosControlInterno = (datos) => {

        window.showSpinner(true);
        let data;

        data = {
            "data": {
                "type": "clasificacion_radicado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": 1,
                    "id_tipo_expediente": datos.selectedTipoExpediente,
                    "observaciones": null,
                    "id_tipo_queja": datos.tipo_queja,
                    "id_termino_respuesta": 0,
                    "fecha_termino": null,
                    "hora_termino": 0,
                    "gestion_juridica": false,
                    "estado": true,
                    "id_estado_reparto": 2,
                    "id_derecho_peticion": null,
                    "oficina_control_interno": true,
                    "created_user": getNombreUsuario,
                }
            }
        }

        ClasificacionRadicadoApi.addClasificacionRadicado(data).then(
            datos => {
                if (!datos.error) {
                    //console.log(data);
                    from.cambiaColorClasificacionRadicado = "btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left";
                    window.showModal(2);
                    setIsRedirect(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )


    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;


        //TIPO EXPEDIENTE => DERECHO DE PETICION => COPIAS 
        if (selectedTipoExpediente === '1' && selectedTipoDerechoPeticion === '1') {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": selectedTipoDerechoPeticion,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }
        //TIPO EXPEDIENTE => DERECHO DE PETICION => GENERAL     
        else if (selectedTipoExpediente === '1' && selectedTipoDerechoPeticion === '2') {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": (datos.gestion_juridica === 'true'),
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": selectedTipoDerechoPeticion,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }
        //TIPO EXPEDIENTE => DERECHO DE PETICION => DERECHO DE PETICION => ALERTA CONTROL POLITICO
        else if (selectedTipoExpediente === '1' && selectedTipoDerechoPeticion === '3') {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": datos.observaciones,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": (datos.gestion_juridica === 'true'),
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": selectedTipoDerechoPeticion,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => PODER REFERENTE A SOLICITUD
        if (selectedTipoExpediente === '2') {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.tipo_queja,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => QUEJA
        if (selectedTipoExpediente === '3') {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.tipo_queja,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": 0,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => TUTELA => TERMINO RESPUESTA = DIAS
        if (selectedTipoExpediente === '4' && datos.termino_rta === 1) {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": datos.fechaTermino ? datos.fechaTermino : fechaTermino,
                        "hora_termino": 0,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => TUTELA => TERMINO RESPUESTA = HORAS
        else if (selectedTipoExpediente === '4' && datos.termino_rta === 2) {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": 0,
                        "id_termino_respuesta": 0,
                        "fecha_termino": null,
                        "hora_termino": datos.horasTermino,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        ClasificacionRadicadoApi.addClasificacionRadicado(data).then(
            datos => {
                if (!datos.error) {
                    console.log(data);
                    window.showModal(2)
                    setIsRedirect(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }

    // COMPONENTE CONFIRMACION OFICINA DE CONTROL INTERNO DISCIPLINARIO
    const componenteTipoRespuesta = (tipo_respuesta) => {

        // CONFIRMACION OFICINA DE CONTRO INTERNO = SI
        if (tipo_respuesta === 1) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            selectedTipoExpediente: 3,
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            console.log(valores);
                            enviarDatosControlInterno(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {respuestaTiposQueja ? selectTiposQueja() : null}
                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === 2) {
            return (
                <>
                    <div className="form-group">
                        <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                            value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                            <option value="">Por favor seleccione</option>
                            {respuestaTipoExpediente ? selectTipoExpediente() : null}
                        </select>
                    </div>


                    {/*DERECHO DE PETICION*/}
                    {selectedTipoExpediente === '1' ? componenteTipoExpediente(1) : ''}
                    {/*PODER REFERENTE A SOLIICTUD*/}
                    {selectedTipoExpediente === '2' ? componenteTipoExpediente(2) : ''}
                    {/*QUEJA*/}
                    {selectedTipoExpediente === '3' ? componenteTipoExpediente(3) : ''}
                    {/*TUTELA*/}
                    {selectedTipoExpediente === '4' ? componenteTipoExpediente(4) : ''}

                </>
            );
        }
    };

    // COMPOENENTE TIPO EXPEDIENTE
    const componenteTipoExpediente = (tipo_expediente) => {

        // TIPO DE EXPEDIENTE = DERECHO DE PETICION
        if (tipo_expediente === 1) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}

                    <div className="form-group">
                        <label htmlFor="ingresoDerechoPeticion">Tipo de derecho de petición <span className="text-danger">*</span></label>
                        <select className="form-control" id="ingresoDerechoPeticion" name="ingresoDerechoPeticion"
                            value={selectedTipoDerechoPeticion} onChange={e => selectChangeTipoDerechoPeticion(e.target.value)}>
                            <option value="">Por favor seleccione</option>
                            {respuestaTipoDerechoPeticion ? selectTipoDerechoPeticion() : null}
                        </select>
                    </div>

                    {/*COPIAS*/}
                    {selectedTipoDerechoPeticion === '1' ? componenteTipoDerechoPeticion(1) : ''}
                    {/*GENERAL*/}
                    {selectedTipoDerechoPeticion === '2' ? componenteTipoDerechoPeticion(2) : ''}
                    {/*ALERTA CONTROL POLITICO*/}
                    {selectedTipoDerechoPeticion === '3' ? componenteTipoDerechoPeticion(3) : ''}
                </>
            );
        }

        // TIPO DE EXPEDIENTE = PODER REFERENTE A SOLICITUD
        else if (tipo_expediente === 2) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            console.log(valores);
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {respuestaTiposQueja ? selectTiposQueja() : null}
                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        // TIPO DE EXPEDIENTE = QUEJA
        else if (tipo_expediente === 3) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {respuestaTiposQueja ? selectTiposQueja() : null}
                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"> <i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        // TIPO DE EXPEDIENTE = TUTELA
        else if (tipo_expediente === 4) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <label htmlFor="ingresoTipoRtaTutela">Ingrese termino de respuesta <span className="text-danger">*</span></label>

                    <select className="form-control" id="termino_rta" name="termino_rta"
                        value={selectedTerminosRespuesta}
                        onChange={e => selectChangeTerminoRespuesta(e.target.value)}>
                        <option value="{selectedTerminosRespuesta}">Por favor seleccione</option>
                        {respuestaTerminosRespuesta ? selectTerminosRespuesta() : null}
                    </select>

                    {/*DIAS TERMINO*/}
                    {selectedTerminosRespuesta === '1' ? componenteTerminosRespuesta(1) : ''}
                    {/*HORAS TERMINO*/}
                    {selectedTerminosRespuesta === '2' ? componenteTerminosRespuesta(2) : ''}

                </>
            );
        }
    };

    // COMOPNENTE TIPO DERECHO DE PETICION
    const componenteTipoDerechoPeticion = (tipo_derecho_peticion) => {

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  COPIAS
        if (tipo_derecho_peticion === 1) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik

                        initialValues={{
                            observaciones: '',
                            tipo_derecho_peticion: tipo_derecho_peticion,
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            return errores
                        }}


                        onSubmit={(valores, { resetForm }) => {

                            console.log(valores);
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">

                                <div className="alert alert-warning alert-dismissable" role="alert">
                                    <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                    <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                </div>

                                <div className="form-group">
                                    <label>Observaciones</label>
                                    <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                        placeholder="Escriba en este espacio los antencedentes"></Field>
                                    <div className="text-right">
                                        <span className="text-primary">{countTextArea} / 4000</span>
                                    </div>
                                </div>
                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </Form>
                    </Formik>
                </>
            );
        }
        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  GENERAL
        else if (tipo_derecho_peticion === 2) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik

                        initialValues={{
                            observaciones: '',
                            gestion_juridica: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }

                            if (!valores.gestion_juridica) {
                                errores.gestion_juridica = 'Debe diligenciar el campo gestión juridica'
                            }

                            return errores
                        }}
                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>

                            <div className="form-group">

                                <div className="alert alert-warning alert-dismissable" role="alert">
                                    <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                    <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                </div>

                                <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
                                    <option value="">Por favor seleccione</option>
                                    <option value="true">Si</option>
                                    <option value="false">No</option>
                                </Field>

                                <label>Observaciones</label>  <span className="text-danger">*</span>
                                <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                    placeholder="Escriba en este espacio las observaciones"></Field>
                                <div className="text-right">
                                    <span className="text-primary">{countTextArea} / 4000</span>
                                </div>
                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>
                    </Formik>
                </>
            );
        }

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  ALERTA CONTROL POLITICO
        else if (tipo_derecho_peticion === 3) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            observaciones: '',
                            gestion_juridica: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            if (!valores.gestion_juridica) {
                                errores.gestion_juridica = 'Debe diligenciar el campo gestión juridica'
                            }

                            return errores
                        }}
                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>

                            <div className="form-group">

                                <div className="alert alert-warning alert-dismissable" role="alert">
                                    <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                    <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                </div>

                                <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
                                    <option value="">Por favor seleccione</option>
                                    <option value="true">Si</option>
                                    <option value="false">No</option>
                                </Field>


                                <label>Observaciones</label>  <span className="text-danger">*</span>
                                <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                    placeholder="Escriba en este espacio las observaciones"></Field>
                                <div className="text-right">
                                    <span className="text-primary">{countTextArea} / 4000</span>
                                </div>

                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                    <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                    </Link>
                                </div>

                            </div>

                        </Form>

                    </Formik>

                </>
            );
        }

    };

    const handleCallbackFechaTermino = (childData) => {
        try {
            setFechaTermino(childData)
        } catch (error) {

        }

    }

    // COMPONENTE TERMINOS DE RESPUESTA -- TIPO EXPEDIENTE TUTELA
    const componenteTerminosRespuesta = (termino_respuesta) => {

        //TERMINO TUTELA = DIAS      
        if (termino_respuesta === 1) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            termino_rta: termino_respuesta,
                            horasTermino: '',
                            fechaTermino: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.fechaTermino && !fechaTermino) {
                                errores.fechaTermino = 'Debe seleccionar el termino de respuesta'
                            }

                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label>Horas para termino</label>  <span className="text-danger">*</span>

                                {/*<DatePicker id="fechaTermino" locale='es' name="fechaTermino" dateFormat="DD/MM/YYYY" closeOnSelect={true} placeholder="dd/mm/yyyy" onChange={(date) => setFechaTermino(date)} timeFormat={false} isValidDate={disableCustomDt} />*/}
                                <DatePerson resultDiasNoLaborales={resultDiasNoLaborales} getAnosAtrasInvalidos={getAnosAtrasInvalidos} parentCallback={handleCallbackFechaTermino} id="fechaTermino" name="fechaTermino" />
                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>
                    </Formik>
                </>

            )
        }

        //TERMINO TUTELA = HORAS     
        else if (termino_respuesta === 2) {

            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            termino_rta: termino_respuesta,
                            horasTermino: '',
                            fechaTermino: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.horasTermino) {
                                errores.horasTermino = 'Debe seleccionar el termino de respuesta'
                            }

                            return errores
                        }}


                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label>Horas para termino</label>  <span className="text-danger">*</span>
                                <Field type="text" id="horasTermino" name="horasTermino" className="form-control" placeholder="" />
                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                            </div>

                            <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                            </Link>

                        </Form>
                    </Formik>
                </>
            )

        }

    };

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ClasificacionRadicadoLista/`} state={{ from: from }}><small>Clasificacion del radicado Lista</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Nueva clasificacion del radicado</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                {}

                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title"> SINPROC No {radicado} :: Clasificación del Radicado</h3>
                            <h2> {getNombreRol}</h2>
                        </div>

                        {getNombreRol != 'Control interno disciplinario' &&
                        (

                            <div className="block-content">
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="form-group">
                                    <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                            value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                                            <option value="">Por favor seleccione</option>
                                            {respuestaTipoExpediente ? selectTipoExpediente() : null}
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/*DERECHO DE PETICION*/}
                            {selectedTipoExpediente === '1' ? componenteTipoExpediente(1) : ''}
                            {/*PODER REFERENTE A SOLIICTUD*/}
                            {selectedTipoExpediente === '2' ? componenteTipoExpediente(2) : ''}
                            {/*QUEJA*/}
                            {selectedTipoExpediente === '3' ? componenteTipoExpediente(3) : ''}
                            {/*TUTELA*/}
                            {selectedTipoExpediente === '4' ? componenteTipoExpediente(4) : ''}
      
                        </div>
                        )
                        }

                        {getNombreRol != 'Control interno disciplinario' &&
                        (
                            <div></div>
                        )}





                        <div className="block-content">
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="form-group">
                                    <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                            value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                                            <option value="">Por favor seleccione</option>
                                            {respuestaTipoExpediente ? selectTipoExpediente() : null}
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/*DERECHO DE PETICION*/}
                            {selectedTipoExpediente === '1' ? componenteTipoExpediente(1) : ''}
                            {/*PODER REFERENTE A SOLIICTUD*/}
                            {selectedTipoExpediente === '2' ? componenteTipoExpediente(2) : ''}
                            {/*QUEJA*/}
                            {selectedTipoExpediente === '3' ? componenteTipoExpediente(3) : ''}
                            {/*TUTELA*/}
                            {selectedTipoExpediente === '4' ? componenteTipoExpediente(4) : ''}
      
                        </div>
                    </div>
                </div>
            </div>

        </>
    );

}
export default ClasificacionRadicadoForm;
