import React, { useEffect , useState } from "react";
import Spinner from '../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser , quitarAcentos } from '../Utils/Common';
import GenericApi from '../Api/Services/GenericApi';
import '../Utils/Constants';
import ModalGen from '../Utils/Modals/ModalGeneric';
import InfoErrorApi from '../Utils/InfoErrorApi';
import DataTable from 'react-data-table-component';
import { Field, Form, Formik } from 'formik';

function DocumentosFirmadosOPendientesDeFirma() {

    const [getListaFirmasMecanicas, setListaFirmasMecanicas] = useState({ data: [], links: [], meta: [] }); 
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [errorApi, setErrorApi] = useState('');
    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);

    useEffect(() => {
        async function fetchData() {
           window.showSpinner(true);
           
           GenericApi.getGeneric('usuario/get-usuario-por-name/' + getUser().nombre).then(
                datos => {
                    if (!datos.error) {
                        dataFirmasMecanica(datos.data[0].id);
                    } else {

                    }
                }
            )
            
        }
        fetchData();
    }, []);
    

    const dataFirmasMecanica = (idUser) => {
        GenericApi.getGeneric("actuaciones/get-firmas-por-usuario/" + idUser).then(
            datos => {
    
                if (!datos.error) {
                    if (datos.data.length > 0) {
                        setListaFirmasMecanicas(datos);
                    }
                }
                window.showSpinner(false);
            }
        )
    }

    const handleDownloadArchivo = (uuid_actuacion) => {
        try {
            window.showSpinner(true);
             // Buscamos el detalle de los documentos
            GenericApi.getGeneric("archivo-actuaciones/get-archivo-actuaciones-by-uuid/" + uuid_actuacion).then(
                datos => {
                    if (!datos.error) {
                        if (datos.data.length > 0) {
                            handleDownloadArchivo2(datos.data[0]);
                        }
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    }

    const handleDownloadArchivo2 = (getArchivosListaSearch) => {
        try {
            GenericApi.getGeneric("archivo-actuaciones/get-documento/" + getArchivosListaSearch.attributes.id + "/" + getArchivosListaSearch.attributes.extension).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, getArchivosListaSearch.attributes.nombre_archivo);
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            console.error(error);
        }
    }

    function downloadBase64File(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const columns = [
        {
            name: 'Proceso disciplinario',
            cell: documento =>
                <div>
                    <strong>Radicado: </strong>{documento.attributes.proceso_disciplinario.radicado}<br/>
                    <strong>Vigencia: </strong>{documento.attributes.proceso_disciplinario.vigencia}
                </div>
            ,
            selector: documento => documento.attributes.proceso_disciplinario.radicado,
            sortable: true,
        },
        {
            name: 'Solicitado Por',

            cell: documento => 
                <div>
                    <strong>Fecha: </strong>{documento.attributes.created_at}<br/>
                    <strong>Usuario: </strong>{documento.attributes.id_user.nombre + " " + documento.attributes.id_user.apellido + " ("+ documento.attributes.id_user.name +")"}
                </div>
            ,
            selector: documento => documento.attributes.id_user.nombre,
            sortable: true,
        },
        {
            name: 'Documento',
            cell: documento =>
                <button type='button' title={documento.attributes.id_actuacion? documento.attributes.id_actuacion.documento_ruta.slice(19) : null} className='btn btn-sm btn-primary' onClick={() => handleDownloadArchivo(documento.attributes.id_actuacion.uuid)}>Descargar documento <i className="fas fa-download"></i> </button>          
            ,
            sortable: true,
        },
        {
            name: 'Estado',
            cell: documento => 
                <div>
                    {documento.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.PENDIENTE_FIRMA ? "Pendiente firma" : null}
                    {documento.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.FIRMADO ? "Firmado" : null}
                </div>
            ,
            selector: documento => (documento.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.PENDIENTE_FIRMA ? 
                "Pendiente firma" 
                : 
                documento.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.FIRMADO ?
                "Firmado" 
                : 
                null
                
            ),
            sortable: true,
        },
        {
            name: 'Acciones',
            width: "15%",
            cell: documento => (
                documento.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.PENDIENTE_FIRMA ? 
                (
                    documento.attributes.id_user.firma_mecanica != null ? 
                    (
                        <Link className="btn btn-primary" to="/FirmaActuaciones" state={{FirmaActuacion: documento }}>
                            <span className="nav-main-link-name">Firmar</span>
                        </Link>
                    ) : 
                    (
                        <button type="button" className="btn btn-rounded btn-primary" onClick={() => setModalState({ title: "Firmar Actuacion", message: "Falta agregar firma mecanica en su perfil", show: true, redirect: "/perfil/", alert: global.Constants.TIPO_ALERTA.ERROR })}>Firmar</button>
                        
                    )
                ) : null)
        }
    ];

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            {<InfoErrorApi error={errorApi} />}
            <Formik>
                <Form>
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li className="breadcrumb-item"><Link underline="hover" className="text-dark" to={`/Perfil`}><small>Perfil</small></Link></li>
                                <li className="breadcrumb-item"><small>Documentos firmados y/o pendientes de firma</small></li>
                            </ol>
                        </nav>
                    </div>
                    <div className="row">
                        <div className="col-md-12"> 
                            <div className="block block-themed">
                                <div className="block-header">
                                    <h3 className="block-title">Documentos firmados o pendientes de firma</h3>
                                </div>
                                <div className="block-content">
                                    <div className="row">
                                        <div className='col-md-3'>
                                            <div className="form-group ">
                                                <Field type="text" id="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} name="search" className="form-control border border-success" placeholder="Buscar" />
                                            </div>
                                        </div>
                                        <div className='col-md-9 text-right'>
                                            <Link to={`/Perfil/`} title='Regresar al perfil'>
                                                <button type="button" className="btn btn-success"><i class="fas fa-backward"></i></button>
                                            </Link>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className='col-md-12 mt-2 mb-2'>
                                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                columns={columns}
                                                data={getListaFirmasMecanicas.data.filter((suggestion) => {
                                                    console.log(getListaFirmasMecanicas);
                                                    if (getSeach === "") {
                                                        return suggestion;
                                                    } else if (

                                                        ((quitarAcentos(suggestion.attributes.proceso_disciplinario.radicado)
                                                        + quitarAcentos(suggestion.attributes.proceso_disciplinario.vigencia) 
                                                        + quitarAcentos(suggestion.attributes.created_at) 
                                                        + quitarAcentos(suggestion.attributes.id_user.nombre) 
                                                        + quitarAcentos(suggestion.attributes.id_user.apellido) 
                                                        + quitarAcentos(suggestion.attributes.id_user.name) 
                                                        + (suggestion.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.PENDIENTE_FIRMA ? "Pendiente firma" : suggestion.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.FIRMADO ? "Firmado" : null)
                                                        
                                                        ).toLowerCase().includes(getSeach.toLowerCase()))

                                                    ) {
                                                        return suggestion;
                                                    }
                                                })}
                                                perPage={perPage}
                                                page={pageActual}
                                                pagination
                                                noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                                paginationTotalRows={getListaFirmasMecanicas.data.length}
                                                onChangePage={handlePageChange}
                                                onChangeRowsPerPage={handlePerRowsChange}
                                                striped
                                                paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>    
                    </div>
                </Form>
            </Formik>
        </>
    )

}

export default DocumentosFirmadosOPendientesDeFirma;
