import React, { useEffect, useState } from 'react';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { Link } from "react-router-dom";
import GenericApi from '../../Api/Services/GenericApi';
import { useLocation } from 'react-router-dom';
import { getUser } from '../../Utils/Common';
import DatePerson from "../../DatePerson/DatePerson";
import 'react-datetime/css/react-datetime.css';
import DatePicker from 'react-datetime';
import moment from 'moment';

function SeleccionDeFechaParaSemaforo() {

    const [getFechaInicioSemaforo, setFechaInicioSemaforo] = useState();
    const [getSemaforo, setSemaforo] = useState();
    const [getListaSemaforos, setListaSemaforos] = useState({ data: [] });
    const [getRespuestaSemaforo, setRespuestaSemaforo] = useState(false);
    

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const location = useLocation();
    const { from , selected_id_etapa , id, nombre, estadoActualActuacion, tipoActuacion} = location.state;

    useEffect(() => {
        async function fetchData() {
            getSemaforos();
        }
        fetchData();
    }, []);

    const enviarDatos = (valores) => {
        const userInput = moment(getFechaInicioSemaforo).format("DD-MM-YY");
        GenericApi.getByIdGeneric('existe-semaforo-con-fecha', getSemaforo).then(
            datos => {
                if (!datos.error) {
                    let data = {
                        "data": {
                            "type": "pdxsemaforo",
                            "attributes": {
                                "id_semaforo": getSemaforo,
                                "id_proceso_disciplinario": from.procesoDisciplinarioId,
                                "id_actuacion": id,
                                "fecha_inicio": userInput,
                                "estado": 1,
                            }
                        }
                    }
                    if(datos.data.length >= 1){
                        GenericApi.updateGeneric('pdxsemaforo', datos.data[0].id ,data).then(
                            datosSemaforo => {
                                if (!datosSemaforo.error) {
                                    setModalState({ title: "Semáforo :: Selección de fecha", message: 'Fecha seleccionada actualizada con éxito ', show: true, redirect: "/ActuacionesVer/" + from.procesoDisciplinarioId + "/" + selected_id_etapa + "/1", from: { from: from, id: id, nombre: nombre, selected_id_etapa: selected_id_etapa, id_actuacion: id  } ,alert: global.Constants.TIPO_ALERTA.EXITO });
                                }else{
                                    setModalState({ title: "Semáforo :: Selección de fecha", message: datosSemaforo.error.toString(), show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                                }
                            }
                        )
                    }else{
                        GenericApi.addGeneric('pdxsemaforo', data).then(
                            datosSemaforo => {
                                if (!datosSemaforo.error) {
                                    setModalState({ title: "Semáforo :: Selección de fecha", message: 'Fecha seleccionada con éxito ', show: true, redirect: "/ActuacionesVer/" + from.procesoDisciplinarioId + "/" + selected_id_etapa + "/1", from: { from: from, id: id, nombre: nombre, selected_id_etapa: selected_id_etapa, id_actuacion: id  } ,alert: global.Constants.TIPO_ALERTA.EXITO });
                                }else{
                                    setModalState({ title: "Semáforo :: Selección de fecha", message: datosSemaforo.error.toString(), show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                                }
                            }
                        )
                    }
                }else{
                    console.log(datos.error);
                }
            }
        )
    }

    const changeSemaforo = (e) => {
        setSemaforo(e.target.value);
    }

    const selectSemaforo = () => {
        return (
            getListaSemaforos.data.map((evento, i) => {
                return (
                    <option key={evento.id} value={evento.id}>{evento.attributes.nombre}</option>
                )
            })
        )
    }
    
    const getSemaforos= () => {
        GenericApi.getAllGeneric('semaforo').then(
            datos =>{
                if (!datos.error) {
                    datos.data.forEach(element => {
                        if(element.attributes.id_mas_evento_inicio.id == 3){
                            getListaSemaforos.data.push(element);
                            setRespuestaSemaforo(true);
                        }
                    });
                    
                }else{
                    setModalState({ title: "Semáforo :: Ocurrio un error", message: datos.error.toString(), show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }

            } 
        )
    }
    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    informacion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {};

                    return errores;
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <small>Selección de fecha para semáforo</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {from.radicado} <strong> VIGENCIA: {from.vigencia} :: Selección de fecha para semaforo </strong></h3>
                            </div>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="Semaforo">Semáforo</label>.
                                            <Field as="select" value={getSemaforo} onChange={changeSemaforo} className="form-control" id="Semaforo" name="Semaforo" placeholder="Semáforo">
                                                <option value="">Por favor seleccione</option>
                                                {getRespuestaSemaforo ? selectSemaforo() : null}
                                            </Field>
                                            <ErrorMessage name="Semaforo" component={() => (<span className="text-danger">{errors.Semaforo}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor='fechaInicioSemaforo'>Fecha inicio semáforo<span className="text-danger">*</span></label>
                                            <DatePicker id="fechaInicioSemaforo" locale='es' name="fechaInicioSemaforo" dateFormat="DD/MM/YYYY" closeOnSelect={true} placeholder="dd/mm/yyyy" onChange={(date) => setFechaInicioSemaforo(date)} timeFormat={false}  />
                                            
                                            <ErrorMessage name="fechaInicioSemaforo" component={() => (<span className="text-danger">{errors.fechaInicioSemaforo}</span>)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-rounded btn-primary" >Aceptar</button>
                                    <Link to={"/ActuacionesVer/" + from.procesoDisciplinarioId + "/" + selected_id_etapa + "/1"} state={{ from: from , selected_id_etapa: selected_id_etapa , id: id , nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion }} className="font-size-h5 font-w600" >
                                        <button type="button" className="btn btn-rounded btn-outline-primary" >Cancelar</button>
                                    </Link>
                                </div>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>


    );

}

export default SeleccionDeFechaParaSemaforo;
