import { Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { getUser, hasAccess } from '../../../components/Utils/Common';
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import InfoConfirmarAccion from '../../Utils/InfoConfirmarAccion';
import ModalActuaciones from '../../Utils/Modals/ModalActuaciones';
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import DataTable from 'react-data-table-component';
import moment from 'moment';
import ModalItemsVer from '../../Utils/Modals/ModalItemsVer';

function ActuacionesVer() {

    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [errorApi, setErrorApi] = useState('');
    const location = useLocation()
    const { from, selected_id_etapa, id, nombre, estadoActualActuacion, tipoActuacion, disable, actuacionIdMaestra } = location.state;

    let radicado = from.radicado;

    const [getidMasActuacion, setidMasActuacion] = useState();
    const [getTrazabilidadListaSearch, setTrazabilidadListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getArchivosListaSearch, setArchivosListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoTrazabilidadActuaciones, setRtaInfoTrazabilidadActuaciones] = useState(false);
    const [getRtaInfoArchivoActuaciones, setRtaInfoArchivoActuaciones] = useState(false);
    const [getRtaInfoTrue, setRtaInfoTrue] = useState(false);
    const [getPesoTotalArchivos, setPesoTotalArchivos] = useState(0);
    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [inputListArchivos, setInputListArchivos] = useState([{ archivo: {}, filebase64: "", size: 0, ext: "" }]);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [inputActualizarArchivos, setInputActualizarArchivos] = useState([{ archivo: {}, filebase64: "", size: 0, ext: "", uuid: "" }]);
    const [getValidarAccion, setValidarAccion] = useState(0);
    const [getRtaJefe, setRtaJefe] = useState(false);
    const [getInfoModal, setInfoModal] = useState(false);
    const [getMessageModal, setMessageModal] = useState('');
    const [getNameUsuario, setNameUsuario] = useState("");
    const [getUltimoEstado, setUltimoEstado] = useState("");
    const [getUsuarioSolicitante, setUsuarioSolicitante] = useState("");
    const [getTipoDocumentoSubir, setTipoDocumentoSubir] = useState("");
    const [getFirmasMecanicas, setFirmasMecanicas] = useState(false);
    const [getListaFirmasMecanicas, setListaFirmasMecanicas] = useState({ data: [], links: [], meta: [] });
    const [getActuacionPendienteDeFirmas, setActuacionPendienteDeFirmas] = useState(false);
    const [getActuacionEstaAprobadaSioNo, setActuacionEstaAprobadaSioNo] = useState(false);
    const [getValidarUsuariosPendienteFirma, setValidarUsuariosPendienteFirma] = useState(false);
    const [getMuestraBotonFechaParaSemaforo, setMuestraBotonFechaParaSemaforo] = useState(false);
    const [getNombreProceso, setNombreProceso] = useState('');
    const [getListaSemaforos, setListaSemaforos] = useState({ data: [] });
    const [getRtaListaSemaforos, setRtaListaSemaforos] = useState(false);
    const [getEstadoActuacion, setEstadoActuacion] = useState(false);

    const [getUsuarioNombre, setUsuarioNombre] = useState("");
    const [getUsuarioApellido, setUsuarioApellido] = useState("");
    const [getUsuarioName, setUsuarioName] = useState("");
    const [getUsuarioDependenciaNombre, setUsuarioDependenciaNombre] = useState("");

    // Constantes de campos adicionales    
    const [getRespuestaCampos, setRespuestaCampos] = useState(false);
    const [getDataCamposAdicionales, setDataCamposAdicionales] = useState({ data: [] });
    const [getModalStateItems, setModalStateItems] = useState({ title: "", message: "", show: false, data: {} });

    // Constante para validar si el usuario pertenece a la misma dependencia de la actuacion 
    const [getDependenciaActuacionCreada, setDependenciaActuacionCreada] = useState(false);

    useEffect(() => {
        async function fetchData(params) {
            window.showSpinner(true);
            IdDependenciaAceptaSolicitudesDeAnulacion();
            setNombreUsuario(getUser().nombre);

            nombreProceso();

            console.log("id maestra   -> ", actuacionIdMaestra);
            console.log("id parametro -> ", id);
            console.log("from -> ", estadoActualActuacion);

            // Se setea el id de la maestra de actuacion en el from
            if (actuacionIdMaestra != "") {

                // Se setea en el from
                from.actuacionIdMaestra = actuacionIdMaestra;
            }

            // Se setea el id de la actuaci√≥n
            setidMasActuacion(actuacionIdMaestra);

            // Se inicializa la api de los semaforos
            GenericApi.getGeneric('semaforo').then(

                // Se inicializa la variable de respuesta
                datosSemaforo => {

                    // Se valida que no haya error
                    if (!datosSemaforo.error) {

                        // Se recorre la informacion
                        datosSemaforo.data.forEach(element => {

                            // Se valida que el ic de la actuacion inicial sea diferente de null
                            if (element.attributes.id_mas_actuacion_inicia != null) {

                                // Se valida que el id de la actuacion inicial sea igual a la actuacion actual
                                if (element.attributes.id_mas_actuacion_inicia.id == actuacionIdMaestra
                                    && element?.attributes?.id_mas_evento_inicio?.id == 3
                                ) {

                                    // Se setea en true
                                    setMuestraBotonFechaParaSemaforo(true);
                                }
                            }
                        });

                        // Se quita el cargando
                        window.showSpinner(false);
                    }
                }
            )

            // Buscamos el detalle de los documentos
            GenericApi.getGeneric("archivo-actuaciones/get-archivo-actuaciones-by-uuid/" + id).then(

                // Se inicializa la variable de respuesta
                datos => {

                    // Se valida que no haya error
                    if (!datos.error) {

                        // Se valida que tenga informacion
                        if (datos.data.length > 0) {

                            // Se setean los valores
                            setArchivosListaSearch(datos);
                            setRtaInfoArchivoActuaciones(true);
                        }
                    }
                }
            )

            // Buscamos el detalle de la trazabilidad
            GenericApi.getGeneric("trazabilidad-actuaciones/get-all-trazabilidad-actuaciones-uuid/" + id).then(

                // Se inicializa la variable de respuesta
                datos => {

                    // Se valida que no haya error
                    if (!datos.error) {

                        // Se valida que haya informacion
                        if (datos.data.length > 0) {

                            // Se setean los valores
                            setTrazabilidadListaSearch(datos);
                            setRtaInfoTrazabilidadActuaciones(true);
                        }
                    }
                }
            )

            // Buscamos el detalle de la actuacion
            GenericApi.getGeneric("actuaciones/" + id).then(

                // Se inicializa la variable de respuesta
                datos => {

                    // Se valida que no haya error
                    if (!datos.error) {

                        // Se inicializa la variable
                        let encuentraCampos = datos.data.attributes.campos_finales.length > 0 ? true : false;

                        // Se captura el estado de la actuacion
                        let estado = datos.data.attributes.estado == "1" ? true : false;

                        // Se setea el estado a la variable general
                        setEstadoActuacion(estado);

                        // Se valida cuando hay campos adicionales
                        if (encuentraCampos) {

                            // Se setea el valor en true
                            setRespuestaCampos(true);
                            setDataCamposAdicionales(datos.data.attributes.campos_finales);
                        }

                        // Se inicializa la variable
                        let dependenciaEncontrada = datos.data.attributes.id_dependencia ? true : false;

                        // Se valida cuando hay campos adicionales
                        if (dependenciaEncontrada && (datos.data.attributes.id_dependencia.id == getUser().id_dependencia)) {

                            // Se setea el valor en true
                            setDependenciaActuacionCreada(true);
                        }
                    }
                }
            )

            jefeDependencia();
            dataFirmasMecanica();
            ListaSemaforos();
            // validarFirmadoDocumentoTodosUsuarios
        }
        fetchData()
    }, []);

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }

    const ListaSemaforos = () => {
        GenericApi.getByIdGeneric('getDiasTranscurridos/actuacion', id).then(
            datosDiasTranscurridos => {
                if (!datosDiasTranscurridos.error) {
                    console.log(datosDiasTranscurridos);
                    datosDiasTranscurridos.data.forEach(element => {
                        if (SemaforoColor(element) == "Azul") {
                            GenericApi.getGeneric('set-finaliza-asemaforo/' + element.attributes.actuacionxsemaforo.attributes.id_semaforo.id + "/" + id)
                        }
                    });

                    setListaSemaforos(datosDiasTranscurridos);
                }
                window.showSpinner(false);
            }
        )
    }

    const nombreProceso = () => {
        GenericApi.getByIdGeneric("nombre-proceso", from.procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    setNombreProceso(datos.data.attributes.nombre);
                }
            }
        )
    }

    // Metodo encargado de validar si el documento se ha firmado por todos los usuarios
    const validarFirmadoDocumentoTodosUsuarios = () => {

        // Buscamos el detalle de la trazabilidad
        GenericApi.getGeneric("mas_actuaciones/validarUsuarioPendienteFirma/" + id).then(

            // Se inicializa la variable de respuesta
            datos => {

                // Se valida cuando es true
                if (datos == 1) {

                    // Se setea el valor
                    setValidarUsuariosPendienteFirma(true);
                }
            }
        )
    }

    // Se consulta si el usuario que se encuentra en sesi√≥n es jefe dependencia
    const jefeDependencia = () => {

        const data = {
            "data": {
                "type": "usuario",
                "attributes": {
                    "": ""
                }
            }
        }
        GenericApi.getByDataGeneric("usuario/get-jefe-dependencia", data).then(
            datos => {
                if (!datos.error) {
                    setRtaJefe(true);
                } else {
                    setRtaJefe(false);
                }
            }
        )
    }

    const handleClicArchivo = (id_documento, extension, nombre_documento) => {

        try {
            window.showSpinner(true);

            // Se genera la peticion para descargar el archivo
            GenericApi.getGeneric("archivo-actuaciones/get-documento/" + id_documento + "/" + extension).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento);
                    } else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    const handleClicArchivoManual = () => {
        try {
            window.showSpinner(true);

            const data = {
                "data": {
                    "type": "mas_actuaciones",
                    "attributes": {
                        "nombre_actuacion": "",
                        "nombre_plantilla": "",
                        "id_etapa": "",
                        "estado": "",
                        "id_etapa_despues_aprobacion": "",
                        "despues_aprobacion_listar_actuacion": "",
                        "nombre_plantilla_manual": "",
                        "fileBase64": "",
                        "fileBase64_manual": ""
                    }
                }
            }

            GenericApi.getByDataGeneric('mas_actuaciones/manual/' + getidMasActuacion, data).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64FileManual(datos.content_type, datos.base_64, datos.file_name);
                    }
                    else {

                    }
                    window.showSpinner(false);
                }
            )

        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64FileManual(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    function downloadBase64File(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const infoTrazabilidad = () => {
        setUltimoEstado(getTrazabilidadListaSearch.data[0].attributes.nombre_estado_actuacion);
        setUsuarioSolicitante(getTrazabilidadListaSearch.data[0].attributes.created_user);
        return (getTrazabilidadListaSearch.data.map((t) => {

            if (t.attributes.nombre_estado_actuacion == "Aprobada") {
                setActuacionEstaAprobadaSioNo(true);
            }

            let nombreDependencia = t.attributes.id_dependencia == "" ? "" : " (" + t.attributes.id_dependencia.nombre + ")";

            return (
                <tr key={t.attributes.id}>
                    <td>
                        {t.attributes.created_at.toUpperCase()}
                    </td>
                    <td>
                        {t.attributes.usuario.nombre.toUpperCase() + " " + t.attributes.usuario.apellido.toUpperCase() + nombreDependencia.toUpperCase()}
                    </td>
                    <td>
                        {t.attributes.updated_user.toUpperCase()}
                    </td>
                    <td>
                        {t.attributes.nombre_estado_actuacion.toUpperCase()}
                    </td>
                    <td>
                        {t.attributes.observacion.toUpperCase()}
                    </td>
                </tr>
            )
        }))
    }

    const infoArchivos = () => {
        let index = 0;
        return (getArchivosListaSearch.data.map((t) => {
            return (
                <tr key={t.attributes.id}>
                    <td>
                        {t.attributes.nombre_tipo_archivo.toUpperCase()}
                    </td>
                    <td>
                        {t.attributes.nombre_archivo.toUpperCase()}
                    </td>
                    <td>
                        <div className='col-3'>
                            <button type='button' title='DESCARGAR DOCUMENTO' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(t.attributes.id, t.attributes.extension, t.attributes.nombre_archivo)}><i className="fas fa-download"></i></button>
                        </div>
                    </td>
                    <td style={{ width: '35%' }}>
                        <div className='col-12'>
                            {(getUltimoEstado == "Aprobada" ||
                                getUltimoEstado == "Aprobada y pdf definitivo" ||
                                getUltimoEstado == "Remitida" ||
                                getUltimoEstado != "Documento firmado") ?
                                <div className="col-md-12">
                                    <label >{tipoActuacion == "0" ? "LA ACTUACI√ìN" : (tipoActuacion == "1" ? 'EL IMPEDIMENTO' : 'EL COMISORIO')} NO SE PUEDE ACTUALIZAR.</label>
                                </div> :
                                disable != true && getEstadoActuacion ? (
                                    inputListArchivos.map((x, i) => {
                                        let indice = index++;
                                        return (
                                            <div className="col-md-12" key={i}>
                                                <label className="custom-file-label" htmlFor="example-file-input-custom" name="input-file" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                <input className="custom-file-input" data-toggle="custom-file-input" type="file" accept='.docx' name="archivo" onChange={e => handleInputChangeArchivos(e, i, 1, t.attributes.id, t.attributes.uuid_actuacion, t.attributes.nombre_tipo_archivo, indice)} required />
                                                <label>PESO DEL ARCHIVO: {formatBytes(x.archivo.size)}</label>
                                            </div>
                                        );
                                    })
                                ) : null
                            }
                        </div>
                    </td>
                </tr >
            )
        }))
    }

    const handleInputChangeArchivos = (e, index, actualizar = 0, id = 0, uuid_actuacion = 0, tipo_archivo = "", valorTabla = "") => {

        const { name, files } = e.target;
        setTipoDocumentoSubir(valorTabla);

        if (files.length > 0) {
            const extension = getFileExtension(files[0].name);

            var error = 0;

            if (tipo_archivo == "Documento inicial" && actualizar == 1 && extension != global.Constants.TIPO_DOCUMENTO_PERMITIDO_ACTUACIONES.DOCX) {
                setErrorApi("El archivo seleccionado no tiene un formato permitido debe ser .docx");
                window.showModal(1);
                error = 1;
                return false;
            } else if (tipo_archivo == "Documento definitivo" && actualizar == 1 && extension != global.Constants.TIPO_DOCUMENTO_PERMITIDO_ACTUACIONES.PDF) {
                setErrorApi("El archivo seleccionado no tiene un formato permitido, debe ser .pdf");
                window.showModal(1);
                error = 1;
                return false;
            } else if (tipo_archivo == "Documento definitivo" && actualizar == 0 && extension != global.Constants.TIPO_DOCUMENTO_PERMITIDO_ACTUACIONES.PDF) {
                setErrorApi("El archivo seleccionado no tiene un formato permitido, debe ser .pdf");
                window.showModal(1);
                error = 1;
                return false;
            }

            if (error == 0) {

                if (actualizar == 0) {
                    var list = [...inputListArchivos];
                    list[index][name] = files[0];
                } else {
                    var list = [...inputActualizarArchivos];
                    list[index][name] = files[0];
                    setValidarAccion(1);
                }

                if (files[0]) {
                    list[index][name] = files[0];
                } else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].ext = "";
                    list[index].uuid = "";
                    if (actualizar == 0) {
                        setInputListArchivos(list);
                    } else {
                        setInputActualizarArchivos(list);
                    }
                    return false;
                }

                // Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].ext = extension;
                        list[index].uuid = id;
                        if (actualizar == 0) {
                            setInputListArchivos(list);
                        } else {
                            list[index].uuid_actuacion = uuid_actuacion;
                            setInputActualizarArchivos(list);
                            setValidarAccion(1);
                        }
                        setRtaInfoTrue(true);
                        window.showModal(9)
                    }
                })
                obtenerPesoTotalArchivos(list);
            } else {
                if (actualizar == 0) {
                    var list = [...inputListArchivos];
                } else {
                    var list = [...inputActualizarArchivos];
                }
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                list[index].uuid = "";
                if (actualizar == 0) {
                    setInputListArchivos(list);
                } else {
                    list[index].uuid_actuacion = uuid_actuacion;
                    setInputActualizarArchivos(list);
                }
            }
        } else {
            if (actualizar == 0) {
                var list = [...inputListArchivos];
            } else {
                var list = [...inputActualizarArchivos];
            }
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            list[index].uuid = "";
            if (actualizar == 0) {
                setInputListArchivos(list);
            } else {
                setInputActualizarArchivos(list);
            }
            obtenerPesoTotalArchivos(list);
            return false;
        }
    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function obtenerPesoTotalArchivos(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if (archivo.archivo.size) {
                    peso += archivo.archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setErrorApi('El peso/tama√±o de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tama√±o y elimine algunos adjuntos.')
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    }

    function subirArchivo(e, accion) {

        if (e.type == "click") {

            window.showSpinner(true);

            if (accion == 0) {
                const data = {
                    "data": {
                        "type": 'archivo-actuaciones/up-documento',
                        "attributes": {
                            "uuid_actuacion": getArchivosListaSearch.data[0].attributes.uuid_actuacion,
                            "fileBase64": inputListArchivos[0].filebase64,
                            "nombre_archivo": inputListArchivos[0].archivo.name,
                            "extension": inputListArchivos[0].ext,
                            "peso": inputListArchivos[0].archivo.size,
                            "id_tipo_archivo": 2
                        }
                    }
                }

                GenericApi.addGeneric("archivo-actuaciones/up-documento", data).then(
                    datos => {

                        if (!datos.error) {
                            setModalState({ title: getNombreProceso.toUpperCase() + " :: CREAR ACTUACIONES", message: global.Constants.MENSAJES_MODAL.EXITOSO, show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.EXITO });
                        } else {
                            setModalState({ title: getNombreProceso.toUpperCase() + " :: CREAR ACTUACIONES", message: datos.error.toString(), show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                        }
                        window.showSpinner(false);
                    }
                )
            } else if (accion == 1) {

                const data = {
                    "data": {
                        "type": 'archivo-actuaciones/update-documento',
                        "attributes": {
                            "uuid_actuacion": inputActualizarArchivos[0].uuid,
                            "fileBase64": inputActualizarArchivos[0].filebase64,
                            "nombre_archivo": inputActualizarArchivos[0].archivo.name,
                            "extension": inputActualizarArchivos[0].ext,
                            "peso": inputActualizarArchivos[0].archivo.size,
                            "id_tipo_archivo": 2,
                            "tipoDocumentoActualizar": getTipoDocumentoSubir
                        }
                    }
                }

                GenericApi.getByDataGeneric("archivo-actuaciones/update-documento/" + inputActualizarArchivos[0].uuid + "/" + inputActualizarArchivos[0].uuid_actuacion, data).then(
                    datos => {

                        if (!datos.error) {
                            setModalState({ title: getNombreProceso.toUpperCase() + " :: CREAR ACTUACIONES", message: global.Constants.MENSAJES_MODAL.EXITOSO, show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.EXITO });
                        } else {
                            setModalState({ title: getNombreProceso.toUpperCase() + " :: CREAR ACTUACIONES", message: datos.error.toString(), show: true, redirect: "/ActuacionesLista/", from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                        }
                        window.showSpinner(false);
                    }
                )
            }


        }
    }

    const IdDependenciaAceptaSolicitudesDeAnulacion = () => {
        const data = {
