    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        data = {
            "data": {
                "type": 'migrar-actuaciones',
                "attributes": {
                    "nombre": datos.nombre,
                    "tipo": datos.tipo,
                    "autonumero":  datos.autoNumero,
                    "fecha":  datos.fecha,
                    "fechatermino":  datos.fechaTermino,
                    "instancia":  datos.instancia,
                    "decision":  datos.decision,
                    "terminomonto":  datos.terminoMonto,
                    "observacion":  datos.observacion,
                    "radicado":  datos.radicado,
                    "vigencia":  datos.vigencia,
                    "item":  datos.item,
                    "documentoData": datos.documentoData,
                    "documentoBase64": datos.documentoBase64
                }
            }
        }

        GenericApi.addGeneric("migrar-actuaciones",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "Proceso No "+radicado+" :: Migrar Actuación", message: 'Registro Exitoso', show: true, redirect: `/CargarMigracionActuacion/${radicado}/${vigencia}/${item}`, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
