import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useEffect, useState } from 'react';
import { Navigate, useParams } from "react-router";
import { Link, useLocation } from 'react-router-dom';
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";
import InfoExitoApiTextsCustom from '../../Utils/InfoExitoApiTextsCustom';
import Spinner from '../../Utils/Spinner';
import { getUser } from '../../../components/Utils/Common';
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';
import GestorRespuestaApi from '../../Api/Services/GestorRespuestaApi';
import '../../Utils/Constants';
import ModalListaVersionesGestorRespuesta from '../../Utils/Modals/ModalListaVersionesGestorRespuesta';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { set } from 'react-hook-form';

function InformeCierreSiriusForm() {

    const location = useLocation();
    const { from } = location.state;

    let radicado = from.radicado;

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });

    const [respuestaSoporteRadicado, setRespuestaSoporteRadicado] = useState(false);
    const [soporteRadicadoLista, setSoporteRadicadoLista] = useState();
            
    useEffect(() => {
        async function fetchData() {

        }
        fetchData();
    }, []);

    function buscarExpediente(radicado_sirius) {
        window.showSpinner(true);
        setRespuestaSoporteRadicado(false)
        DocumentoSiriusApi.getDocumentacionSiriusByRadicadoSirius(radicado_sirius).then(
            datos => {
                if (!datos.error) {
                    console.log("Esperando respuesta", datos);
                    setSoporteRadicadoLista(datos);
                    setRespuestaSoporteRadicado(true);
                    window.showModal(6);
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Documento Cierre", message: datos.error.toString(), show: true, redirect: null, from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);
            }
        )
    }

    const componentForm = () => {
        return (
            <Formik
                initialValues={{
                    radicado_sirius: ''
                }}
                enableReinitialize
                validate={(valores) => {

                    let errores = {}

                    if (!valores.radicado_sirius) {
                        errores.radicado_sirius = 'Campo obligatorio'
                    }

                    return errores;
                }}
                onSubmit={(valores, { resetForm }) => {
                    //handleOnSubmit(valores);
                }}
            >
                {({ errors, values }) => (
                    <div className='row'>
                        <div className='col-md-5'>
                            <div className="form-group">
                                <label htmlFor="radicado_sirius">No radicado SIRIUS</label>
                                <Field className="form-control" id="radicado_sirius" name="radicado_sirius" placeholder="No radicado SIRIUS"></Field>
                                <ErrorMessage name="radicado_sirius" component={() => (<span className="text-danger">{errors.radicado_sirius}</span>)} />
                            </div>
                        </div>
                        <div className='row'>
                            <div className='col-md-2'>
                                <div className='text-right'>
                                    <button type="button" className="btn btn-primary" onClick={() => buscarExpediente(values.radicado_sirius)} disabled={!values.radicado_sirius}><i className="fa fa-fw fa-search"></i></button>
                                </div>
                            </div>
                        </div>

                        

                        <div className='col-md-5'>
                            <div className="form-group">
                                <label htmlFor="radicado_sirius">Documentos Sirius</label>
                                <select className='form-control' id='funcionario_tutela' name='funcionario_tutela' required>
                                    <option value=''>Seleccione un documento</option>
                                    {/*
                                        getGestorRespuestaApi?.data[0]?.attributes?.rol_anterior.map((rol, i) => {
                                            return (
                                                <option key={rol.nombre_funcionario} value={rol.nombre_funcionario}>{rol.nombre_completo + ' ' + rol.apellido_completo}</option>
                                            )
                                        })
                                    */}
                                </select>
                            </div>
                        </div>
                    </div>
                )}
            </Formik>
        )
    }

    const componentFormularioSirius = () => {
        return (
            <>
                <div className="col-md-12 text-center alert alert-success">
                    LA INFORMACIÓN REFERENTE AL CIERRE DEL PROCESO AÚN NO HA SIDO REGISTRADA
                </div>
                <div className="col-md-12 alert alert-warning alert-dismissable">
                    <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                    <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                    <p className="mb-0">
                        NO puede superar QUINCE (15) Mb de peso/tamaño.
                        Tipo/Formato permitido: pdf
                        {/*getFormatosApi ? (getFormatosApi.map((suggestion) => {
                            if (suggestion.attributes.estado == true)
                                return suggestion.attributes.nombre;

                        })).join(' - '):null*/}
                    </p>
                    <p className="mb-0">
                        El nombre del archivo debe ser de máximo 40 caracteres.
                    </p>
                    <p className="mb-0">
                        Tenga en cuenta que para continuar y validar el radicado se debe digitar con el mismo formato generado por SIRIUS:
                    </p>
                    <ul>
                        <li>15 caracteres.</li>
                        <li>Incluir los dos (2) guiones "-".</li>
                    </ul>
                    <p className="mb-0">
                        Ej: 2020-EE-0000002, 2020-EE-0000123
                    </p>
                </div>
                <div className="col-md-12">
                    { componentForm() }
                </div>
            </>
        )
    }

    const componentDocumentoRegistrado = () => {
        return (
            <>
                <div className='row'>
                    <div className="col-md-4">
                        <b>Fecha de cierre: </b>
                    </div>
                    <div className="col-md-4">
                        <b>SIRIUS: </b>
                    </div>
                    <div className="col-md-4">
                       {/*<button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(getGestorRespuestaApi.data[0].attributes.documento_sirius)}><i className="fas fa-download"></i></button>*/}
                       <button type='button' title='Descargar documento' className='btn btn-sm btn-primary'><i className="fas fa-download"></i></button>
                    </div>
                </div>
                <div className='row'>
                    <div className="col-md-12">
                        <b>Observación de Cierre: </b>
                    </div>
                    <div className="col-md-12">
                        <b>Cerrado por: </b>
                    </div>
                    <div className="col-md-12">
                        <b>Cerrado en: </b>
                    </div>
                </div>
            </>
        )
    }

    const cargarTablaInformacionCierre = () => {
        return null;
        /*return (
            getDocumentoCierreLista.data.map((documento_sirius, i) => {
                return (
                    <tr key={documento_sirius.id}>
                        <td width="10%" className="text-center">{i + 1}</td>
                        <td width="20%">{documento_sirius.attributes.created_at}</td>
                        <td>
                            {documento_sirius.attributes.nombre_archivo}
                        </td>
                        <td>
                            {documento_sirius.attributes.compulsa == '1' ? ('Se realizó compulsa de datos de radicación SIRIUS No: ' + documento_sirius.attributes.sirius_track_id ) : 'Se adjunto'}
                        </td>
                        <td>                            
                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(documento_sirius, documento_sirius.attributes.compulsa == '1' ? true : false)}><i className="fas fa-download"></i></button>
                            <button type='button' title='Consultar Descripcion' className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => cargarDetalleCambiosEstado(documento_sirius)} data-target={'#modal-consultar-detalle'}><i className="fas fa-search"></i></button>
                            { componentModalListaDetalleCambios() }
                        </td>
                    </tr>
                )
            })
        )*/
    }

    const componentListDocumentosAnexos = () => {
        return (
            <>
                <div className='row'>
                    <div className="col-md-12">
                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <thead>
                                <tr>
                                    <th className="text-center">No.</th>
                                    <th>Fecha de registro</th>
                                    <th>Observación</th>
                                    <th>Registrado por</th>
                                    <th>Archivo</th>
                                </tr>
                            </thead>
                            <tbody>
                                { cargarTablaInformacionCierre() }
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className='row'>
                    <div className='col-md-12 text-center'>
                        <div className="form-group">
                            <input type="checkbox" className="form-check-input" id="cierre_informacion_cierre" name="cierre_informacion_cierre"/>
                            <label className="form-check-label" htmlFor="usuario_unico">¿Con la información registrada se puede proceder archivar la tutela?</label>
                        </div>
                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            { <ModalGen data={getModalState} /> }
            { <Spinner /> }

            <div className="block block-rounded block-bordered">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                            <li className="breadcrumb-item"> <small>Informe Cierre</small></li>
                        </ol>
                    </nav>
                </div>
            </div>

            <div className='content'>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='block block-themed'>
                            <div className='block-header'>
                                <h3 className='block-title'>SINPROC NO {radicado} ::: INFORME CIERRE</h3>
                            </div>
                            <div className='block-content'>
                                {
                                    componentDocumentoRegistrado()
                                }
                                {
                                    componentListDocumentosAnexos()
                                }
                                {
                                    componentFormularioSirius()                                        
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    );   

}

export default InformeCierreSiriusForm;
