import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../Models/ParametroModel';


function ListaBotones(props) {

    const location = useLocation()
    const { from } = location.state

    const crear_registro = "/"+props.crear_registro+"/";
    const consultar_registros = "/"+props.consultar_registros+"/";
    const adjunta_documentos = "/"+props.registrar+"/";
    from.id_etapa = props.id_etapa;
    from.id_fase = props.id_fase;

    const refrescar = (estado)=>{
        from.estadoActividad = estado;
        window.location.reload(false);
    }

    return (
        <>
            <div className='col-md-9 text-right'>
                <Link to={crear_registro} state={{ from: from }}>
                    {from.estadoActividad = false}
                    <button type="button" title='Agregar nuevo registro' className="btn btn-primary"> <i className="fas fa-plus"></i> </button>
                </Link>

                <Link to={consultar_registros} state={{ from: from }}>
                    <button type="button" title='Listado de activos' onClick={() => refrescar(true)} className="btn btn-primary"> <i className="fas fa-list"></i> </button>
                </Link>

                <Link to={consultar_registros} state={{ from: from }}>
                    <button type="button" title='Listado de inactivos' onClick={() => refrescar(false)} className="btn btn-danger"> <i className="fas fa-list"></i> </button>
                </Link>

                <Link to={`/AntencentesForm/`} state={{ from: from }}>
                    <button type="button" title='Adjuntar documento(s)' className="btn btn-primary"> <i className="fas fa-paperclip"></i> </button>
                </Link>

                <Link to={`/AntencentesForm/`} state={{ from: from }}>
                    <button type="button" title='Repositorio de documentos' className="btn btn-primary"> <i className="fas fa-folder"></i> </button>
                </Link>

                <Link to={`/RamasProceso/`} title='Regresar a ramas del proceso' state={{ from: from }}>
                    <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                </Link>
            </div>
        </>
    );

}

export default ListaBotones;
