import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ModalDatosInteresado from '../../Utils/Modals/ModalDatosInteresado';
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import moment from 'moment';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import { hasAccess } from '../../../components/Utils/Common';
import ListaBotones from '../../Utils/ListaBotones';

function DatosInteresadoLista() {


    const [getRoutes, setRoutes] = useState({
        id_etapa: 1,
        id_fase: 3,
        crear_registro: "/DatosInteresadoForm",
        consultar_registros: "/DatosInteresadoLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: ""
    });


    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [listaTipoDocumento, setListaTipoDocumento] = useState({ data: {} });
    const [respuestaTipoDocumento, setRespuestaTipoDocumento] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);

    const [getDatosInteresado, setDatosInteresado] = useState({});

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    const redirectToRoutes = () => {
        return <Navigate to={`/DatosInteresadoList/`} state={{ from: from }} />;
    }

    useEffect(() => {
        async function fetchData() {

            cargarDatosInteresados("1");

            // VALIDA CIERRE DE ETAPA CAPTURA Y REPARTO
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }

            CierreEtapaApi.getCierreEtapaByIdProDisciplinario(dataCierreEtapa, procesoDisciplinarioId, 1).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaDatosInteresadoSearch, setRespuestaDatosInteresadoSearch] = useState(false);
    const [datosInteresadoListaSearch, setDatosInteresadoListaSearch] = useState({ data: [], links: [], meta: [] });

    const [respuestaDatosInteresadoTotal, setRespuestaDatosInteresadoTotal] = useState(false);
    const [datosInteresadoListaTotal, setDatosInteresadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {

        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setDatosInteresadoListaSearch(datosInteresadoListaTotal);
            }
            else {

                setDatosInteresadoListaSearch(datosInteresadoListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.primer_apellido
                        + suggestion.attributes.segundo_apellido + suggestion.attributes.primer_nombre +
                        suggestion.attributes.segundo_nombre + suggestion.attributes.nombre_entidad
                        + suggestion.attributes.created_at + suggestion.attributes.created_user
                    ).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };

    const enviarDatos = (estado, id) => {

        try {


            //actualizamos el estado
            window.showSpinner(true);
            const data = {

                "data": {
                    "type": "interesado",
                    "attributes": {
                        "estado": estado
                    }
                }
            }


            DatosInteresadoApi.updateDatosInteresado(data, id).then(
                datos => {

                    if (!datos.error) {
                        DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                            datos => {
                                if (!datos.error) {
                                    setDatosInteresadoListaTotal(datos);
                                    setRespuestaDatosInteresadoTotal(true);
                                    setDatosInteresadoListaSearch(datos); setRespuestaDatosInteresadoSearch(true);
                                    //window.showModal(2); 
                                    window.showSpinner(false);

                                }
                                else {
                                    window.showModal(1);
                                    window.showSpinner(false);
                                }
                            }
                        )

                        //setIsRedirect(true);
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showSpinner(false)
                    }

                }
            )

        } catch (error) {
            window.showSpinner(false);
        }
    }


    const listaDatisIntersado = () => {

        if (datosInteresadoListaSearch.data != null && typeof (datosInteresadoListaSearch.data) != 'undefined') {
            return (

                datosInteresadoListaSearch.data.map((datosInteresado, i) => {
                    return (
                        cuerpoTabla(datosInteresado, i)
                    )
                })
            )
        }
        else {
            return (

                datosInteresadoListaSearch.map((datosInteresado, i) => {
                    return (
                        cuerpoTabla(datosInteresado, i)
                    )
                })
            )
        }

    }

    const cuerpoTabla = (datosInteresado, i) => {

        from.idDatosInteresado = datosInteresado.id;

        return (
            <tr key={datosInteresado.id}>
                <td>{datosInteresado.attributes.created_at}</td>
                <td>{datosInteresado.attributes.primer_apellido} {datosInteresado.attributes.segundo_apellido} {datosInteresado.attributes.primer_nombre} {datosInteresado.attributes.segundo_nombre}</td>
                <td>{(datosInteresado.attributes.nombre_entidad) ? datosInteresado.attributes.nombre_entidad : (datosInteresado.attributes.entidad ? datosInteresado.attributes.entidad : (datosInteresado.attributes.id_entidad ? datosInteresado.attributes.id_entidad : "SIN ENTIDAD"))}</td>
                <td>{datosInteresado.attributes.created_user}</td>
                <td>
                    <>
                        {
                            (hasAccess('CR_Interesado', 'Inactivar')) ? (
                                <div>
                                    <button type='button' className='btn btn-sm btn-primary' onClick={() => showModal(datosInteresado.id)}><span className="fas fa-search"></span></button>

                                    {
                                        (datosInteresado.attributes.estado == 0) ? (
                                            <Link to={`/DatosInteresadoCambiarEstadoForm/`} state={{ from: from }}>
                                                <button type='button' title='Activar Datos Interesado' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado' + i}><i className="fas fa-plus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                    {
                                        (datosInteresado.attributes.estado == 1) ? (
                                            <Link to={`/DatosInteresadoCambiarEstadoForm/`} state={{ from: from }}>
                                                <button type='button' title='Inactivar Antecedente' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                </div>
                            ) : null
                        }
                    </>

                </td>

            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const showModal = (id) => {
        window.showSpinner(true);
        //buscamos el detalle
        DatosInteresadoApi.getAllDatosInteresadoById(id).then(
            datos => {
                console.log("**********************************************");
                if (!datos.error) {
                    if (datos["data"].length > 0) {
                        setDatosInteresado(datos["data"][0]["attributes"]);
                        window.showModalDatosInteresado(datos["data"][0]["attributes"]);
                        window.showSpinner(false)
                    }
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showSpinner(false)
                    window.showModal(1)
                }
            }
        )

    }

    const cargarDatosInteresados = (estado) => {

        console.log("SANDRA: "+estado)
        window.showSpinner(true);

        const data = {
            "data": {
                "type": "interesado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "tipo_documento": "1",
                    "numero_documento": "1",
                    "primer_nombre": "1",
                    "segundo_nombre": "1",
                    "primer_apellido": "1",
                    "segundo_apellido": "1",
                    "estado": estado,
                }
            }
        }

        if (hasAccess('CR_Interesado', 'Consultar')) {

            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setDatosInteresadoListaTotal(datos), setRespuestaDatosInteresadoTotal(true),
                    setDatosInteresadoListaSearch(datos), setRespuestaDatosInteresadoSearch(true)) : window.showModal(1)
            )
        }

    }


    const handleCallback = (childData) => {
        try {
            cargarDatosInteresados(childData);
        } catch (error) {

        }

    }

    return (
        <>


            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Datos del interesado Lista</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

            {isRedirect ? redirectToRoutes() : null}
            {<ModalDatosInteresado getDatosInteresado={getDatosInteresado} />}
       
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>

                    <div className="block block-themed">

                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: Datos del interesado</h3>
                        </div>

                        <div className="block-content">
                            <>
                                {
                                    (hasAccess('CR_Interesado', 'Consultar')) ? (
                                        <div className='row'>

                                            <div className='col-md-3'>
                                                <div className="form-group ">
                                                    <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                </div>
                                            </div>

                                             {/*ListaBotones(getRoutes)*/}
                                             <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />

                                        </div>

                                    ) : null
                                }

                                {
                                    (hasAccess('CR_Interesado', 'Consultar')) ? (
                                        <div className="row">
                                            <div className="col-sm-12">

                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                    <thead>
                                                        <tr>
                                                            <th>Fecha registro</th>
                                                            <th className="text-center">Persona</th>
                                                            <th>Entidad</th>
                                                            <th>Registro por</th>
                                                            <th>Acciones</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {respuestaDatosInteresadoSearch ? listaDatisIntersado() : null}
                                                    </tbody>
                                                </table>
                                            </div>
                                        </div>
                                    ) : null
                                }
                            </>
                           
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default DatosInteresadoLista;
