import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function ActuacionesMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getLista, setLista] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
   

    let { radicado, vigencia, id, item } = useParams();


    const [nombre, setNombre] = useState('');
    const [tipo, setTipo] = useState('');
    const [autoNumero, setAutoNumero] = useState('');
    const [fecha, setFecha] = useState('');
    const [fechaTermino, setFechaTermino] = useState('');
    const [instancia, setInstancia] = useState('');
    const [decision, setDesicion] = useState('');
    const [terminoMonto, setTerminoMonto] = useState('');
    const [observacion, setObservacion] = useState('');

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            lista();
            
        }
        fetchData();
    }, []);


    const lista= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-actuacion/"+radicado+"/"+vigencia+"/"+item).then(
                datos => {

                    if (!datos.error) {
                        setLista(datos);
                    } else {
                        //setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }


    const handleClick = (event, i) => {

        if(i===1){
            setNombre(getLista.data[0].attributes.nombre);
        }

        else if(i===2){
            setTipo(getLista.data[0].attributes.tipo);      
        }

        else if(i===3){
            setAutoNumero(getLista.data[0].attributes.autoNumero);
        }

        else if(i===4){
            setFecha(getLista.data[0].attributes.fecha);      
        }

        else if(i===5){
            setFechaTermino(getLista.data[0].attributes.fechaTermino);      
        }

        else if(i===6){
            setInstancia(getLista.data[0].attributes.instancia);      
        }

        else if(i===7){
            setDesicion(getLista.data[0].attributes.decision);      
        }

        else if(i===8){
            setTerminoMonto(getLista.data[0].attributes.terminoMonto);      
        }

        else if(i===9){
            setObservacion(getLista.data[0].attributes.observacion);      
        }

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setNombre(event.currentTarget.value);
        }

        else if(i===2){
            setTipo(event.currentTarget.value)
        }

        else if(i===3){
            setAutoNumero(event.currentTarget.value)
        }

        else if(i===4){
            setFecha(event.currentTarget.value)
        }

        else if(i===5){
            setFechaTermino(event.currentTarget.value)
        }

        else if(i===6){
            setInstancia(event.currentTarget.value)
        }

        else if(i===7){
            setDesicion(event.currentTarget.value)
        }

        else if(i===8){
            setTerminoMonto(event.currentTarget.value)
        }

        else if(i===9){
            setObservacion(event.currentTarget.value)
        }


        return true;

        //return true;
    }



    const infoInteresado = () => {

        if (getLista.data != null && typeof (getLista.data) != 'undefined') {
            return (

                getLista.data.map((item, i) => {
                    return (
                        <>
                        {/* 1. NOMBRE */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="nombre_origen">Nombre</label>
                                <Field value={item.attributes.nombre} as="input" type="text" autoComplete="off" 
                                className="form-control" id="nombre_origen" name="nombre_origen">                                   
                                </Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 1)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="nombre_destino">Nombre<span className="text-danger">*</span></label>
                                <Field value={nombre} as="input" type="text" autoComplete="off" className="form-control" 
                                id="nombre_destino" name="nombre_destino" onChange={event => handleInputChange(event, 1)}></Field>
                            </div>
                        </div> 

                        {/* 2. TIPO */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="tipo_origen">Tipo</label>
                                <Field value={item.attributes.tipo} as="input" type="text" autoComplete="off" 
                                className="form-control" id="tipo_origen" name="tipo_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 2)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    

                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="tipo_destino">Tipo<span className="text-danger">*</span></label>
                                <Field value={tipo} as="input" type="text" autoComplete="off" className="form-control" 
                                id="tipo_destino" name="tipo_destino" onChange={event => handleInputChange(event, 2)}>

                                </Field>
                            </div>
                        </div>


                        {/* 3. AUTO NUMERO */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="auto_origen">Nmero de auto</label>
                                <Field value={item.attributes.autoNumero} as="input" type="text" autoComplete="off" 
                                className="form-control" id="auto_origen" name="auto_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 3)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="auto_destino">Sector<span className="text-danger">*</span></label>
                                <Field value={autoNumero} as="input" type="text" autoComplete="off" className="form-control" 
                                id="auto_destino" name="auto_destino" 
                                onChange={event => handleInputChange(event, 3)}></Field>
                            </div>
                        </div> 

                        {/* 4. NOMBRE INVESTIGADO */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="nombre_investigado_origen">Nombre investigado</label>
                                <Field value={item.attributes.nombreInvestigado} as="input" type="text" 
                                autoComplete="off" className="form-control" 
                                id="nombre_investigado_origen" name="nombre_investigado_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 4)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="nombre_investigado_destino">Nombre investigado<span className="text-danger">*</span></label>
                                <Field value={nombreInvestigado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="nombre_investigado_destino" name="nombre_investigado_destino" 
                                onChange={event => handleInputChange(event, 4)}></Field>
                            </div>
                        </div> 


                        {/*5. CARGO INVESTIGADO*/}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="cargo_investigado_origen">Cargo investigado</label>
                                <Field value={item.attributes.cargoInvestigado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="cargo_investigado_origen" name="cargo_investigado_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 5)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="cargo_investigado_destino">Cargo investigado<span className="text-danger">*</span></label>
