import React, { useEffect, useState, } from 'react';
import { Form, Field, Formik } from 'formik';
import { Link } from 'react-router-dom';
import Spinner from '../Utils/Spinner';
import GenericApi from '../Api/Services/GenericApi';
import { Chart } from "react-google-charts";
import '../Utils/Constants';

function Dashboard() {


    const [getInfoDashboard, setInfoDashboard] = useState({ data: []});
    const [misPendientes, setMisPendientes] = useState();
    const [pendientesFirma, setPendientesFirma] = useState();

    const [listaPendientesFirma, setListaPendientesFirma] = useState();
    const [rtaListaPendientesFirma, setRtaListaPendientesFirma] = useState(false);

    const [getListaEtapa, setListaEtapa] = useState();
    const [rtaListaEtapa, setRtaListaEtapa] = useState(false);
    
    const [rtaInfoDashboard, setRtaInfoDashboard] = useState(false);

    const [getSinClasificacion, setSinClasificacion] = useState();
    const [getCantDerechosPeticion, setCantDerechosPeticion] = useState();
    const [getCantPoderPreferente, setCantPoderPreferente] = useState();
    const [getCantQueja, setCantQueja] = useState();
    const [getCantTutela, setCantTutela] = useState();
    const [getCantProcDisciplinario, setCantProcDisciplinario] = useState();


    const [getNombreEtapaUno, setNombreEtapaUno] = useState();
    const [getNombreEtapaDos, setNombreEtapaDos] = useState();
    const [getNombreEtapaTres, setNombreEtapaTres] = useState();
    const [getNombreEtapaCuatro, setNombreEtapaCuatro] = useState();
    const [getNombreEtapaCinco, setNombreEtapaCinco] = useState();
    const [getNombreEtapaSeis, setNombreEtapaSeis] = useState();
    const [getNombreEtapaSiete, setNombreEtapaSiete] = useState();
    const [getNombreEtapaOcho, setNombreEtapaOcho] = useState();

    const [getCantEtapaUno, setCantEtapaUno] = useState();
    const [getCantEtapaDos, setCantEtapaDos] = useState();
    const [getCantEtapaTres, setCantEtapaTres] = useState();
    const [getCantEtapaCuatro, setCantEtapaCuatro] = useState();
    const [getCantEtapaCinco, setCantEtapaCinco] = useState();
    const [getCantEtapaSeis, setCantEtapaSeis] = useState();
    const [getCantEtapaSiete, setCantEtapaSiete] = useState();
    const [getCantEtapaOcho, setCantEtapaOcho] = useState();
    


    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);

            await infoDashboard();
            await infoDocumentosPorFirmar();
            await casosAsignadosPorEtapa();
            await casosAsignadosPorExpediente();        

            window.showSpinner(false);
        }
        fetchData();
    }, []);


    const infoDashboard = () => {

        GenericApi.getGeneric("inicio").then(
            datos => {
    
                if (!datos.error) {
