import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';

function EvaluacionFasesDetalle() {


    const [getListaFases, setListaFases] = useState({ data: [], links: [], meta: [] });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });

    let { id_tipo_expediente, id_sub_tipo_expediente, id_tipo_evaluacion } = useParams();


    useEffect(() => {
        async function fetchData() {

            console.log("TIPO DE EXPEDIENTE:"+ id_tipo_expediente);
            console.log("TIPO DE SUB EXPEDIENTE:"+ id_sub_tipo_expediente);
            console.log("TIPO DE EVALUACION:"+ id_tipo_evaluacion);

            getListaFases();

            //id_tipo_expediente/:id_sub_tipo_expediente/:id_tipo_evaluacion"
        
        }
        fetchData();
    }, []);



    const getListaFasesPorTipoExpediente = () => {
        window.showSpinner(true);
        GenericApi.getGeneric("administracion/evaluacion/lista-expedientes-by-id-tipo-expediente/"+id_tipo_expediente+"/"+id_sub_tipo_expediente+"/"+id_tipo_evaluacion).then(
            datos => {
                if (!datos.error) {
                    setListaFases(datos);
                }
                else {
                    setModalState({ title: "SINPROC No ", message: datos.error.toString(), show: true, redirect: '/ClasificacionRadicadoLista', alert: global.Constants.TIPO_ALERTA.ERROR }); 
                }
                window.showSpinner(false);
            }
        )
    }


    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }


    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "SINPROC No  :: Actualizar antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }
    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": "",
                    "update_user": nombreUsuario,
                    "update_at": Date.now(),
                    "estado_observacion": datos.descripcion,
                    "estado": global.Constants.ESTADOS.ACTIVO,
                }
            }
        }

       GenericApi.updateGeneric("antecedentes",idAntecedente,data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No  :: Actualizar antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista',  alert: global.Constants.TIPO_ALERTA.EXITO});
                    window.showSpinner(false);

                }
                else {
                    setModalState({ title: "SINPROC No  :: Actualizar antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista',  alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showSpinner(false);
                    
                }
            }
        )
    }

    const CargarInfoAntecedente = () => {

        if (getAntecedente.data != null && typeof (getAntecedente.data) != 'undefined') {
            return (
                <tr>
                    <td style={{ width: "20%" }}>
                        <strong>Funcionario: </strong>{getAntecedente.data.attributes.nombre_completo}<br />
                        <strong>Etapa: </strong>{getAntecedente.data.attributes.etapa.nombre}
                        <strong>Fecha: </strong>{getAntecedente.data.attributes.fecha_creado}
                    </td>
                    <td style={{ width: "20%" }}>{getAntecedente.data.attributes.nombre_dependencia}</td>
                    <td style={{ width: "60%" }} data-toggle="popover" data-placement="top" title={getAntecedente.data.attributes.descripcion}>{getAntecedente.data.attributes.descripcion_corta}</td>
                </tr>
            )
        }

    }

    return (
        <>
            {<Spinner/>}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)


                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < getMinimoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMinimoTextArea + ' caracteres'
                    }
                    else if (valores.descripcion.length > getMaximoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMaximoTextArea + ' caracteres'
                    }

                    if (valores.descripcion) {
                        if (containsSpecialChars(valores.descripcion))
                            errores.descripcion = 'Tiene caracteres inválidos'
                    }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <>
                        <h1>Hola</h1>
                        </>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default EvaluacionFasesDetalle;
