import axios from 'axios'

const baseUrl = process.env.REACT_APP_API_URL;

const headers = {
    'Content-Type': 'application/vnd.api+json',
    'Accept': 'application/vnd.api+json'
}

async function getAllAntecedente(data, procesoDisciplinarioId){

    const url = baseUrl + "antencedentes/obtener-antecedentes/" + procesoDisciplinarioId;
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        //console.log(response.data);
        return response.data;
    })
    .catch(error => {
        //console.log(error);
        return {estado: false, error: error}
    });
}

async function getAllAntecedentes(){

    const url = baseUrl + "antencedentes";
   
    return await axios.get(url, {
        headers: headers
    })
    .then(response => {
        console.log(response.data);
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });
}


async function addAntecedente(data){

    const url = baseUrl + "antencedentes";
   
    console.log(url);
    console.log(data);

    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        console.log(response.data);
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });
}

export default { getAllAntecedente, getAllAntecedentes, addAntecedente }
