import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";

function DatosInteresadoLista() {

    const [respuestaDatosInteresado, setRespuestaDatosInteresado] = useState(false);
    const [DatosInteresadoLista, setDatosInteresadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

 
    let { radicado, procesoDisciplinarioId } = useParams();

    const redirectToRoutes = () => {
        return <Navigate to={`/DatosInteresadoList/${procesoDisciplinarioId}`} />;
    }

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "interesado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "tipo_documento" : "1",
                        "numero_documento" : "1",
                        "primer_nombre" : "1",
                        "segundo_nombre" : "1",
                        "primer_apellido" : "1",
                        "segundo_apellido" : "1"
                    }
                }
            }


        
        }
        fetchData();
    }, []);

    

    const listaDatisIntersado = () => {

        return(
            DatosInteresadoLista.data.map((datosInteresado, i) => {
                return (
                    <tr key={datosInteresado.id}>
                        
                        <td>{ datosInteresado.attributes.primer_apellido } { datosInteresado.attributes.segundo_apellido } { datosInteresado.attributes.primer_nombre } { datosInteresado.attributes.segundo_nombre }</td>
                        <td>{ datosInteresado.attributes.nombre_entidad }</td>
                        <td>{datosInteresado.attributes.id_funcionario}</td>
                        <td>{ datosInteresado.attributes.direccion }</td>
                        <td>{datosInteresado.attributes.estado ? 'Activo' : 'Inactivo'}</td>

                       
                    </tr>
                )
            })
        )
    }
    

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        setDatosInteresadoLista({ data: [], links: [], meta: [] });
        setRespuestaDatosInteresado(false);

        const data = {
            "data" : {
                "type": "interesado",
                "attributes": {
                    "tipo_documento" : datos.tipoDocumento?datos.tipoDocumento:"-1",
                    "numero_documento" : datos.numeroDocumento?datos.numeroDocumento:"-1",
                    "primer_nombre" : datos.primerNombre?datos.primerNombre:"-1",
                    "segundo_nombre" : datos.segundoNombre?datos.segundoNombre:"-1",
                    "primer_apellido" : datos.primerApellido?datos.primerApellido:"-1",
                    "segundo_apellido" : datos.segundoApellido?datos.segundoApellido:"-1",

                    "id_tipo_interesao": "0",
                    "id_tipo_sujeto_procesal": "0",
                    "id_departamento": "0",
                    "id_ciudad": "0",
                    "direccion": "0",
                    "id_localidad": "0",
                    "email": "0",
                    "telefono_celular": "0",
                    "telefono_fijo": "0",
                    "id_sexo": "0",
                    "id_genero": "0",
                    "id_orientacion_sexual": "0",
                    "cargo": "0",
                     "tarjeta_profesional": "0",
                     "id_dependencia": "0",
                     "id_tipo_entidad": "0",
                     "nombre_entidad":"0",
                     "id_proceso_disciplinario": procesoDisciplinarioId
                }
            }
        }

        //console.log(JSON.stringify(data));


        DatosInteresadoApi.getAllDatosInteresadoByFilter(data, procesoDisciplinarioId).then(
            datos => {
                if(!datos.error){
                 
                    setDatosInteresadoLista(datos)
                    setRespuestaDatosInteresado(true)
                }
                else{
                   
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )

        

    }


    return(
        <>
        <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
    
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-primary" aria-current="page"  to={`/DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}><small>Datos Interesado</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                { isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }
                <Formik
                    initialValues={{
                        tipoDocumento: '',
                        numeroDocumento: '',
                        primerNombre: '',
                        segundoNombre: '',
                        primerApellido: '',
                        segundoApellido: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {}
       
                        if((!valores.tipoDocumento || typeof(valores.tipoDocumento) == 'undefined')
                        && (!valores.numeroDocumento || typeof(valores.numeroDocumento) == 'undefined')
                        && (!valores.primerNombre || typeof(valores.primerNombre) == 'undefined')
                        && (!valores.segundoNombre || typeof(valores.segundoNombre) == 'undefined')
                        && (!valores.primerApellido || typeof(valores.primerApellido) == 'undefined')
                        && (!valores.segundoApellido || typeof(valores.segundoApellido) == 'undefined')){
                            errores.segundoApellido = 'Debe ingresar al menos un campo'
                        }


                        return errores
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        enviarDatos(valores);
                    }}
                >

                {({ errors }) => (
                    <Form>
            
            <div className="col-md-12 text-right">

                <Link to={`/DatosInteresadoForm/${radicado}/${procesoDisciplinarioId}`}>
                    <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                        <i className="fa fa-fw fa-archive mr-1"></i> Crear datos del interesado
                    </button>
                </Link>
            </div>         
            <div className="block-header block-header-default">
                    <h3 className="block-title">Filtro de búsqueda</h3>
                    </div> 
                    <div className="block block-rounded block-bordered">
              
                        <div className="block-content block-content-full">
                            <div className="row">
                                <div className="col">
                                    
                                    <div className="form-group">
                                        <label htmlFor='tipoDocumento'>Tipo documento <span className="text-danger">*</span></label>
                                        <Field as="select" id="tipoDocumento" name="tipoDocumento" className="form-control">
                                            <option value="">Por favor seleccione</option>
                                            <option value="1">CÉDULA DE CIUDADANÍA</option>
                                            <option value="2">CÉDULA DE EXTRANJERIA</option>
                                            <option value="3">TARJETA DE IDENTIDAD</option>
                                        </Field>
                                        <ErrorMessage name="tipoDocumento" component={() => (<span className="text-danger">{errors.tipoDocumento}</span>)} />
                                    </div>
                                                       
                                </div>

                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="numeroDocumento">Número de documento</label>
                                        <Field as="input" className="form-control" type="text" id="numeroDocumento" name="numeroDocumento" placeholder="Número de documento" />
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="primerNombre">Primer Nombre</label>
                                        <Field as="input" className="form-control" type="text" id="primerNombre" name="primerNombre" placeholder="Primer Nombre" />
                                        <ErrorMessage name="primerNombre" component={() => (<span className="text-danger">{errors.primerNombre}</span>)} />
                                    </div>
                                </div>
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="segundoNombre">Segundo Nombre</label>
                                        <Field as="input" className="form-control" type="text" id="segundoNombre" name="segundoNombre" placeholder="Segundo Nombre" />
                                        <ErrorMessage name="segundoNombre" component={() => (<span className="text-danger">{errors.segundoNombre}</span>)} />
                                    </div>
                                </div>

                            </div>

                            <div className="row">
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="primerApellido">Primer Apellido</label>
                                        <Field as="input" className="form-control" type="text" id="primerApellido" name="primerApellido" placeholder="Primer Apellido" />
                                        <ErrorMessage name="primerApellido" component={() => (<span className="text-danger">{errors.primerApellido}</span>)} />
                                    </div>
                                </div>
                                <div className="col">
                                <div className="form-group">
                                        <label htmlFor="segundoApellido">Segundo Apellido</label>
                                        <Field as="input" className="form-control" type="text" id="segundoApellido" name="segundoApellido" placeholder="Segundo Apellido" />
                                        <ErrorMessage name="segundoApellido" component={() => (<span className="text-danger">{errors.segundoApellido}</span>)} />
                                    </div>
                                </div>

                            </div>
                            
                        </div>
                        <div className="block-content block-content-full text-right bg-light">
                            <button type="submit" className="btn btn-sm btn-primary" >Buscar</button>           
                        </div>
                
                    </div>
                    </Form>
                )}
            </Formik> 

            <div className="block-header block-header-default">
                <h3 className="block-title">Historial datos del Interesado (RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
            </div> 

            <div className="block block-rounded block-bordered">
                        
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">Persona</th>
                                        <th>Empresa</th>
                                        <th>Registrado por</th>
                                        <th>Detalle</th>
                                        <th>Estado</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    { respuestaDatosInteresado ? listaDatisIntersado() : null }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    );

  
}
export default DatosInteresadoLista;
