import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import Spinner from '../../Utils/Spinner';
import { Link } from "react-router-dom";
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import SamplesApi from '../../Api/Services/SamplesApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import { Navigate } from "react-router-dom";
function DatosInteresadoForm() {
   
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [selectTipoInteresado, setSelectTipoInteresado] = useState("");
    const [selectTipoEntidad, setSelectTipoEntidad] = useState("");
    const [selectSujetoProcesal, setSelectSujetoProcesal] = useState("");
    const [formIntialValuesInteresado, setFormIntialValuesInteresado] = useState();
    
    const [selectedDepartamento, setSelectedDepartamento] = useState("");
    const [respuestaDepartamento, setRespuestaDepartamento] = useState(false);
    const [listaDepartamento, setListaDepartamento] = useState({ data:{} });

    const [listaCiudad, setListaCiudad] = useState({ data:{} });
    const [respuestaCiudad, setRespuestaCiudad] = useState(false);
 
    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to="/MisPendientes" />;
    }


    useEffect(() => {
        async function fetchData() {
            SamplesApi.getAllDepartamentos().then(
                datos => !datos.error ? (setListaDepartamento(datos), setRespuestaDepartamento(true) ) : window.showModal(1)
            )
            
        }
        fetchData();
    }, []);

    const selectChangeTipoInteresado = (e) => {
        setSelectTipoInteresado(e);
    }

    let selectChangeDepartamento= (e) => {
        
        setSelectedDepartamento(e);
        window.showSpinner(true);
        setListaCiudad({ data: [], links: [], meta: [] });
        setRespuestaCiudad(false);

        const data = {
            "data" : {
                "type": "interesado",
                "attributes": {
                    "nombre":"1",
                    "codigo_dane":"1",
                    "id_departamento":e
                }
            }
        }

        ParametrosMasApi.getAllCiudadByDepartamento(data).then(
            datos => !datos.error ? (setListaCiudad(datos), setRespuestaCiudad(true) ) : window.showModal(1)
        )
        
        window.showSpinner(false);
    }
    const selectDepartamentos = () => {
        return(
            listaDepartamento.data.map((departamento, i) => {
                return (
                    <option key={departamento.id} value={departamento.id}>{departamento.attributes.nombre}</option>
                )
            })
        )
        
    }

    const selectCiudad = () => {
        return(
            listaCiudad.data.map((ciuudad, i) => {
                return (
                    <option key={ciuudad.id} value={ciuudad.id}>{ciuudad.attributes.nombre}</option>
                )
            })
        )
    }

    


    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        if(formIntialValuesInteresado){
                
            data = {
            
                "data": {
                    "type": "interesado",
                    "attributes": {
                        
                        "id_tipo_interesao": selectTipoInteresado,
                        "id_tipo_sujeto_procesal": formIntialValuesInteresado.tipoSujetoProcesal ? formIntialValuesInteresado.tipoSujetoProcesal : '-1',
                        "tipo_documento":  formIntialValuesInteresado ? formIntialValuesInteresado.tipoDocumento : '-1',
                        "numero_documento":  formIntialValuesInteresado ? formIntialValuesInteresado.numeroDocumento : '', 
                        "primer_nombre":  formIntialValuesInteresado ? formIntialValuesInteresado.primerNombre : '', 
                        "segundo_nombre":  formIntialValuesInteresado ? formIntialValuesInteresado.segundoNombre : '',
                        "primer_apellido": formIntialValuesInteresado ? formIntialValuesInteresado.primerApellido : '',
                        "segundo_apellido": formIntialValuesInteresado ? formIntialValuesInteresado.segundoApellido : '',
                        "id_departamento": selectedDepartamento != "" ? selectedDepartamento : '',
                        "id_ciudad": formIntialValuesInteresado ? formIntialValuesInteresado.ciudad : '',
                        "direccion": formIntialValuesInteresado ? formIntialValuesInteresado.direccionCorrespondencia : '',
                        "id_localidad": formIntialValuesInteresado ? formIntialValuesInteresado.localidad : '',
                        "email": formIntialValuesInteresado ? formIntialValuesInteresado.correo : '',
                        "telefono_celular": formIntialValuesInteresado ? formIntialValuesInteresado.telefonoCelular : '',
                        "telefono_fijo": formIntialValuesInteresado ? formIntialValuesInteresado.telefonoFijo : '',
                        "id_sexo": formIntialValuesInteresado ? formIntialValuesInteresado.sexo : '',
                        "id_genero": formIntialValuesInteresado ? formIntialValuesInteresado.genero : '',
                        "id_orientacion_sexual": formIntialValuesInteresado ? formIntialValuesInteresado.orientacionSexual : '',
                        "cargo": formIntialValuesInteresado.cargo ? formIntialValuesInteresado.cargo: 'NO_APLICA',
                        "tarjeta_profesional": formIntialValuesInteresado.tarjetaProfesional ? formIntialValuesInteresado.tarjetaProfesional : 'NO_APLICA',
                        "id_dependencia": formIntialValuesInteresado.dependencia ? formIntialValuesInteresado.dependencia : '1',
                        "id_tipo_entidad": formIntialValuesInteresado.tipoEntidad ? formIntialValuesInteresado.tipoEntidad : '1',
                        "nombre_entidad": formIntialValuesInteresado.nombreEntidad ? formIntialValuesInteresado.nombreEntidad : 'NO_APLICA'

                    }
                }
            }
        }

        

        console.log(JSON.stringify(data));
        
        DatosInteresadoApi.addDatosInteresado(data).then(
            datos => {
                
                if(!datos.error){
                    window.showModal(2)
                    setIsRedirect(true);
                }
                else{
                    console.log("hay error");
                    console.log(datos.error);
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }
    
    const componentFormularioInteresado = () => {
        return (
            <>
                <Formik                
                    initialValues={{ 
                        tipoEntidad: formIntialValuesInteresado ? formIntialValuesInteresado.tipoEntidad : '',
                        nombreEntidad: formIntialValuesInteresado ? formIntialValuesInteresado.nombreEntidad : '',
                        sector: formIntialValuesInteresado ? formIntialValuesInteresado.sector : '',
                        secretaria: formIntialValuesInteresado ? formIntialValuesInteresado.secretaria : '',
                        tipoSujetoProcesal: formIntialValuesInteresado ? formIntialValuesInteresado.tipoSujetoProcesal : '',
                        tipoDocumento: formIntialValuesInteresado ? formIntialValuesInteresado.tipoDocumento : '',
                        numeroDocumento: formIntialValuesInteresado ? formIntialValuesInteresado.numeroDocumento : '',
                        primerNombre: formIntialValuesInteresado ? formIntialValuesInteresado.primerNombre : '',
                        segundoNombre: formIntialValuesInteresado ? formIntialValuesInteresado.segundoNombre : '',
                        primerApellido: formIntialValuesInteresado ? formIntialValuesInteresado.primerApellido : '',
                        segundoApellido: formIntialValuesInteresado ? formIntialValuesInteresado.segundoApellido : '',
                        dependencia: formIntialValuesInteresado ? formIntialValuesInteresado.dependencia : '',
                        cargo: formIntialValuesInteresado ? formIntialValuesInteresado.cargo : '',
                        entidadSujetoProcesal: formIntialValuesInteresado ? (selectSujetoProcesal === '6' ? 'Procuraduria general de la nación' : formIntialValuesInteresado.entidadSujetoProcesal)  : '',
                        tarjetaProfesional: formIntialValuesInteresado ? formIntialValuesInteresado.tarjetaProfesional : '',
                        departamento: formIntialValuesInteresado ? formIntialValuesInteresado.departamento : '',
                        ciudad: formIntialValuesInteresado ? formIntialValuesInteresado.ciudad : '',
                        direccionCorrespondencia: formIntialValuesInteresado ? formIntialValuesInteresado.direccionCorrespondencia : '',
                        localidad: formIntialValuesInteresado ? formIntialValuesInteresado.localidad : '',
                        correo: formIntialValuesInteresado ? formIntialValuesInteresado.correo : '', 
                        telefonoCelular: formIntialValuesInteresado ? formIntialValuesInteresado.telefonoCelular : '',
                        telefonoFijo: formIntialValuesInteresado ? formIntialValuesInteresado.telefonoFijo : '',
                        sexo: formIntialValuesInteresado ? formIntialValuesInteresado.sexo : '',
                        genero: formIntialValuesInteresado ? formIntialValuesInteresado.genero : '',
                        orientacionSexual: formIntialValuesInteresado ? formIntialValuesInteresado.orientacionSexual : '',
                        folio: formIntialValuesInteresado ? formIntialValuesInteresado.folio : '',
                    }}
                    enableReinitialize
                    validate={(valores) => {

                        setFormIntialValuesInteresado(valores);

                        let errores = {}
                        
                        setSelectTipoEntidad(valores.tipoEntidad)
                        setSelectSujetoProcesal(valores.tipoSujetoProcesal)
                        //valores.entidadSujetoProcesal = '123123';
                         
                        if(!valores.tipoEntidad && selectTipoEntidad){
                            errores.tipoEntidad = 'Debe seleccionar un tipo de entidad'
                        }
                        
                        //Entidad Privada y Publica
                        if(!valores.nombreEntidad && selectTipoEntidad){
                            errores.nombreEntidad = 'Debe ingresar el nombre de la entidad'
                        }
                        //Entidad Publica
                        if(!valores.sector && selectTipoEntidad === '2'){
                            errores.sector = 'Debe ingresar el sector'
                        }
                        if(!valores.secretaria && selectTipoEntidad === '2'){
                            errores.secretaria = 'Debe ingresar la secretaria'
                        }
                        
                        if(!valores.tipoSujetoProcesal && selectTipoInteresado === '1'){
                            errores.tipoSujetoProcesal = 'Debe seleccionar un tipo de sujeto procesal'
                        }

                        if(!valores.tipoDocumento){
                            errores.tipoDocumento = 'Debe seleccionar un tipo de documento'
                        }
                        
                        if(!valores.numeroDocumento){
                            errores.numeroDocumento = 'Debe ingresar el número de documento'
                        }

                        if(!valores.primerNombre){
                            errores.primerNombre = 'Debe ingresar el primer nombre'
                        }

                        if(!valores.primerApellido){
                            errores.primerApellido = 'Debe ingresar el primer apellido'
                        }

                        if(!valores.dependencia && selectSujetoProcesal === '6'){
                            errores.dependencia = 'Debe ingresar la dependencia'
                        }

                        if(!valores.cargo && (selectSujetoProcesal === '3' || selectSujetoProcesal === '6')){
                            errores.cargo = 'Debe ingresar el cargo'
                        }

                        if(!valores.entidadSujetoProcesal && (selectSujetoProcesal === '3' || selectSujetoProcesal === '6')){
                            errores.entidadSujetoProcesal = 'Debe ingresar la entidad'
                        }

                        if(!valores.entidadSujetoProcesal && (selectSujetoProcesal === '3')){
                            errores.entidadSujetoProcesal = 'Debe ingresar la entidad'
                        }

                        if(!valores.tarjetaProfesional && (selectSujetoProcesal === '4' || selectSujetoProcesal === '5')){
                            errores.tarjetaProfesional = 'Debe ingresar la tarjeta profesional'
                        }

                        if(selectedDepartamento =="" ){
                            errores.departamento = 'Debe seleccionar el departamento'
                        }

                        if(!valores.ciudad){
                            errores.ciudad = 'Debe seleccionar la ciudad'
                        }
                        
                        if(!valores.direccionCorrespondencia){
                            errores.direccionCorrespondencia = 'Debe ingresar la dirección de correspondencia'
                        }
                        
                        if(!valores.localidad){
                            errores.localidad = 'Debe seleccionar la localidad'
                        }

                        if(!valores.correo){
                            errores.correo = 'Debe ingresar un correo'
                        }

                        if(!valores.telefonoCelular){
                            errores.telefonoCelular = 'Debe ingresar el teléfono celular'
                        }

                        if(!valores.telefonoFijo){
                            errores.telefonoFijo = 'Debe ingresar el teléfono fijo'
                        }

                        if(!valores.sexo){
                            errores.sexo = 'Debe seleccionar el sexo'
                        }

                        if(!valores.genero){
                            errores.genero = 'Debe seleccionar el genero'
                        }
                        
                        if(!valores.orientacionSexual){
                            errores.orientacionSexual = 'Debe seleccionar la orientación sexual'
                        }

                        if(!valores.folio){
                            errores.folio = 'Debe ingresar el folio'
                        }
                        console.log(errores)
                        return errores
                    }}
                    onSubmit={(valores, {resetForm}) => {

                        enviarDatos(valores);
                    }}
                >
                    {({ errors }) => (
                        <Form>



                            <div className='row'>                                
                                {
                                    selectTipoInteresado === '2' ? 
                                    (
                                        <div className="col-md-12">
                                            <div className="block block-themed">
                                                <div className="block-header">
                                                    <h3 className="block-title">Datos de la entidad</h3>
                                                </div>
                                                <div className="block-content">
                                                    <div className="row">
                                                        <div className="col-md-12">
                                                            <div className="form-group">
                                                                <label htmlFor="tipoEntidad">Tipo de entidad <span className="text-danger">*</span></label>
                                                                <Field as="select" className="form-control" id="tipoEntidad" name="tipoEntidad">
                                                                    <option value="">Por favor seleccione</option>
                                                                    <option value="1">Publica</option>
                                                                    <option value="2">Privada</option>
                                                                </Field>
                                                                <ErrorMessage name="tipoEntidad" component={() => (<span className="text-danger">{errors.tipoEntidad}</span>)} />
                                                            </div>
                                                        </div>
                                                        {
                                                            selectTipoEntidad ? 
                                                            (
                                                                <>
                                                                    <div className="col-md-12">
                                                                        <div className="form-group">
                                                                            <label htmlFor='nombreEntidad'>Nombre de la entidad <span className="text-danger">*</span></label>
                                                                            <Field type="text" id="nombreEntidad" name="nombreEntidad" className="form-control" placeholder="Nombre entidad"/>
                                                                            <ErrorMessage name="nombreEntidad" component={() => (<span className="text-danger">{errors.nombreEntidad}</span>)} />
                                                                        </div>
                                                                    </div>
                                                                    {
                                                                        selectTipoEntidad === '2' ? 
                                                                        (
                                                                            <>
                                                                                <div className="col-md-6">
                                                                                    <div className="form-group">
                                                                                        <label htmlFor='sector'>Sector <span className="text-danger">*</span></label>
                                                                                        <Field type="text" id="sector" name="sector" className="form-control" placeholder="Sector" disabled/>
                                                                                        <ErrorMessage name="sector" component={() => (<span className="text-danger">{errors.sector}</span>)} />
                                                                                    </div>
                                                                                </div>
                                                                                <div className="col-md-6">
                                                                                    <div className="form-group">
                                                                                        <label htmlFor='secretaria'>Secretaria <span className="text-danger">*</span></label>
                                                                                        <Field type="text" id="secretaria" name="secretaria" className="form-control" placeholder="Secretaria" disabled/>
                                                                                        <ErrorMessage name="secretaria" component={() => (<span className="text-danger">{errors.secretaria}</span>)} />
                                                                                    </div>
                                                                                </div>
                                                                            </>
                                                                        ) : null
                                                                    }
                                                                </>
                                                            ) : null
                                                        }
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ) : null
                                }
                                {
                                    selectTipoInteresado ? 
                                    (
                                        <div className="col-md-12">
                                            <div className="block block-themed">
                                                <div className="block-header">
                                                    <h3 className="block-title">Datos basicos del Peticionario/Interesado</h3>
                                                </div>
                                                <div className="block-content">
                                                    <div className='text-center'>
                                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                                            <input type="checkbox" className="custom-control-input" id="example-sw-custom-lg1" name="example-sw-custom-lg1"  />
                                                            <label className="custom-control-label" htmlFor="example-sw-custom-lg1">¿El registro se realiza de forma anónima?</label>
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="block-content">
                                                    <div className="row">
                                                    {
                                                        selectTipoInteresado === '1' ? 
                                                        (
                                                            <div className="col-md-12">
                                                                <div className="form-group">
                                                                    <label htmlFor="tipoSujetoProcesal">Tipo de sujeto procesal <span className="text-danger">*</span></label>
                                                                    <Field as="select" className="form-control" id="tipoSujetoProcesal" name="tipoSujetoProcesal">
                                                                        <option value="">Por favor seleccione</option>
                                                                        <option value="1">Interesado</option>
                                                                        <option value="2">Interesado acoso laboral</option>
                                                                        <option value="3">Disciplinado</option>
                                                                        <option value="4">Apoderado</option>
                                                                        <option value="5">Defensor de oficio</option>
                                                                        <option value="6">Ministerio público</option>
                                                                        <option value="7">Victimas graves, Violación, Derechos humanos</option>
                                                                    </Field>
                                                                    <ErrorMessage name="tipoSujetoProcesal" component={() => (<span className="text-danger">{errors.tipoSujetoProcesal}</span>)} />
                                                                </div>
                                                            </div>
                                                        ) : null
                                                    }
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='tipoDocumento'>Tipo documento <span className="text-danger">*</span></label>
                                                                <Field as="select" id="tipoDocumento" name="tipoDocumento" className="form-control">
                                                                    <option value="">Por favor seleccione</option>
                                                                    <option value="1">CÉDULA DE CIUDADANÍA</option>
                                                                    <option value="2">CÉDULA DE EXTRANJERIA</option>
                                                                    <option value="3">TARJETA DE IDENTIDAD</option>
                                                                </Field>
                                                                <ErrorMessage name="tipoDocumento" component={() => (<span className="text-danger">{errors.tipoDocumento}</span>)} />
                                                            </div>
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='numeroDocumento'>Número de documento <span className="text-danger">*</span></label>
                                                                <Field type="text" id="numeroDocumento" name="numeroDocumento" className="form-control" placeholder="Numero de documento" />
                                                                <ErrorMessage name="numeroDocumento" component={() => (<span className="text-danger">{errors.numeroDocumento}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='primerNombre'>Primer Nombre <span className="text-danger">*</span></label>
                                                                <Field type="text" id="primerNombre" name="primerNombre" className="form-control" placeholder="Primer Nombre" />
                                                                <ErrorMessage name="primerNombre" component={() => (<span className="text-danger">{errors.primerNombre}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='segundoNombre'>Segundo Nombre </label>
                                                                <Field type="text" id="segundoNombre" name="segundoNombre" className="form-control" placeholder="Segundo Nombre" />
                                                                <ErrorMessage name="segundoNombre" component={() => (<span className="text-danger">{errors.segundoNombre}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='primerApellido'>Primer Apellido <span className="text-danger">*</span></label>
                                                                <Field type="text" id="primerApellido" name="primerApellido" className="form-control" placeholder="Primer Apellido" />
                                                                <ErrorMessage name="primerApellido" component={() => (<span className="text-danger">{errors.primerApellido}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='segundoApellido'>Segundo Apellido </label>
                                                                <Field type="text" id="segundoApellido" name="segundoApellido" className="form-control" placeholder="Segundo Apellido" />
                                                                <ErrorMessage name="segundoApellido" component={() => (<span className="text-danger">{errors.segundoApellido}</span>)} />
                                                            </div>  
                                                        </div>
                                                        {
                                                            selectSujetoProcesal === '6' ? 
                                                            (
                                                                <div className="col-md-6">
                                                                    <div className="form-group">
                                                                        <label htmlFor='dependencia'>Dependencia <span className="text-danger">*</span></label>
                                                                        <Field type="text" id="dependencia" name="dependencia" className="form-control" placeholder="Dependencia" />
                                                                        <ErrorMessage name="dependencia" component={() => (<span className="text-danger">{errors.dependencia}</span>)} />
                                                                    </div>
                                                                </div>
                                                            ) : null
                                                        }
                                                        {
                                                            (selectSujetoProcesal === '3' || selectSujetoProcesal === '6') ? 
                                                            (
                                                                <div className="col-md-6">
                                                                    <div className="form-group">
                                                                        <label htmlFor='cargo'>Cargo <span className="text-danger">*</span></label>
                                                                        <Field type="text" id="cargo" name="cargo" className="form-control" placeholder="Cargo" />
                                                                        <ErrorMessage name="cargo" component={() => (<span className="text-danger">{errors.cargo}</span>)} />
                                                                    </div>
                                                                </div>
                                                            ) : null
                                                        }
                                                        {
                                                            (selectSujetoProcesal === '3' || selectSujetoProcesal === '6') ? 
                                                            (
                                                                <div className="col-md-6">
                                                                    <div className="form-group">
                                                                        <label htmlFor='entidadSujetoProcesal'>Entidad <span className="text-danger">*</span></label>
                                                                        <Field type="text" id="entidadSujetoProcesal" name="entidadSujetoProcesal" className="form-control" placeholder="Entidad" disabled={selectSujetoProcesal === '6'}/>
                                                                        <ErrorMessage name="entidadSujetoProcesal" component={() => (<span className="text-danger">{errors.entidadSujetoProcesal}</span>)} />
                                                                    </div>
                                                                </div>
                                                            ) : null
                                                        }
                                                        {
                                                            (selectSujetoProcesal === '4' || selectSujetoProcesal === '5') ? 
                                                            (
                                                                <div className="col-md-6">
                                                                    <div className="form-group">
                                                                        <label htmlFor='tarjetaProfesional'>Tarjeta profesional <span className="text-danger">*</span></label>
                                                                        <Field type="text" id="tarjetaProfesional" name="tarjetaProfesional" className="form-control" placeholder="Tarjeta profesional" />
                                                                        <ErrorMessage name="tarjetaProfesional" component={() => (<span className="text-danger">{errors.tarjetaProfesional}</span>)} />
                                                                    </div>
                                                                </div>
                                                            ) : null
                                                        }
                                                        <div className="col-md-6">
                                                            <div className="form-group">

                                                                <label htmlFor="departamento">Departamento <span className="text-danger">*</span></label>
                                                               
                                                                <Field as="select" className="form-control" id="departamento" name="departamento"
                                                                 value={selectedDepartamento} onChange={e => selectChangeDepartamento(e.target.value)}>
                                                                    <option value="">Por favor seleccione</option>
                                                                    { respuestaDepartamento ? selectDepartamentos() : null }
                                                                </Field>
                                                                <ErrorMessage name="departamento" component={() => (<span className="text-danger">{errors.departamento}</span>)} />
                                                            
                                                            </div>
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor="ciudad">Ciudad </label>
                                                                <Field as="select" className="form-control" id="ciudad" name="ciudad">
                                                                    <option value="">Por favor seleccione</option>
                                                                    { respuestaCiudad ? selectCiudad() : null }
                                                                </Field>
                                                                <ErrorMessage name="ciudad" component={() => (<span className="text-danger">{errors.ciudad}</span>)} />
                                                            </div>
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='direccionCorrespondencia'>Dirección <span className="text-danger">*</span></label>
                                                                <Field type="text" id="direccionCorrespondencia" name="direccionCorrespondencia" className="form-control" placeholder="Dirección de correspondencia" />
                                                                <ErrorMessage name="direccionCorrespondencia" component={() => (<span className="text-danger">{errors.direccionCorrespondencia}</span>)} />
                                                            </div>
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='localidad'>Localidad <span className="text-danger">*</span></label>
                                                                <Field as="select" id="localidad" name="localidad" className="form-control">
                                                                    <option value="">Por favor seleccione</option>
                                                                    <option value="1">Localidad 1</option>
                                                                    <option value="2">Localidad 2</option>
                                                                    <option value="3">Localidad 3</option>
                                                                </Field>
                                                                <ErrorMessage name="localidad" component={() => (<span className="text-danger">{errors.localidad}</span>)} />
                                                            </div>
                                                        </div>
                                                        <div className="col-md-6">
                                                            <div className="form-group">
                                                                <label htmlFor='correo'>Correo <span className="text-danger">*</span></label>
                                                                <Field type="text" id="correo" name="correo" className="form-control" placeholder="Correo" />
                                                                <ErrorMessage name="correo" component={() => (<span className="text-danger">{errors.correo}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-3">
                                                            <div className="form-group">
                                                                <label htmlFor='telefonoCelular'>Teléfono celular <span className="text-danger">*</span></label>
                                                                <Field type="text" id="telefonoCelular" name="telefonoCelular" className="form-control" placeholder="Teléfono celular" />
                                                                <ErrorMessage name="telefonoCelular" component={() => (<span className="text-danger">{errors.telefonoCelular}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-3">
                                                            <div className="form-group">
                                                                <label htmlFor='telefonoFijo'>Teléfono fijo</label>
                                                                <Field type="text" id="telefonoFijo" name="telefonoFijo" className="form-control" placeholder="Teléfono fijo" />
                                                                <ErrorMessage name="telefonoFijo" component={() => (<span className="text-danger">{errors.telefonoFijo}</span>)} />
                                                            </div>  
                                                        </div>
                                                        <div className="col-md-3">
                                                            <div className="form-group">
                                                                <label htmlFor='sexo'>Sexo <span className="text-danger">*</span></label>
                                                                <Field as="select" id="sexo" name="sexo" className="form-control">
                                                                    <option value="">Por favor seleccione</option>
                                                                    <option value="1">Hombre</option>
                                                                    <option value="2">Mujer</option>
                                                                    <option value="3">Intersexual</option>
                                                                </Field>
                                                                <ErrorMessage name="sexo" component={() => (<span className="text-danger">{errors.sexo}</span>)} />
                                                            </div>
                                                        </div>
                                                        <div className="col-md-3">
                                                            <div className="form-group">
                                                                <label htmlFor='genero'>Genero <span className="text-danger">*</span></label>
                                                                <Field as="select" id="genero" name="genero" className="form-control">
                                                                    <option value="">Por favor seleccione</option>
                                                                    <option value="1">Femenino</option>
                                                                    <option value="2">Masculino</option>
                                                                    <option value="3">Transgenero</option>
                                                                    <option value="4">No deseo informar</option>
                                                                </Field>
                                                                <ErrorMessage name="genero" component={() => (<span className="text-danger">{errors.genero}</span>)} />
                                                            </div>
                                                        </div>
                                                       <div className="col-md-3">
                                                            <div className="form-group">
                                                                <label htmlFor='orientacionSexual'>Orientación sexual <span className="text-danger">*</span></label>
                                                                <Field as="select" id="orientacionSexual" name="orientacionSexual" className="form-control">
                                                                    <option value="">Por favor seleccione</option>
                                                                    <option value="1">Bisexual</option>
                                                                    <option value="2">Heterosexual</option>
                                                                    <option value="3">Homosexual</option>
                                                                    <option value="4">No deseo informar</option>
                                                                </Field>
                                                                <ErrorMessage name="orientacionSexual" component={() => (<span className="text-danger">{errors.orientacionSexual}</span>)} />
                                                            </div>
                                                        </div>
                                                        <div className="col-md-3">
                                                            <div className="form-group">
                                                                <label htmlFor='folio'>Folio <span className="text-danger">*</span></label>
                                                                <Field type="text" id="folio" name="folio" className="form-control" placeholder="Folio" />
                                                                <ErrorMessage name="folio" component={() => (<span className="text-danger">{errors.folio}</span>)} />
                                                            </div>  
                                                        </div>
                                                    </div>                                
                                                </div>
                                                <div className="block-content block-content-full text-right">
                                                    <button type="submit" className="btn btn-primary">
                                                        <i className="fa fa-fw fa-save"></i> Registrar
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                    ) : null
                                }
                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        )
    }

    return(
        
        <>
        { isRedirect ? redirectToRoutes() : null }
            <Spinner />
            { <InfoErrorApi error={errorApi}/> }
            { <InfoExitoApi /> }

            <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
    
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Datos Interesado historia</small></Link></li>
									<li class="breadcrumb-item"> <Link underline="hover" className="text-primary" to={`/MisPendientes`}><small>Datos Interesado</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

            <div className="content">
                <div className="row">
                    <div className="col-md-12">
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Datos del interesado</h3>
                            </div>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="ingresoProcesoDisciplinario">Tipo de interesado <span className="text-danger">*</span></label>
                                            <select className="form-control" id="ingresoProcesoDisciplinario" name="ingresoProcesoDisciplinario" value={selectTipoInteresado} onChange={e => selectChangeTipoInteresado(e.target.value)}>
                                                <option value="">Por favor seleccione</option>
                                                <option value="1">Persona Natural</option>
                                                <option value="2">Entidad</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>                
                { componentFormularioInteresado() }
            </div>
        </>
    )

}

export default DatosInteresadoForm;
