import { createSchema, addTranslations, setLanguageByName } from 'react-hook-form-auto'

export const TipoFirmaModel = createSchema('TipoFirmaModel', {
    nombre: {
        type: 'string',
        required: true,
        minLength: 3,
        maxLength: 255,
    },
    tamano: {
        type: 'select',
        required: true,
        options: [
            { value: '0', label: 'Peque単o' },
            { value: '1', label: 'Mediano' },
            { value: '2', label: 'Grande' }
        ],
    },
    estado: {
        type: 'select',
        required: true,
        options: [
            { value: '1', label: 'Activo' },
            { value: '0', label: 'Inactivo' }
        ],
    }
})

setLanguageByName('es')
addTranslations({
    models: {
        TipoFirmaModel: {
            nombre: 'Nombre',
            tamano: {
                _field: 'Tama単o',
                _default: "Seleccione",
            },
            estado: {
                _field: 'Estado',
                _default: "Seleccione",
            },
        },
    },
})
