import React, { useEffect, useState, } from 'react';
import { ErrorMessage, Form, Field, Formik } from 'formik';
import { Link } from 'react-router-dom';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import Spinner from '../Utils/Spinner';
import PendientesApi from "./../Api/Services/PendientesApi";
import { getUser } from '../../components/Utils/Common';
import { ParametroModel } from '../Models/ParametroModel';
import ModalRemitirExpediente from '../Utils/Modals/ModalRemitirExpediente';
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';
import GenericApi from './../Api/Services/GenericApi';
function TrasladoProcesos() {

    const [errorApi, setErrorApi] = useState('');
    const [formIntialValuesTraslado, setFormIntialValuesTraslado] = useState();
    const columns = [

    ];

    useEffect(() => {
        async function fetchData() {

        }
        fetchData();
    }, []);

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        if (formIntialValuesTraslado) {

            data = {

                "data": {
                    "type": "interesado",
                    "attributes": {
                        "tipo_documento": formIntialValuesTraslado ? (formIntialValuesTraslado.tipoDocumento ? formIntialValuesTraslado.tipoDocumento :  ''):'',
                        "numero_documento": formIntialValuesTraslado ? (formIntialValuesTraslado.numeroDocumento ? formIntialValuesTraslado.numeroDocumento :  ''):'',
                        "primer_nombre": formIntialValuesTraslado ? (formIntialValuesTraslado.primerNombre ? formIntialValuesTraslado.primerNombre  : ''):'',
                        "segundo_nombre": formIntialValuesTraslado ? (formIntialValuesTraslado.segundoNombre ? formIntialValuesTraslado.segundoNombre : ''):'',
                        "primer_apellido": formIntialValuesTraslado ? (formIntialValuesTraslado.primerApellido ? formIntialValuesTraslado.primerApellido :  ''):'',
                        "segundo_apellido": formIntialValuesTraslado ? (formIntialValuesTraslado.segundoApellido ? formIntialValuesTraslado.segundoApellido : ''):'',
                        "numero_proceso": formIntialValuesTraslado ? (formIntialValuesTraslado.numeroProceso? formIntialValuesTraslado.numeroProceso : ''):'',
                        
                    }
                }
            }
        }




        console.log(JSON.stringify(data));

        GenericApi.getAllGeneric(data).then(
            datos => {

                if (!datos.error) {
                    window.showModal(2);
                }
                else {
                    //console.log("hay error");
                    console.log(datos.error);
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }

    return (
        <>
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    primerNombre: formIntialValuesTraslado.primerNombre ? formIntialValuesTraslado.primerNombre : '',
                    segundoNombre: formIntialValuesTraslado.segundoNombre ? formIntialValuesTraslado.segundoNombre : '',
                    primerApellido: formIntialValuesTraslado.primerApellido ? formIntialValuesTraslado.primerApellido : '',
                    segundoApellido: formIntialValuesTraslado.segundoApellido ? formIntialValuesTraslado.segundoApellido : '',
                    numeroProceso: formIntialValuesTraslado.numeroProceso ? formIntialValuesTraslado.numeroProceso : '',
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>


                        <div className="block block-rounded block-bordered">

                            <div className="block-content block-content-full">
                                <div className="row">
                                    <div className="col-md-12 text-center mb-3">
                                        <label>Busca los procesos que deseas trasladar a otros usuarios, puedes buscar por número o por el usuario que los tiene asignados actualmente:</label>
                                    </div>
                                </div>
                                <div className="row">

                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor='primerNombre'>Primer Nombre </label>
                                            <Field type="text" id="primerNombre" name="primerNombre" className="form-control" placeholder="Primer Nombre" />
                                            <ErrorMessage name="primerNombre" component={() => (<span className="text-danger">{errors.primerNombre}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor='segundoNombre'>Segundo Nombre </label>
                                            <Field type="text" id="segundoNombre" name="segundoNombre" className="form-control" placeholder="Segundo Nombre" />
                                            <ErrorMessage name="segundoNombre" component={() => (<span className="text-danger">{errors.segundoNombre}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor='primerApellido'>Primer Apellido </label>
                                            <Field type="text" id="primerApellido" name="primerApellido" className="form-control" placeholder="Primer Apellido" />
                                            <ErrorMessage name="primerApellido" component={() => (<span className="text-danger">{errors.primerApellido}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor='segundoApellido'>Segundo Apellido </label>
                                            <Field type="text" id="segundoApellido" name="segundoApellido" className="form-control" placeholder="Segundo Apellido" />
                                            <ErrorMessage name="segundoApellido" component={() => (<span className="text-danger">{errors.segundoApellido}</span>)} />
                                        </div>
                                    </div>

                                    <div className="col-md-4">
                                        <label htmlFor='numeroProceso'>Número de proceso </label>
                                        <Field type="number" id="numeroProceso" name="numeroProceso"
                                            className="form-control" placeholder="Número de proceso" />

                                    </div>

                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Buscar</button>
                                </div>

                            </div>


                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default TrasladoProcesos;
